pragma solidity ^0.8.20;

library OpinionStructs {
    struct Opinion {
        uint96 lastPrice;
        uint96 nextPrice;
        uint96 totalVolume;
        uint96 salePrice;
        address creator;
        address questionOwner;
        address currentAnswerOwner;
        bool isActive;
        string question;
        string currentAnswer;
        string currentAnswerDescription;     // ðŸ†• NEW FIELD
        string ipfsHash;
        string link;
        string[] categories;                 // ðŸš¨ IMPOSED - ADDED AT END ONLY
    }

    struct AnswerHistory {
        string answer;
        string description;          // ðŸ†• NEW FIELD
        address owner;
        uint96 price;
        uint32 timestamp;
    }

    struct TradeParams {
        uint256 opinionId;
        uint96 price;
        string answer;
        address trader;
        uint32 timestamp;
    }

    struct FeeDistribution {
        uint96 platformFee;
        uint96 creatorFee;
        uint96 ownerAmount;
    }
}

