// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IOpinionAdminInternal.sol";
import "./interfaces/IOpinionCoreInternal.sol";
import "./interfaces/IOpinionMarketEvents.sol";
import "./interfaces/IOpinionMarketErrors.sol";
import "./interfaces/IFeeManager.sol";
import "./interfaces/IPoolManager.sol";
import "./interfaces/IMonitoringManager.sol";
import "./interfaces/ISecurityManager.sol";

/**
 * @title OpinionAdmin
 * @dev Administrative functions and parameter management - Size optimized (~6KB)
 */
contract OpinionAdmin is
    Initializable,
    UUPSUpgradeable,
    AccessControlUpgradeable,
    PausableUpgradeable,
    IOpinionAdminInternal,
    IOpinionMarketEvents,
    IOpinionMarketErrors
{
    using SafeERC20 for IERC20;

    // --- ROLES ---
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");
    bytes32 public constant TREASURY_ROLE = keccak256("TREASURY_ROLE");
    bytes32 public constant CORE_CONTRACT_ROLE = keccak256("CORE_CONTRACT_ROLE");

    // --- CONSTANTS ---
    uint256 public constant TREASURY_CHANGE_DELAY = 48 hours;

    // --- STATE VARIABLES ---
    IOpinionCoreInternal public coreContract;
    IERC20 public usdcToken;

    // Treasury management
    address public treasury;
    address public pendingTreasury;
    uint256 public treasuryChangeTimestamp;

    // System settings
    bool public isPublicCreationEnabled;
    uint96 public maxInitialPrice;

    // --- MODIFIERS ---
    modifier onlyCoreContract() {
        if (!hasRole(CORE_CONTRACT_ROLE, msg.sender))
            revert AccessControlUnauthorizedAccount(msg.sender, CORE_CONTRACT_ROLE);
        _;
    }

    // --- INITIALIZATION ---
    function initialize(
        address _coreContract,
        address _usdcToken,
        address _treasury,
        address _admin
    ) public initializer {
        __AccessControl_init();
        __Pausable_init();

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);
        _grantRole(MODERATOR_ROLE, _admin);
        _grantRole(TREASURY_ROLE, _admin);
        _grantRole(CORE_CONTRACT_ROLE, _coreContract);

        coreContract = IOpinionCoreInternal(_coreContract);
        usdcToken = IERC20(_usdcToken);
        treasury = _treasury;

        // Initialize settings
        isPublicCreationEnabled = true;
        maxInitialPrice = 100_000_000; // 100 USDC
    }

    // --- PARAMETER MANAGEMENT ---

    function setMinimumPrice(uint96 _minimumPrice) external onlyRole(ADMIN_ROLE) {
        coreContract.updateCoreParameter(0, _minimumPrice);
        emit ParameterUpdated(0, _minimumPrice);
    }

    function setQuestionCreationFee(uint96 _questionCreationFee) external onlyRole(ADMIN_ROLE) {
        coreContract.updateCoreParameter(1, _questionCreationFee);
        emit ParameterUpdated(1, _questionCreationFee);
    }

    function setInitialAnswerPrice(uint96 _initialAnswerPrice) external onlyRole(ADMIN_ROLE) {
        coreContract.updateCoreParameter(2, _initialAnswerPrice);
        emit ParameterUpdated(2, _initialAnswerPrice);
    }

    function setMaxPriceChange(uint256 _maxPriceChange) external onlyRole(ADMIN_ROLE) {
        coreContract.updateCoreParameter(3, _maxPriceChange);
        emit ParameterUpdated(3, _maxPriceChange);
    }

    function setMaxTradesPerBlock(uint256 _maxTradesPerBlock) external onlyRole(ADMIN_ROLE) {
        coreContract.updateCoreParameter(4, _maxTradesPerBlock);
        emit ParameterUpdated(4, _maxTradesPerBlock);
    }

    function setMaxInitialPrice(uint96 _maxInitialPrice) external onlyRole(ADMIN_ROLE) {
        require(_maxInitialPrice >= 1_000_000, "Below minimum"); // At least 1 USDC
        maxInitialPrice = _maxInitialPrice;
        coreContract.updateCoreParameter(5, _maxInitialPrice);
        emit ParameterUpdated(5, _maxInitialPrice);
    }

    function togglePublicCreation() external onlyRole(ADMIN_ROLE) {
        isPublicCreationEnabled = !isPublicCreationEnabled;
        emit AdminAction(1, msg.sender, bytes32(0), 0);
    }

    // --- CONTRACT MANAGEMENT ---

    /**
     * @dev Sets the core contract address. Can only be called once if initially zero,
     *      or by admin for updates. Essential for post-deployment linking.
     */
    function setCoreContract(address _coreContract) external onlyRole(ADMIN_ROLE) {
        require(_coreContract != address(0), "Zero address");

        // Revoke role from old address if it exists
        address oldCore = address(coreContract);
        if (oldCore != address(0)) {
            _revokeRole(CORE_CONTRACT_ROLE, oldCore);
        }

        // Grant role to new address and update reference
        _grantRole(CORE_CONTRACT_ROLE, _coreContract);
        coreContract = IOpinionCoreInternal(_coreContract);

        emit AdminAction(100, msg.sender, bytes32(uint256(uint160(_coreContract))), 0);
    }

    function setFeeManager(address _feeManager) external onlyRole(ADMIN_ROLE) {
        require(_feeManager != address(0), "Zero address");
        coreContract.updateCoreParameterAddress(0, _feeManager);
        emit AdminAction(2, msg.sender, bytes32(uint256(uint160(_feeManager))), 0);
    }

    function setPoolManager(address _poolManager) external onlyRole(ADMIN_ROLE) {
        require(_poolManager != address(0), "Zero address");
        coreContract.updateCoreParameterAddress(1, _poolManager);
        emit AdminAction(3, msg.sender, bytes32(uint256(uint160(_poolManager))), 0);
    }

    function setMonitoringManager(address _monitoringManager) external onlyRole(ADMIN_ROLE) {
        coreContract.updateCoreParameterAddress(2, _monitoringManager);
        emit AdminAction(4, msg.sender, bytes32(uint256(uint160(_monitoringManager))), 0);
    }

    function setSecurityManager(address _securityManager) external onlyRole(ADMIN_ROLE) {
        coreContract.updateCoreParameterAddress(3, _securityManager);
        emit AdminAction(5, msg.sender, bytes32(uint256(uint160(_securityManager))), 0);
    }

    function grantMarketContractRole(address _contract) external onlyRole(ADMIN_ROLE) {
        // Note: This would need to be implemented in core contract
        emit AdminAction(6, msg.sender, bytes32(uint256(uint160(_contract))), 1);
    }

    function revokeMarketContractRole(address _contract) external onlyRole(ADMIN_ROLE) {
        // Note: This would need to be implemented in core contract
        emit AdminAction(6, msg.sender, bytes32(uint256(uint160(_contract))), 0);
    }

    // --- TREASURY MANAGEMENT ---

    function setTreasury(address _treasury) external onlyRole(TREASURY_ROLE) {
        require(_treasury != address(0), "Zero address");

        pendingTreasury = _treasury;
        treasuryChangeTimestamp = block.timestamp + TREASURY_CHANGE_DELAY;

        emit AdminAction(10, msg.sender, bytes32(uint256(uint160(_treasury))), treasuryChangeTimestamp);
    }

    function confirmTreasuryChange() external onlyRole(TREASURY_ROLE) {
        require(pendingTreasury != address(0), "No pending change");
        require(block.timestamp >= treasuryChangeTimestamp, "Too early");

        address oldTreasury = treasury;
        treasury = pendingTreasury;

        pendingTreasury = address(0);
        treasuryChangeTimestamp = 0;

        emit TreasuryUpdated(oldTreasury, treasury, msg.sender, block.timestamp);
    }

    // --- SYSTEM CONTROLS ---

    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
        emit ContractPaused(msg.sender, block.timestamp);
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
        emit ContractUnpaused(msg.sender, block.timestamp);
    }

    function emergencyWithdraw(address token, address to, uint256 amount) external onlyRole(ADMIN_ROLE) {
        require(to != address(0), "Zero address");

        if (token == address(0)) {
            // Withdraw ETH
            (bool success, ) = to.call{value: amount}("");
            require(success, "ETH transfer failed");
        } else {
            // Withdraw ERC20
            IERC20(token).safeTransfer(to, amount);
        }

        emit EmergencyWithdrawal(token, to, amount, block.timestamp);
    }

    // --- MODERATION FUNCTIONS (STUBS) ---

    function deactivateOpinion(uint256 opinionId) external onlyRole(MODERATOR_ROLE) {
        // Could implement opinion deactivation logic here
        emit AdminAction(20, msg.sender, bytes32(opinionId), 0);
    }

    function reactivateOpinion(uint256 opinionId) external onlyRole(MODERATOR_ROLE) {
        // Could implement opinion reactivation logic here
        emit AdminAction(21, msg.sender, bytes32(opinionId), 1);
    }

    function moderateAnswer(uint256 opinionId, string calldata reason) external onlyRole(MODERATOR_ROLE) {
        // Could implement answer moderation logic here
        emit AnswerModerated(opinionId, msg.sender, address(0), reason, block.timestamp);
    }

    // --- INTERNAL INTERFACE IMPLEMENTATION ---

    function validateAdminAccess(address caller) external view onlyCoreContract returns (bool) {
        return hasRole(ADMIN_ROLE, caller);
    }

    function validateModeratorAccess(address caller) external view onlyCoreContract returns (bool) {
        return hasRole(MODERATOR_ROLE, caller);
    }

    function notifyParameterChange(uint8 paramType, uint256 newValue) external onlyCoreContract {
        emit ParameterUpdated(paramType, newValue);
    }

    function notifyAddressChange(uint8 paramType, address newAddress) external onlyCoreContract {
        emit AdminAction(paramType, msg.sender, bytes32(uint256(uint160(newAddress))), 0);
    }

    function isSystemPaused() external view onlyCoreContract returns (bool) {
        return paused();
    }

    function getTreasury() external view returns (address) {
        return treasury;
    }

    function isPendingTreasuryChange() external view onlyCoreContract returns (bool) {
        return pendingTreasury != address(0);
    }

    // --- VIEW FUNCTIONS ---

    function getSystemStatus() external view returns (
        bool _isPaused,
        bool _isPublicCreationEnabled,
        address _treasury,
        address _pendingTreasury,
        uint256 _treasuryChangeTimestamp
    ) {
        return (
            paused(),
            isPublicCreationEnabled,
            treasury,
            pendingTreasury,
            treasuryChangeTimestamp
        );
    }

    function getMaxInitialPrice() external view returns (uint96) {
        return maxInitialPrice;
    }

    // --- RECEIVE FUNCTION ---
    receive() external payable {
        // Allow contract to receive ETH for emergency operations
    }

    // --- UUPS UPGRADE AUTHORIZATION ---
    function _authorizeUpgrade(address newImplementation) internal override onlyRole(ADMIN_ROLE) {}

    // --- ADMIN TRANSFER ---
    function transferFullAdmin(address newAdmin) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newAdmin != address(0), "Invalid address");
        _grantRole(DEFAULT_ADMIN_ROLE, newAdmin);
        _grantRole(ADMIN_ROLE, newAdmin);
        _grantRole(MODERATOR_ROLE, newAdmin);
        _grantRole(TREASURY_ROLE, newAdmin);
        _revokeRole(TREASURY_ROLE, msg.sender);
        _revokeRole(MODERATOR_ROLE, msg.sender);
        _revokeRole(ADMIN_ROLE, msg.sender);
        _revokeRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }
}

contracts/active/OpinionCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IOpinionCore.sol";
import "./interfaces/IFeeManager.sol";
import "./interfaces/IPoolManager.sol";
import "./interfaces/IMonitoringManager.sol";
import "./interfaces/ISecurityManager.sol";
import "./interfaces/IOpinionMarketEvents.sol";
import "./interfaces/IOpinionMarketErrors.sol";
import "./interfaces/IOpinionCoreInternal.sol";
import "./interfaces/IOpinionExtensionsInternal.sol";
import "./interfaces/IOpinionAdminInternal.sol";
import "./structs/OpinionStructs.sol";
import "./libraries/ValidationLibrary.sol";
import "./libraries/PriceCalculator.sol";
import "./interfaces/IValidationErrors.sol";

/**
 * @title OpinionCore
 * @dev Core trading contract for opinions and answers - Size optimized (~16KB)
 */
contract OpinionCore is
    Initializable,
    UUPSUpgradeable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    IOpinionCoreInternal,
    IOpinionMarketEvents,
    IOpinionMarketErrors,
    IValidationErrors
{
    using SafeERC20 for IERC20;

    // --- ROLES ---
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");
    bytes32 public constant MARKET_CONTRACT_ROLE = keccak256("MARKET_CONTRACT_ROLE");
    bytes32 public constant POOL_MANAGER_ROLE = keccak256("POOL_MANAGER_ROLE");
    bytes32 public constant EXTENSION_CONTRACT_ROLE = keccak256("EXTENSION_CONTRACT_ROLE");
    bytes32 public constant ADMIN_CONTRACT_ROLE = keccak256("ADMIN_CONTRACT_ROLE");

    // --- CONSTANTS ---
    uint256 public constant MAX_QUESTION_LENGTH = 60;
    uint256 public constant MAX_ANSWER_LENGTH = 60;
    uint256 public constant MAX_LINK_LENGTH = 260;
    uint256 public constant MAX_IPFS_HASH_LENGTH = 68;
    uint256 public constant MAX_DESCRIPTION_LENGTH = 280;
    uint256 public constant MAX_CATEGORIES_PER_OPINION = 3;

    // --- INITIAL PRICE RANGE CONSTANTS ---
    uint96 public constant MIN_INITIAL_PRICE = 1_000_000;   // 1 USDC (6 decimals)

    // --- STATE VARIABLES ---
    IERC20 public usdcToken;
    IFeeManager public feeManager;
    IPoolManager public poolManager;
    IMonitoringManager public monitoringManager;
    ISecurityManager public securityManager;

    // Linked contracts
    IOpinionExtensionsInternal public extensionsContract;
    IOpinionAdminInternal public adminContract;

    uint256 public nextOpinionId;

    // Security and rate limiting
    uint256 public maxTradesPerBlock;
    mapping(address => uint256) private userLastBlock;
    mapping(address => uint256) private userTradesInBlock;
    mapping(address => mapping(uint256 => uint256)) private userLastTradeBlock;

    // Price calculation
    uint256 private nonce;
    mapping(uint256 => uint256) private priceMetadata;
    mapping(uint256 => uint256) private priceHistory;

    // Configurable parameters
    uint96 public minimumPrice;
    uint96 public questionCreationFee;
    uint96 public initialAnswerPrice;
    uint256 public absoluteMaxPriceChange;
    uint96 public maxInitialPrice;

    // Core data structures
    mapping(uint256 => OpinionStructs.Opinion) public opinions;
    mapping(uint256 => OpinionStructs.AnswerHistory[]) public answerHistory;

    // --- MODIFIERS ---
    modifier onlyMarketContract() {
        if (!hasRole(MARKET_CONTRACT_ROLE, msg.sender))
            revert AccessControlUnauthorizedAccount(msg.sender, MARKET_CONTRACT_ROLE);
        _;
    }

    modifier onlyPoolManager() {
        if (!hasRole(POOL_MANAGER_ROLE, msg.sender))
            revert AccessControlUnauthorizedAccount(msg.sender, POOL_MANAGER_ROLE);
        _;
    }

    modifier onlyLinkedContracts() {
        if (!hasRole(EXTENSION_CONTRACT_ROLE, msg.sender) && 
            !hasRole(ADMIN_CONTRACT_ROLE, msg.sender))
            revert AccessControlUnauthorizedAccount(msg.sender, EXTENSION_CONTRACT_ROLE);
        _;
    }

    // --- INITIALIZATION ---
    function initialize(
        address _usdcToken,
        address _opinionMarket,
        address _feeManager,
        address _poolManager,
        address _monitoringManager,
        address _securityManager,
        address _treasury,
        address _extensionsContract,
        address _adminContract
    ) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();
        __Pausable_init();

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(MODERATOR_ROLE, msg.sender);

        // Set external contracts
        if (
            _usdcToken == address(0) ||
            _opinionMarket == address(0) ||
            _feeManager == address(0) ||
            _poolManager == address(0) ||
            _extensionsContract == address(0) ||
            _adminContract == address(0)
        ) revert ZeroAddressNotAllowed();

        usdcToken = IERC20(_usdcToken);
        feeManager = IFeeManager(_feeManager);
        poolManager = IPoolManager(_poolManager);
        extensionsContract = IOpinionExtensionsInternal(_extensionsContract);
        adminContract = IOpinionAdminInternal(_adminContract);

        // Grant roles to linked contracts
        _grantRole(MARKET_CONTRACT_ROLE, _opinionMarket);
        _grantRole(EXTENSION_CONTRACT_ROLE, _extensionsContract);
        _grantRole(ADMIN_CONTRACT_ROLE, _adminContract);

        // Set optional modules (can be zero initially)
        if (_monitoringManager != address(0)) {
            monitoringManager = IMonitoringManager(_monitoringManager);
        }
        if (_securityManager != address(0)) {
            securityManager = ISecurityManager(_securityManager);
        }

        // Initialize parameters
        nextOpinionId = 1;
        maxTradesPerBlock = 0; // No trade limit per block
        minimumPrice = 1_000_000; // 1 USDC (6 decimals)
        questionCreationFee = 2_000_000; // 2 USDC minimum
        initialAnswerPrice = 1_000_000; // 1 USDC
        absoluteMaxPriceChange = 200; // 200%
        maxInitialPrice = 100_000_000; // 100 USDC
    }

    // --- CORE OPINION FUNCTIONS ---

    function createOpinion(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) external nonReentrant whenNotPaused {
        // Access control check
        if (!adminContract.isPublicCreationEnabled() && !hasRole(ADMIN_ROLE, msg.sender))
            revert UnauthorizedCreator();

        // Basic validation
        _basicValidation(question, answer, description, initialPrice, opinionCategories);

        // Calculate dynamic creation fee: MAX(1 USDC, 20% of initial price)
        uint96 creationFee = _calculateCreationFee(initialPrice);

        // Check allowance and transfer fee
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < creationFee) revert InsufficientAllowance(creationFee, allowance);
        usdcToken.safeTransferFrom(msg.sender, adminContract.getTreasury(), creationFee);

        // Create opinion record
        uint256 opinionId = _createOpinionRecord(question, answer, description, initialPrice);

        // Initialize categories
        extensionsContract.initializeOpinionCategories(opinionId, opinionCategories);

        emit OpinionAction(opinionId, 0, question, msg.sender, initialPrice);
        emit OpinionCreated(opinionId, question, answer, msg.sender, initialPrice, block.timestamp);
    }

    function submitAnswer(
        uint256 opinionId,
        string calldata answer,
        string calldata description,
        string calldata link
    ) external payable nonReentrant whenNotPaused {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (!opinion.isActive) revert OpinionNotActive();

        // Rate limiting
        _checkAndUpdateTradesInBlock();
        _checkTradeAllowed(opinionId);

        // Validation
        // Validate answer length
        if (bytes(answer).length < 2 || bytes(answer).length > MAX_ANSWER_LENGTH) {
            revert InvalidStringParameter("answer", "Length out of bounds");
        }
        ValidationLibrary.validateDescription(description);
        // Skip ipfs validation for now
        if (bytes(link).length > MAX_LINK_LENGTH) {
            revert InvalidStringParameter("link", "Too long");
        }

        uint96 price = uint96(this.getNextPrice(opinionId));

        // Check allowance
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < price) revert InsufficientAllowance(price, allowance);

        // Calculate fees
        (uint96 platformFee, uint96 creatorFee, uint96 ownerAmount) = feeManager.calculateFeeDistribution(price);

        // Transfer payment
        usdcToken.safeTransferFrom(msg.sender, address(this), price);

        // Transfer to previous answer owner
        if (ownerAmount > 0) {
            usdcToken.safeTransfer(opinion.currentAnswerOwner, ownerAmount);
        }

        // Accumulate fees
        if (creatorFee > 0) {
            feeManager.accumulateFee(opinion.creator, creatorFee);
        }

        // Update opinion state
        _updateOpinionState(opinion, answer, description, link, price, msg.sender);

        // Update price calculation metadata
        _updatePriceMetadata(opinionId, price);

        // Record answer in history
        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: description,
                owner: msg.sender,
                price: price,
                timestamp: uint32(block.timestamp)
            })
        );

        // Track with monitoring manager if available
        // if (address(monitoringManager) != address(0)) {
        //     try monitoringManager.trackAnswerSubmission(opinionId, msg.sender, price) {} catch {}
        // }

        emit OpinionAction(opinionId, 1, answer, msg.sender, price);
        emit OpinionAnswered(opinionId, answer, opinion.currentAnswerOwner, msg.sender, price, block.timestamp);
    }

    // --- QUESTION TRADING FUNCTIONS ---

    function listQuestionForSale(uint256 opinionId, uint256 price) external nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (opinion.questionOwner != msg.sender) revert NotTheOwner(msg.sender, opinion.questionOwner);

        opinion.salePrice = uint96(price);
        emit QuestionSaleAction(opinionId, 0, msg.sender, address(0), price);
    }

    function buyQuestion(uint256 opinionId) external nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();

        uint96 salePrice = opinion.salePrice;
        if (salePrice == 0) revert NotForSale(opinionId);

        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < salePrice) revert InsufficientAllowance(salePrice, allowance);

        address currentOwner = opinion.questionOwner;
        uint96 platformFee = uint96((salePrice * 10) / 100);
        uint96 sellerAmount = salePrice - platformFee;

        opinion.questionOwner = msg.sender;
        opinion.salePrice = 0;

        usdcToken.safeTransferFrom(msg.sender, address(this), salePrice);
        feeManager.accumulateFee(currentOwner, sellerAmount);

        emit QuestionSaleAction(opinionId, 1, currentOwner, msg.sender, salePrice);
    }

    function cancelQuestionSale(uint256 opinionId) external nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (opinion.questionOwner != msg.sender) revert NotTheOwner(msg.sender, opinion.questionOwner);

        opinion.salePrice = 0;
        emit QuestionSaleAction(opinionId, 2, msg.sender, address(0), 0);
    }

    function transferQuestionOwnership(uint256 opinionId, address newOwner) external nonReentrant whenNotPaused {
        if (newOwner == address(0)) revert ZeroAddressNotAllowed();

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (opinion.questionOwner != msg.sender) revert NotTheOwner(msg.sender, opinion.questionOwner);

        address previousOwner = opinion.questionOwner;
        opinion.questionOwner = newOwner;

        // Clear any existing sale listing
        opinion.salePrice = 0;

        emit QuestionOwnershipTransferred(opinionId, previousOwner, newOwner);
    }

    function transferAnswerOwnership(uint256 opinionId, address newOwner) external nonReentrant whenNotPaused {
        if (newOwner == address(0)) revert ZeroAddressNotAllowed();

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (opinion.currentAnswerOwner != msg.sender) revert NotTheOwner(msg.sender, opinion.currentAnswerOwner);

        address previousOwner = opinion.currentAnswerOwner;
        opinion.currentAnswerOwner = newOwner;

        emit AnswerOwnershipTransferred(opinionId, previousOwner, newOwner, block.timestamp);
    }

    // --- POOL INTEGRATION ---

    function updateOpinionOnPoolExecution(
        uint256 opinionId,
        string calldata answer,
        address poolAddress,
        uint256 price
    ) external onlyPoolManager {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (!opinion.isActive) revert OpinionNotActive();

        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: "",
                owner: poolAddress,
                price: uint96(price),
                timestamp: uint32(block.timestamp)
            })
        );

        opinion.currentAnswer = answer;
        opinion.currentAnswerDescription = "";
        opinion.currentAnswerOwner = poolAddress;
        opinion.lastPrice = uint96(price);
        opinion.totalVolume += uint96(price);
        opinion.nextPrice = uint96(_calculateNextPrice(opinionId, price));

        emit OpinionAction(opinionId, 1, answer, address(poolManager), price);
    }

    // --- VIEW FUNCTIONS ---

    function getAnswerHistory(uint256 opinionId) external view returns (OpinionStructs.AnswerHistory[] memory) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return answerHistory[opinionId];
    }

    function getNextPrice(uint256 opinionId) external view returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (!opinion.isActive) revert OpinionNotActive();

        if (opinion.nextPrice == 0) {
            return _estimateNextPrice(opinion.lastPrice);
        }
        return opinion.nextPrice;
    }

    function getOpinionDetails(uint256 opinionId) external view returns (OpinionStructs.Opinion memory) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId];
    }

    function getTradeCount(uint256 opinionId) external view returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return answerHistory[opinionId].length;
    }

    function getCreatorGain(uint256 opinionId) external view returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId].totalVolume;
    }

    function isPoolOwned(uint256 opinionId) external view returns (bool) {
        if (opinionId >= nextOpinionId) return false;
        return hasRole(POOL_MANAGER_ROLE, opinions[opinionId].currentAnswerOwner);
    }

    // --- INTERNAL INTERFACE IMPLEMENTATION ---

    function validateOpinionExists(uint256 opinionId) external view onlyLinkedContracts returns (bool) {
        return opinionId < nextOpinionId && opinions[opinionId].creator != address(0);
    }

    function getNextOpinionId() external view onlyLinkedContracts returns (uint256) {
        return nextOpinionId;
    }

    function updateCoreParameter(uint8 paramType, uint256 value) external onlyLinkedContracts {
        if (paramType == 0) minimumPrice = uint96(value);
        else if (paramType == 1) questionCreationFee = uint96(value);
        else if (paramType == 2) initialAnswerPrice = uint96(value);
        else if (paramType == 3) absoluteMaxPriceChange = value;
        else if (paramType == 4) maxTradesPerBlock = value;
        else if (paramType == 5) maxInitialPrice = uint96(value);

        emit ParameterUpdated(paramType, value);
    }

    function updateCoreParameterAddress(uint8 paramType, address value) external onlyLinkedContracts {
        if (paramType == 0) feeManager = IFeeManager(value);
        else if (paramType == 1) poolManager = IPoolManager(value);
        else if (paramType == 2) monitoringManager = IMonitoringManager(value);
        else if (paramType == 3) securityManager = ISecurityManager(value);
    }

    function getOpinionOwner(uint256 opinionId) external view onlyLinkedContracts returns (address) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId].questionOwner;
    }

    function getOpinionCreator(uint256 opinionId) external view onlyLinkedContracts returns (address) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId].creator;
    }

    function isOpinionActive(uint256 opinionId) external view onlyLinkedContracts returns (bool) {
        if (opinionId >= nextOpinionId) return false;
        return opinions[opinionId].isActive;
    }

    function isPaused() external view returns (bool) {
        return paused();
    }

    // --- INTERNAL FUNCTIONS ---

    function _calculateCreationFee(uint96 initialPrice) internal pure returns (uint96) {
        uint96 percentageFee = uint96((initialPrice * 20) / 100); // 20% of initial price
        return percentageFee < 2_000_000 ? 2_000_000 : percentageFee; // MIN 2 USDC
    }

    function _basicValidation(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) internal view {
        // Validate categories with extensions contract
        require(extensionsContract.validateCategories(opinionCategories), "Invalid categories");

        ValidationLibrary.validateOpinionParams(question, answer, MAX_QUESTION_LENGTH, MAX_ANSWER_LENGTH);
        ValidationLibrary.validateDescription(description);

        if (initialPrice < MIN_INITIAL_PRICE || initialPrice > maxInitialPrice) {
            revert InvalidInitialPrice();
        }
    }

    function _validateExtras(string calldata ipfsHash, string calldata link) internal pure {
        bytes memory ipfsHashBytes = bytes(ipfsHash);
        bytes memory linkBytes = bytes(link);

        if (ipfsHashBytes.length > MAX_IPFS_HASH_LENGTH) {
            revert InvalidStringParameter("ipfsHash", "Too long");
        }
        if (linkBytes.length > MAX_LINK_LENGTH) {
            revert InvalidStringParameter("link", "Too long");
        }
    }

    function _createOpinionRecord(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice
    ) internal returns (uint256 opinionId) {
        opinionId = nextOpinionId++;

        OpinionStructs.Opinion storage newOpinion = opinions[opinionId];
        newOpinion.question = question;
        newOpinion.creator = msg.sender;
        newOpinion.questionOwner = msg.sender;
        newOpinion.isActive = true;

        newOpinion.currentAnswer = answer;
        newOpinion.currentAnswerDescription = description;
        newOpinion.currentAnswerOwner = msg.sender;
        newOpinion.ipfsHash = "";
        newOpinion.link = "";
        newOpinion.lastPrice = initialPrice;
        newOpinion.nextPrice = uint96(_estimateNextPrice(initialPrice));

        // Initialize answer history
        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: description,
                owner: msg.sender,
                price: initialPrice,
                timestamp: uint32(block.timestamp)
            })
        );

        return opinionId;
    }

    function _updateOpinionState(
        OpinionStructs.Opinion storage opinion,
        string calldata answer,
        string calldata description,
        string calldata link,
        uint96 price,
        address newOwner
    ) internal {
        opinion.currentAnswer = answer;
        opinion.currentAnswerDescription = description;
        opinion.currentAnswerOwner = newOwner;
        opinion.link = link;
        opinion.lastPrice = price;
        opinion.totalVolume += price;
        opinion.nextPrice = uint96(_calculateNextPrice(uint256(opinion.lastPrice), price));
    }

    function _estimateNextPrice(uint96 currentPrice) internal view returns (uint256) {
        // Simple estimation without activity data
        uint256 baseIncrease = (currentPrice * 110) / 100; // 10% increase
        if (baseIncrease > currentPrice + (currentPrice * absoluteMaxPriceChange / 100)) {
            return currentPrice + (currentPrice * absoluteMaxPriceChange / 100);
        }
        return baseIncrease;
    }

    function _calculateNextPrice(uint256 opinionId, uint256 currentPrice) internal view returns (uint256) {
        // Simple price calculation for now
        return _estimateNextPrice(uint96(currentPrice));
    }

    function _updatePriceMetadata(uint256 opinionId, uint256 newPrice) internal {
        uint256 metadata = priceMetadata[opinionId];
        metadata = (metadata << 24) | (block.timestamp & ((1 << 24) - 1));
        priceMetadata[opinionId] = metadata;

        uint256 history = priceHistory[opinionId];
        history = (history << 80);
        history |= (newPrice & ((1 << 80) - 1));
        priceHistory[opinionId] = history;
    }

    function _checkAndUpdateTradesInBlock() internal {
        // Skip rate limiting if maxTradesPerBlock is 0
        if (maxTradesPerBlock == 0) return;

        if (userLastBlock[msg.sender] != block.number) {
            userTradesInBlock[msg.sender] = 1;
            userLastBlock[msg.sender] = block.number;
        } else {
            userTradesInBlock[msg.sender]++;
            if (userTradesInBlock[msg.sender] > maxTradesPerBlock) {
                revert MaxTradesPerBlockExceeded(userTradesInBlock[msg.sender], maxTradesPerBlock);
            }
        }
    }

    function _checkTradeAllowed(uint256 opinionId) internal {
        if (userLastTradeBlock[msg.sender][opinionId] == block.number)
            revert OneTradePerBlock();
        userLastTradeBlock[msg.sender][opinionId] = block.number;
    }

    // Removed stub functions to save space - functions moved to appropriate contracts

    // --- UUPS UPGRADE AUTHORIZATION ---
    function _authorizeUpgrade(address newImplementation) internal override onlyRole(ADMIN_ROLE) {}

    // --- ADMIN TRANSFER ---
    function transferFullAdmin(address newAdmin) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newAdmin != address(0), "Invalid address");
        _grantRole(DEFAULT_ADMIN_ROLE, newAdmin);
        _grantRole(ADMIN_ROLE, newAdmin);
        _grantRole(MODERATOR_ROLE, newAdmin);
        _revokeRole(MODERATOR_ROLE, msg.sender);
        _revokeRole(ADMIN_ROLE, msg.sender);
        _revokeRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }
}

contracts/active/OpinionCoreNoMod.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IOpinionCore.sol";
import "./interfaces/IFeeManager.sol";
import "./interfaces/IPoolManager.sol";
import "./interfaces/IMonitoringManager.sol";
import "./interfaces/ISecurityManager.sol";
import "./interfaces/IOpinionMarketEvents.sol";
import "./interfaces/IOpinionMarketErrors.sol";
import "./structs/OpinionStructs.sol";
import "./libraries/ValidationLibrary.sol";
import "./libraries/PriceCalculator.sol";
import "./interfaces/IValidationErrors.sol";

/**
 * @title OpinionMarketCapCore
 * @dev Core contract for managing opinions, answers, and related functionality
 * Security and monitoring features have been extracted to separate contracts
 */
contract OpinionMarketCapCore is
    Initializable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    IOpinionCore,
    IOpinionMarketEvents,
    IOpinionMarketErrors,
    IValidationErrors
{
    using SafeERC20 for IERC20;

    // --- ROLES ---
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");
    bytes32 public constant MARKET_CONTRACT_ROLE = keccak256("MARKET_CONTRACT_ROLE");
    bytes32 public constant POOL_MANAGER_ROLE = keccak256("POOL_MANAGER_ROLE");

    // --- CONSTANTS ---
    uint256 public constant MAX_QUESTION_LENGTH = 60;
    uint256 public constant MAX_ANSWER_LENGTH = 60;
    uint256 public constant MAX_LINK_LENGTH = 260;
    uint256 public constant MAX_IPFS_HASH_LENGTH = 68;
    uint256 public constant MAX_DESCRIPTION_LENGTH = 280;
    uint256 public constant MAX_CATEGORIES_PER_OPINION = 3;

    // --- INITIAL PRICE RANGE CONSTANTS ---
    uint96 public constant MIN_INITIAL_PRICE = 1_000_000;   // 1 USDC (6 decimals)
    uint96 public maxInitialPrice = 100_000_000; // 100 USDC (6 decimals) - Admin configurable

    // --- STATE VARIABLES ---
    IERC20 public usdcToken;
    IFeeManager public feeManager;
    IPoolManager public poolManager;
    IMonitoringManager public monitoringManager;
    ISecurityManager public securityManager;

    address public treasury;
    address public pendingTreasury;
    uint256 public treasuryChangeTimestamp;
    uint256 public constant TREASURY_CHANGE_DELAY = 48 hours;

    bool public isPublicCreationEnabled;
    uint256 public nextOpinionId;

    // --- CATEGORIES STORAGE ---
    string[] public categories;

    // --- EXTENSION SLOTS STORAGE ---
    mapping(uint256 => mapping(string => string)) public opinionStringExtensions;
    mapping(uint256 => mapping(string => uint256)) public opinionNumberExtensions;  
    mapping(uint256 => mapping(string => bool)) public opinionBoolExtensions;
    mapping(uint256 => mapping(string => address)) public opinionAddressExtensions;
    mapping(uint256 => string[]) public opinionExtensionKeys;

    // Security and rate limiting
    uint256 public maxTradesPerBlock;
    mapping(address => uint256) private userLastBlock;
    mapping(address => uint256) private userTradesInBlock;
    mapping(address => mapping(uint256 => uint256)) private userLastTradeBlock;

    // Price calculation
    uint256 private nonce;
    mapping(uint256 => uint256) private priceMetadata;
    mapping(uint256 => uint256) private priceHistory;

    // Configurable parameters
    uint96 public minimumPrice;
    uint96 public questionCreationFee;
    uint96 public initialAnswerPrice;
    uint256 public absoluteMaxPriceChange;

    // Core data structures
    mapping(uint256 => OpinionStructs.Opinion) public opinions;
    mapping(uint256 => OpinionStructs.AnswerHistory[]) public answerHistory;

    // --- INITIALIZATION ---
    function initialize(
        address _usdcToken,
        address _opinionMarket,
        address _feeManager,
        address _poolManager,
        address _monitoringManager,
        address _securityManager,
        address _treasury
    ) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();
        __Pausable_init();

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(MODERATOR_ROLE, msg.sender);

        // Set external contracts
        if (
            _usdcToken == address(0) ||
            _opinionMarket == address(0) ||
            _feeManager == address(0) ||
            _poolManager == address(0) ||
            _treasury == address(0)
        ) revert ZeroAddressNotAllowed();

        usdcToken = IERC20(_usdcToken);
        feeManager = IFeeManager(_feeManager);
        poolManager = IPoolManager(_poolManager);
        treasury = _treasury;

        // Grant role to opinion market contract
        _grantRole(MARKET_CONTRACT_ROLE, _opinionMarket);

        // Set optional modules (can be zero initially)
        if (_monitoringManager != address(0)) {
            monitoringManager = IMonitoringManager(_monitoringManager);
        }
        if (_securityManager != address(0)) {
            securityManager = ISecurityManager(_securityManager);
        }

        // Initialize parameters
        nextOpinionId = 1;
        isPublicCreationEnabled = true;
        maxTradesPerBlock = 0; // No trade limit per block
        minimumPrice = 1_000_000; // 1 USDC (6 decimals)
        questionCreationFee = 2_000_000; // 2 USDC minimum
        initialAnswerPrice = 1_000_000; // 1 USDC
        absoluteMaxPriceChange = 200; // 200%
        maxInitialPrice = 100_000_000; // 100 USDC

        // Initialize default categories
        categories = [
            "Technology", "AI & Robotics", "Crypto & Web3", "DeFi (Decentralized Finance)", 
            "Science", "Environment & Climate", "Business & Finance", "Real Estate", 
            "Politics", "Law & Legal", "News", "Sports", "Automotive", "Gaming", 
            "Movies", "TV Shows", "Music", "Podcasts", "Literature", "Art & Design", 
            "Photography", "Celebrities & Pop Culture", "Social Media", "Humor & Memes", 
            "Fashion", "Beauty & Skincare", "Health & Fitness", "Food & Drink", "Travel", 
            "History", "Philosophy", "Spirituality & Religion", "Education", 
            "Career & Workplace", "Relationships", "Parenting & Family", "Pets & Animals", 
            "DIY & Home Improvement", "True Crime", "Adult (NSFW)"
        ];
    }

    // --- MODIFIERS ---
    modifier onlyMarketContract() {
        if (!hasRole(MARKET_CONTRACT_ROLE, msg.sender))
            revert AccessControlUnauthorizedAccount(msg.sender, MARKET_CONTRACT_ROLE);
        _;
    }

    modifier onlyPoolManager() {
        if (!hasRole(POOL_MANAGER_ROLE, msg.sender))
            revert AccessControlUnauthorizedAccount(msg.sender, POOL_MANAGER_ROLE);
        _;
    }

    // --- CORE OPINION FUNCTIONS ---

    function createOpinion(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) external override nonReentrant whenNotPaused {
        // Access control check
        if (!isPublicCreationEnabled && !hasRole(ADMIN_ROLE, msg.sender))
            revert UnauthorizedCreator();

        // Enhanced validation if SecurityManager is available
        if (address(securityManager) != address(0)) {
            try securityManager.validateCreateOpinionInputs(
                question, answer, description, initialPrice, opinionCategories, msg.sender
            ) {} catch {
                // Fallback to basic validation if security manager fails
                _basicValidation(question, answer, description, initialPrice, opinionCategories);
            }
        } else {
            _basicValidation(question, answer, description, initialPrice, opinionCategories);
        }

        // Check allowance for initialPrice
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < initialPrice)
            revert InsufficientAllowance(initialPrice, allowance);

        // Create opinion record
        uint256 opinionId = _createOpinionRecord(
            question, answer, description, "", "", initialPrice, opinionCategories
        );

        // Transfer payment to treasury
        usdcToken.safeTransferFrom(msg.sender, treasury, initialPrice);

        // Track with monitoring manager if available
        if (address(monitoringManager) != address(0)) {
            try monitoringManager.trackOpinionCreation(opinionId, msg.sender, initialPrice) {} catch {}
        }

        // Emit events
        emit OpinionAction(opinionId, 0, question, msg.sender, initialPrice);
        emit OpinionAction(opinionId, 1, answer, msg.sender, initialPrice);
    }

    function createOpinionWithExtras(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories,
        string calldata ipfsHash,
        string calldata link
    ) external override nonReentrant whenNotPaused {
        // Access control check
        if (!isPublicCreationEnabled && !hasRole(ADMIN_ROLE, msg.sender))
            revert UnauthorizedCreator();

        // Enhanced validation if SecurityManager is available
        if (address(securityManager) != address(0)) {
            try securityManager.validateCreateOpinionInputs(
                question, answer, description, initialPrice, opinionCategories, msg.sender
            ) {} catch {
                _basicValidation(question, answer, description, initialPrice, opinionCategories);
            }
        } else {
            _basicValidation(question, answer, description, initialPrice, opinionCategories);
        }

        // Validate IPFS hash and link
        _validateExtras(ipfsHash, link);

        // Check allowance
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < initialPrice)
            revert InsufficientAllowance(initialPrice, allowance);

        // Create opinion record
        uint256 opinionId = _createOpinionRecord(
            question, answer, description, ipfsHash, link, initialPrice, opinionCategories
        );

        // Transfer payment to treasury
        usdcToken.safeTransferFrom(msg.sender, treasury, initialPrice);

        // Track with monitoring manager if available
        if (address(monitoringManager) != address(0)) {
            try monitoringManager.trackOpinionCreation(opinionId, msg.sender, initialPrice) {} catch {}
        }

        // Emit events
        emit OpinionAction(opinionId, 0, question, msg.sender, initialPrice);
        emit OpinionAction(opinionId, 1, answer, msg.sender, initialPrice);
    }

    function submitAnswer(
        uint256 opinionId,
        string calldata answer,
        string calldata description,
        string calldata link
    ) external override nonReentrant whenNotPaused {
        _checkAndUpdateTradesInBlock();
        _checkTradeAllowed(opinionId);

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (!opinion.isActive) revert OpinionNotActive();
        if (opinion.currentAnswerOwner == msg.sender) revert SameOwner();

        // Basic validation
        _validateAnswerInput(answer, description);

        // Validate link (optional)
        bytes memory linkBytes = bytes(link);
        if (linkBytes.length > MAX_LINK_LENGTH) revert InvalidLinkLength();

        // Calculate price
        uint96 price = opinion.nextPrice > 0
            ? opinion.nextPrice
            : uint96(_calculateNextPrice(opinionId, opinion.lastPrice));

        // Check USDC allowance
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < price) revert InsufficientAllowance(price, allowance);

        // Security analysis if SecurityManager is available
        if (address(securityManager) != address(0)) {
            // Check MEV risk
            try securityManager.analyzeMevRisk(msg.sender, price, opinionId) {} catch {}

            // Analyze bot patterns
            // Note: We can't easily determine trade success here, so we skip bot analysis for now
        }

        // Calculate fees
        (uint96 platformFee, uint96 creatorFee, uint96 ownerAmount) = feeManager
            .calculateFeeDistribution(price);

        // Apply MEV penalty if needed
        (platformFee, ownerAmount) = feeManager.applyMEVPenalty(
            price, ownerAmount, msg.sender, opinionId
        );

        // Get addresses for fee distribution
        address creator = opinion.creator;
        address currentAnswerOwner = opinion.currentAnswerOwner;
        bool answerIsPoolOwned = currentAnswerOwner == address(poolManager);

        // Accumulate fees
        feeManager.accumulateFee(creator, creatorFee);

        if (answerIsPoolOwned) {
            poolManager.distributePoolRewards(opinionId, price, msg.sender);
        } else {
            feeManager.accumulateFee(currentAnswerOwner, ownerAmount);
        }

        // Record answer history
        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: description,
                owner: msg.sender,
                price: price,
                timestamp: uint32(block.timestamp)
            })
        );

        // Update opinion state
        uint96 oldPrice = opinion.lastPrice;
        opinion.currentAnswer = answer;
        opinion.currentAnswerDescription = description;
        opinion.currentAnswerOwner = msg.sender;
        opinion.link = link;
        opinion.lastPrice = price;
        opinion.totalVolume += price;
        opinion.nextPrice = uint96(_calculateNextPrice(opinionId, price));

        // Transfer tokens
        usdcToken.safeTransferFrom(msg.sender, address(this), price);

        // Track with monitoring manager if available
        if (address(monitoringManager) != address(0)) {
            try monitoringManager.trackTradingActivity(
                opinionId, msg.sender, price, oldPrice, opinion.nextPrice, platformFee
            ) {} catch {}
        }

        // Update security profiles if SecurityManager is available
        if (address(securityManager) != address(0)) {
            try securityManager.updateMevProfile(msg.sender, opinionId, price) {} catch {}
        }

        // Emit events
        emit FeesAction(opinionId, 0, currentAnswerOwner, price, platformFee, creatorFee, ownerAmount);
        emit OpinionAction(opinionId, 1, answer, msg.sender, price);
    }

    // --- QUESTION TRADING FUNCTIONS ---

    function listQuestionForSale(uint256 opinionId, uint256 price) external override nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (opinion.questionOwner != msg.sender) revert NotTheOwner(msg.sender, opinion.questionOwner);

        opinion.salePrice = uint96(price);
        emit QuestionSaleAction(opinionId, 0, msg.sender, address(0), price);
    }

    function buyQuestion(uint256 opinionId) external override nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();

        uint96 salePrice = opinion.salePrice;
        if (salePrice == 0) revert NotForSale(opinionId);

        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < salePrice) revert InsufficientAllowance(salePrice, allowance);

        address currentOwner = opinion.questionOwner;
        uint96 platformFee = uint96((salePrice * 10) / 100);
        uint96 sellerAmount = salePrice - platformFee;

        opinion.questionOwner = msg.sender;
        opinion.salePrice = 0;

        usdcToken.safeTransferFrom(msg.sender, address(this), salePrice);
        feeManager.accumulateFee(currentOwner, sellerAmount);

        emit QuestionSaleAction(opinionId, 1, currentOwner, msg.sender, salePrice);
    }

    function cancelQuestionSale(uint256 opinionId) external override nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (opinion.questionOwner != msg.sender) revert NotTheOwner(msg.sender, opinion.questionOwner);

        opinion.salePrice = 0;
        emit QuestionSaleAction(opinionId, 2, msg.sender, address(0), 0);
    }

    /**
     * @notice Transfer question ownership and future royalties to another address (free transfer)
     * @param opinionId The ID of the opinion/question
     * @param newOwner The address to transfer ownership to
     */
    function transferQuestionOwnership(uint256 opinionId, address newOwner) external nonReentrant whenNotPaused {
        if (newOwner == address(0)) revert ZeroAddressNotAllowed();

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (opinion.questionOwner != msg.sender) revert NotTheOwner(msg.sender, opinion.questionOwner);

        address previousOwner = opinion.questionOwner;
        opinion.questionOwner = newOwner;

        // Clear any existing sale listing
        opinion.salePrice = 0;

        emit QuestionOwnershipTransferred(opinionId, previousOwner, newOwner);
    }

    // --- MODERATION FUNCTIONS (REMOVED FOR SIZE OPTIMIZATION) ---
    // Can be added back in future upgrades

    function deactivateOpinion(uint256) external pure override {
        revert("Moderation not implemented");
    }

    function reactivateOpinion(uint256) external pure override {
        revert("Moderation not implemented");  
    }

    // --- POOL INTEGRATION ---

    function updateOpinionOnPoolExecution(
        uint256 opinionId,
        string calldata answer,
        address poolAddress,
        uint256 price
    ) external override onlyPoolManager {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (!opinion.isActive) revert OpinionNotActive();

        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: "",
                owner: poolAddress,
                price: uint96(price),
                timestamp: uint32(block.timestamp)
            })
        );

        opinion.currentAnswer = answer;
        opinion.currentAnswerDescription = "";
        opinion.currentAnswerOwner = poolAddress;
        opinion.lastPrice = uint96(price);
        opinion.totalVolume += uint96(price);
        opinion.nextPrice = uint96(_calculateNextPrice(opinionId, price));

        emit OpinionAction(opinionId, 1, answer, address(poolManager), price);
    }

    // --- VIEW FUNCTIONS ---

    function getAnswerHistory(uint256 opinionId) external view override returns (OpinionStructs.AnswerHistory[] memory) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return answerHistory[opinionId];
    }

    function getNextPrice(uint256 opinionId) external view override returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (!opinion.isActive) revert OpinionNotActive();

        if (opinion.nextPrice == 0) {
            return _estimateNextPrice(opinion.lastPrice);
        }
        return opinion.nextPrice;
    }

    function getOpinionDetails(uint256 opinionId) external view override returns (OpinionStructs.Opinion memory) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId];
    }

    function getTradeCount(uint256 opinionId) external view override returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return answerHistory[opinionId].length;
    }

    function getCreatorGain(uint256 opinionId) external view override returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];

        (, uint96 creatorFeePercent, ) = feeManager.calculateFeeDistribution(1_000_000);
        uint256 creatorFeeRate = creatorFeePercent / 10;
        return (opinion.totalVolume * creatorFeeRate) / 100;
    }

    function isPoolOwned(uint256 opinionId) external view override returns (bool) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId].currentAnswerOwner == address(poolManager);
    }

    // --- CATEGORIES FUNCTIONS ---

    function addCategoryToCategories(string calldata newCategory) external onlyRole(ADMIN_ROLE) {
        bytes32 newCategoryHash = keccak256(bytes(newCategory));
        uint256 length = categories.length;

        for (uint256 i = 0; i < length; i++) {
            if (keccak256(bytes(categories[i])) == newCategoryHash) {
                revert CategoryAlreadyExists();
            }
        }
        categories.push(newCategory);
    }

    function getAvailableCategories() external view returns (string[] memory) {
        return categories;
    }

    function getOpinionCategories(uint256 opinionId) external view returns (string[] memory) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId].categories;
    }

    function getCategoryCount() external view returns (uint256) {
        return categories.length;
    }

    // --- EXTENSION SLOTS FUNCTIONS ---

    function setOpinionStringExtension(uint256 opinionId, string calldata key, string calldata value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionStringExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionStringExtensionSet(opinionId, key, value);
    }

    function setOpinionNumberExtension(uint256 opinionId, string calldata key, uint256 value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionNumberExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionNumberExtensionSet(opinionId, key, value);
    }

    function setOpinionBoolExtension(uint256 opinionId, string calldata key, bool value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionBoolExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionBoolExtensionSet(opinionId, key, value);
    }

    function setOpinionAddressExtension(uint256 opinionId, string calldata key, address value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionAddressExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionAddressExtensionSet(opinionId, key, value);
    }

    function getOpinionExtensions(uint256 opinionId) external view returns (
        string[] memory keys,
        string[] memory stringValues,
        uint256[] memory numberValues,
        bool[] memory boolValues,
        address[] memory addressValues
    ) {
        keys = opinionExtensionKeys[opinionId];
        uint256 length = keys.length;

        stringValues = new string[](length);
        numberValues = new uint256[](length);
        boolValues = new bool[](length);
        addressValues = new address[](length);

        for (uint256 i = 0; i < length; i++) {
            string memory key = keys[i];
            stringValues[i] = opinionStringExtensions[opinionId][key];
            numberValues[i] = opinionNumberExtensions[opinionId][key];
            boolValues[i] = opinionBoolExtensions[opinionId][key];
            addressValues[i] = opinionAddressExtensions[opinionId][key];
        }

        return (keys, stringValues, numberValues, boolValues, addressValues);
    }

    function getOpinionStringExtension(uint256 opinionId, string calldata key) external view returns (string memory) {
        return opinionStringExtensions[opinionId][key];
    }

    function getOpinionNumberExtension(uint256 opinionId, string calldata key) external view returns (uint256) {
        return opinionNumberExtensions[opinionId][key];
    }

    function getOpinionBoolExtension(uint256 opinionId, string calldata key) external view returns (bool) {
        return opinionBoolExtensions[opinionId][key];
    }

    function getOpinionAddressExtension(uint256 opinionId, string calldata key) external view returns (address) {
        return opinionAddressExtensions[opinionId][key];
    }

    function hasOpinionExtension(uint256 opinionId, string calldata key) external view returns (bool) {
        string[] memory keys = opinionExtensionKeys[opinionId];
        bytes32 keyHash = keccak256(bytes(key));

        for (uint256 i = 0; i < keys.length; i++) {
            if (keccak256(bytes(keys[i])) == keyHash) {
                return true;
            }
        }
        return false;
    }

    function getOpinionExtensionCount(uint256 opinionId) external view returns (uint256) {
        return opinionExtensionKeys[opinionId].length;
    }

    // --- ADMIN FUNCTIONS ---

    function setMinimumPrice(uint96 _minimumPrice) external onlyRole(ADMIN_ROLE) {
        minimumPrice = _minimumPrice;
        emit ParameterUpdated(0, _minimumPrice);
    }

    function setQuestionCreationFee(uint96 _questionCreationFee) external onlyRole(ADMIN_ROLE) {
        questionCreationFee = _questionCreationFee;
        emit ParameterUpdated(6, _questionCreationFee);
    }

    function setInitialAnswerPrice(uint96 _initialAnswerPrice) external onlyRole(ADMIN_ROLE) {
        initialAnswerPrice = _initialAnswerPrice;
        emit ParameterUpdated(7, _initialAnswerPrice);
    }

    function setMaxInitialPrice(uint96 _maxInitialPrice) external onlyRole(ADMIN_ROLE) {
        if (_maxInitialPrice < MIN_INITIAL_PRICE) revert InvalidInitialPrice();
        maxInitialPrice = _maxInitialPrice;
        emit ParameterUpdated(8, _maxInitialPrice);
    }

    function setMaxPriceChange(uint256 _maxPriceChange) external onlyRole(ADMIN_ROLE) {
        absoluteMaxPriceChange = _maxPriceChange;
        emit ParameterUpdated(3, _maxPriceChange);
    }

    function setMaxTradesPerBlock(uint256 _maxTradesPerBlock) external onlyRole(ADMIN_ROLE) {
        maxTradesPerBlock = _maxTradesPerBlock;
        emit ParameterUpdated(4, _maxTradesPerBlock);
    }

    function togglePublicCreation() external onlyRole(ADMIN_ROLE) {
        isPublicCreationEnabled = !isPublicCreationEnabled;
        emit AdminAction(1, msg.sender, bytes32(0), 0);
    }

    function setFeeManager(address _feeManager) external onlyRole(ADMIN_ROLE) {
        if (_feeManager == address(0)) revert ZeroAddressNotAllowed();
        feeManager = IFeeManager(_feeManager);
    }

    function setPoolManager(address _poolManager) external onlyRole(ADMIN_ROLE) {
        if (_poolManager == address(0)) revert ZeroAddressNotAllowed();
        poolManager = IPoolManager(_poolManager);
    }

    function setMonitoringManager(address _monitoringManager) external onlyRole(ADMIN_ROLE) {
        monitoringManager = IMonitoringManager(_monitoringManager);
    }

    function setSecurityManager(address _securityManager) external onlyRole(ADMIN_ROLE) {
        securityManager = ISecurityManager(_securityManager);
    }

    function grantMarketContractRole(address contractAddress) external onlyRole(ADMIN_ROLE) {
        if (contractAddress == address(0)) revert ZeroAddressNotAllowed();
        _grantRole(MARKET_CONTRACT_ROLE, contractAddress);
    }

    function revokeMarketContractRole(address contractAddress) external onlyRole(ADMIN_ROLE) {
        _revokeRole(MARKET_CONTRACT_ROLE, contractAddress);
    }

    function setTreasury(address newTreasury) external onlyRole(ADMIN_ROLE) {
        if (newTreasury == address(0)) revert ZeroAddressNotAllowed();

        pendingTreasury = newTreasury;
        treasuryChangeTimestamp = block.timestamp + TREASURY_CHANGE_DELAY;

        emit TreasuryUpdated(treasury, newTreasury, msg.sender, block.timestamp);
    }

    function confirmTreasuryChange() external onlyRole(ADMIN_ROLE) {
        if (block.timestamp < treasuryChangeTimestamp) 
            revert("Treasury: Timelock not elapsed");
        if (pendingTreasury == address(0)) 
            revert("Treasury: No pending treasury");

        address oldTreasury = treasury;
        treasury = pendingTreasury;
        pendingTreasury = address(0);
        treasuryChangeTimestamp = 0;

        emit TreasuryUpdated(oldTreasury, treasury, msg.sender, block.timestamp);
    }

    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    function emergencyWithdraw(address token) external nonReentrant whenPaused onlyRole(ADMIN_ROLE) {
        if (token == address(0)) revert ZeroAddressNotAllowed();

        IERC20 tokenContract = IERC20(token);
        uint256 balance = tokenContract.balanceOf(address(this));

        if (token == address(usdcToken)) {
            uint256 totalFees = feeManager.getTotalAccumulatedFees();
            if (balance <= totalFees) revert("Insufficient balance after fees");
            balance -= totalFees;
        }

        tokenContract.safeTransfer(msg.sender, balance);
        emit AdminAction(0, msg.sender, bytes32(0), balance);
    }

    // --- INTERNAL FUNCTIONS ---

    function _basicValidation(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) internal view {
        ValidationLibrary.validateOpinionCategories(opinionCategories, categories);
        ValidationLibrary.validateOpinionParams(question, answer, MAX_QUESTION_LENGTH, MAX_ANSWER_LENGTH);
        ValidationLibrary.validateDescription(description);

        if (initialPrice < MIN_INITIAL_PRICE || initialPrice > maxInitialPrice) {
            revert InvalidInitialPrice();
        }
    }

    function _validateExtras(string calldata ipfsHash, string calldata link) internal pure {
        bytes memory ipfsHashBytes = bytes(ipfsHash);
        bytes memory linkBytes = bytes(link);

        if (ipfsHashBytes.length > MAX_IPFS_HASH_LENGTH) revert InvalidIpfsHashLength();
        if (linkBytes.length > MAX_LINK_LENGTH) revert InvalidLinkLength();

        if (ipfsHashBytes.length > 0) {
            _validateIpfsHash(ipfsHash);
        }
    }

    function _validateAnswerInput(string calldata answer, string calldata description) internal pure {
        bytes memory answerBytes = bytes(answer);
        if (answerBytes.length == 0) revert EmptyString();
        if (answerBytes.length > MAX_ANSWER_LENGTH) revert InvalidAnswerLength();

        ValidationLibrary.validateDescription(description);
    }

    function _createOpinionRecord(
        string memory question,
        string memory answer,
        string memory description,
        string memory ipfsHash,
        string memory link,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) internal returns (uint256) {
        uint256 opinionId = nextOpinionId++;
        OpinionStructs.Opinion storage opinion = opinions[opinionId];

        opinion.creator = msg.sender;
        opinion.questionOwner = msg.sender;
        opinion.lastPrice = initialPrice;
        opinion.nextPrice = uint96(_calculateNextPrice(opinionId, initialPrice));
        opinion.isActive = true;
        opinion.question = question;
        opinion.currentAnswer = answer;
        opinion.currentAnswerDescription = description;
        opinion.currentAnswerOwner = msg.sender;
        opinion.totalVolume = initialPrice;
        opinion.ipfsHash = ipfsHash;
        opinion.link = link;
        opinion.categories = opinionCategories;

        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: description,
                owner: msg.sender,
                price: initialPrice,
                timestamp: uint32(block.timestamp)
            })
        );

        return opinionId;
    }

    function _validateIpfsHash(string memory _ipfsHash) internal pure {
        bytes memory ipfsHashBytes = bytes(_ipfsHash);

        bool isValidCIDv0 = ipfsHashBytes.length == 46 &&
            ipfsHashBytes[0] == "Q" &&
            ipfsHashBytes[1] == "m";

        bool isValidCIDv1 = ipfsHashBytes.length >= 48 &&
            ipfsHashBytes[0] == "b";

        if (!isValidCIDv0 && !isValidCIDv1) {
            revert InvalidIpfsHashFormat();
        }
    }

    function _calculateNextPrice(uint256 opinionId, uint256 lastPrice) internal returns (uint256) {
        uint256 newPrice = PriceCalculator.calculateNextPrice(
            opinionId,
            lastPrice,
            minimumPrice,
            absoluteMaxPriceChange,
            nonce++,
            priceMetadata,
            priceHistory
        );

        _updatePriceHistory(opinionId, newPrice);
        return newPrice;
    }

    function _estimateNextPrice(uint256 lastPrice) internal pure returns (uint256) {
        return (lastPrice * 130) / 100;
    }

    function _updatePriceHistory(uint256 opinionId, uint256 newPrice) internal {
        uint256 meta = priceMetadata[opinionId];
        uint8 count = uint8(meta);

        priceMetadata[opinionId] = (block.timestamp << 8) | (count < 3 ? count + 1 : 3);

        uint256 history = priceHistory[opinionId];
        history = (history << 80) & (~uint256(0) << 160);
        history |= (newPrice & ((1 << 80) - 1));
        priceHistory[opinionId] = history;
    }

    function _checkAndUpdateTradesInBlock() internal {
        // Skip rate limiting if maxTradesPerBlock is 0
        if (maxTradesPerBlock == 0) return;

        if (userLastBlock[msg.sender] != block.number) {
            userTradesInBlock[msg.sender] = 1;
            userLastBlock[msg.sender] = block.number;
        } else {
            userTradesInBlock[msg.sender]++;
            if (userTradesInBlock[msg.sender] > maxTradesPerBlock) {
                revert MaxTradesPerBlockExceeded(userTradesInBlock[msg.sender], maxTradesPerBlock);
            }
        }
    }

    function _checkTradeAllowed(uint256 opinionId) internal {
        if (userLastTradeBlock[msg.sender][opinionId] == block.number)
            revert OneTradePerBlock();
        userLastTradeBlock[msg.sender][opinionId] = block.number;
    }

    function isValidExtensionKey(string memory key) internal pure returns (bool) {
        bytes memory keyBytes = bytes(key);

        if (keyBytes.length == 0 || keyBytes.length > 32) return false;

        for (uint i = 0; i < keyBytes.length; i++) {
            uint8 char = uint8(keyBytes[i]);
            bool isAlpha = (char >= 65 && char <= 90) || (char >= 97 && char <= 122);
            bool isNumeric = (char >= 48 && char <= 57);
            bool isUnderscore = (char == 95);

            if (!isAlpha && !isNumeric && !isUnderscore) return false;
        }

        return true;
    }

    function _trackExtensionKey(uint256 opinionId, string memory key) internal {
        string[] storage keys = opinionExtensionKeys[opinionId];
        bytes32 keyHash = keccak256(bytes(key));

        for (uint i = 0; i < keys.length; i++) {
            if (keccak256(bytes(keys[i])) == keyHash) {
                return;
            }
        }

        keys.push(key);
    }

    function moderateAnswer(uint256, string calldata) external pure {
        revert("Moderation not implemented");
    }

    // --- MISSING INTERFACE IMPLEMENTATIONS ---

    /**
     * @dev Transfer answer ownership (simplified implementation)
     */
    function transferAnswerOwnership(
        uint256 opinionId,
        address newOwner
    ) external override nonReentrant whenNotPaused {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (newOwner == address(0)) revert ZeroAddressNotAllowed();

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.currentAnswerOwner != msg.sender) revert NotAnswerOwner();
        if (newOwner == msg.sender) revert("Cannot transfer to yourself");

        address previousOwner = opinion.currentAnswerOwner;
        opinion.currentAnswerOwner = newOwner;

        emit AnswerOwnershipTransferred(opinionId, previousOwner, newOwner, block.timestamp);
        emit OpinionAction(opinionId, 5, "", newOwner, 0);
    }

    /**
     * @dev Update opinion (simplified implementation)  
     */
    function updateOpinion(
        uint256 opinionId,
        string calldata question,
        string calldata ipfsHash,
        string calldata link,
        string[] calldata opinionCategories
    ) external override nonReentrant whenNotPaused {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator != msg.sender) revert("Only creator can update");

        // Basic validation
        if (bytes(question).length > MAX_QUESTION_LENGTH) revert InvalidQuestionLength();
        if (bytes(ipfsHash).length > MAX_IPFS_HASH_LENGTH) revert InvalidIpfsHashLength();
        if (bytes(link).length > MAX_LINK_LENGTH) revert InvalidLinkLength();
        if (opinionCategories.length > MAX_CATEGORIES_PER_OPINION) revert TooManyCategories();

        // Update the opinion
        opinion.question = question;
        opinion.ipfsHash = ipfsHash;
        opinion.link = link;
        opinion.categories = opinionCategories;

        emit OpinionAction(opinionId, 6, question, msg.sender, 0);
    }

    // Events are inherited from IOpinionMarketEvents
}

contracts/active/OpinionExtensions.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

import "./interfaces/IOpinionExtensionsInternal.sol";
import "./interfaces/IOpinionCoreInternal.sol";
import "./interfaces/IOpinionMarketEvents.sol";
import "./interfaces/IOpinionMarketErrors.sol";

/**
 * @title OpinionExtensions
 * @dev Extension slots and categories management - Size optimized (~8KB)
 */
contract OpinionExtensions is
    Initializable,
    UUPSUpgradeable,
    AccessControlUpgradeable,
    IOpinionExtensionsInternal,
    IOpinionMarketEvents,
    IOpinionMarketErrors
{
    // --- ROLES ---
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant CORE_CONTRACT_ROLE = keccak256("CORE_CONTRACT_ROLE");

    // --- CONSTANTS ---
    uint256 public constant MAX_CATEGORIES_PER_OPINION = 3;

    // --- STATE VARIABLES ---
    IOpinionCoreInternal public coreContract;

    // Categories storage
    string[] public categories;
    mapping(uint256 => string[]) public opinionCategories;

    // Extension slots storage
    mapping(uint256 => mapping(string => string)) public opinionStringExtensions;
    mapping(uint256 => mapping(string => uint256)) public opinionNumberExtensions;
    mapping(uint256 => mapping(string => bool)) public opinionBoolExtensions;
    mapping(uint256 => mapping(string => address)) public opinionAddressExtensions;
    mapping(uint256 => string[]) public opinionExtensionKeys;

    // --- MODIFIERS ---
    modifier onlyCoreContract() {
        if (!hasRole(CORE_CONTRACT_ROLE, msg.sender))
            revert AccessControlUnauthorizedAccount(msg.sender, CORE_CONTRACT_ROLE);
        _;
    }

    modifier validOpinion(uint256 opinionId) {
        require(coreContract.validateOpinionExists(opinionId), "Opinion not found");
        _;
    }

    // --- INITIALIZATION ---
    function initialize(
        address _coreContract,
        address _admin
    ) public initializer {
        __AccessControl_init();

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);
        _grantRole(CORE_CONTRACT_ROLE, _coreContract);

        coreContract = IOpinionCoreInternal(_coreContract);

        // Initialize default categories
        categories = [
            "Technology", "AI & Robotics", "Crypto & Web3", "DeFi (Decentralized Finance)", 
            "Science", "Environment & Climate", "Business & Finance", "Real Estate", 
            "Politics", "Law & Legal", "News", "Sports", "Automotive", "Gaming", 
            "Movies", "TV Shows", "Music", "Podcasts", "Literature", "Art & Design", 
            "Photography", "Celebrities & Pop Culture", "Social Media", "Humor & Memes", 
            "Fashion", "Beauty & Skincare", "Health & Fitness", "Food & Drink", "Travel", 
            "History", "Philosophy", "Spirituality & Religion", "Education", 
            "Career & Workplace", "Relationships", "Parenting & Family", "Pets & Animals", 
            "DIY & Home Improvement", "True Crime", "Adult (NSFW)"
        ];
    }

    // --- CONTRACT MANAGEMENT ---

    /**
     * @dev Sets the core contract address. Essential for post-deployment linking.
     */
    function setCoreContract(address _coreContract) external onlyRole(ADMIN_ROLE) {
        require(_coreContract != address(0), "Zero address");

        // Revoke role from old address if it exists
        address oldCore = address(coreContract);
        if (oldCore != address(0)) {
            _revokeRole(CORE_CONTRACT_ROLE, oldCore);
        }

        // Grant role to new address and update reference
        _grantRole(CORE_CONTRACT_ROLE, _coreContract);
        coreContract = IOpinionCoreInternal(_coreContract);
    }

    // --- EXTENSION SLOTS FUNCTIONS ---

    function setOpinionStringExtension(
        uint256 opinionId, 
        string calldata key, 
        string calldata value
    ) external validOpinion(opinionId) {
        require(coreContract.getOpinionCreator(opinionId) == msg.sender, "Not opinion creator");

        if (!_hasExtensionKey(opinionId, key)) {
            opinionExtensionKeys[opinionId].push(key);
        }

        opinionStringExtensions[opinionId][key] = value;
        emit OpinionStringExtensionSet(opinionId, key, value);
    }

    function setOpinionNumberExtension(
        uint256 opinionId, 
        string calldata key, 
        uint256 value
    ) external validOpinion(opinionId) {
        require(coreContract.getOpinionCreator(opinionId) == msg.sender, "Not opinion creator");

        if (!_hasExtensionKey(opinionId, key)) {
            opinionExtensionKeys[opinionId].push(key);
        }

        opinionNumberExtensions[opinionId][key] = value;
        emit OpinionNumberExtensionSet(opinionId, key, value);
    }

    function setOpinionBoolExtension(
        uint256 opinionId, 
        string calldata key, 
        bool value
    ) external validOpinion(opinionId) {
        require(coreContract.getOpinionCreator(opinionId) == msg.sender, "Not opinion creator");

        if (!_hasExtensionKey(opinionId, key)) {
            opinionExtensionKeys[opinionId].push(key);
        }

        opinionBoolExtensions[opinionId][key] = value;
        emit OpinionBoolExtensionSet(opinionId, key, value);
    }

    function setOpinionAddressExtension(
        uint256 opinionId, 
        string calldata key, 
        address value
    ) external validOpinion(opinionId) {
        require(coreContract.getOpinionCreator(opinionId) == msg.sender, "Not opinion creator");

        if (!_hasExtensionKey(opinionId, key)) {
            opinionExtensionKeys[opinionId].push(key);
        }

        opinionAddressExtensions[opinionId][key] = value;
        emit OpinionAddressExtensionSet(opinionId, key, value);
    }

    function getOpinionExtensions(uint256 opinionId) external view validOpinion(opinionId) returns (
        string[] memory keys,
        string[] memory stringValues,
        uint256[] memory numberValues,
        bool[] memory boolValues,
        address[] memory addressValues
    ) {
        keys = opinionExtensionKeys[opinionId];
        stringValues = new string[](keys.length);
        numberValues = new uint256[](keys.length);
        boolValues = new bool[](keys.length);
        addressValues = new address[](keys.length);

        for (uint256 i = 0; i < keys.length; i++) {
            stringValues[i] = opinionStringExtensions[opinionId][keys[i]];
            numberValues[i] = opinionNumberExtensions[opinionId][keys[i]];
            boolValues[i] = opinionBoolExtensions[opinionId][keys[i]];
            addressValues[i] = opinionAddressExtensions[opinionId][keys[i]];
        }
    }

    function getOpinionStringExtension(uint256 opinionId, string calldata key) external view validOpinion(opinionId) returns (string memory) {
        return opinionStringExtensions[opinionId][key];
    }

    function getOpinionNumberExtension(uint256 opinionId, string calldata key) external view validOpinion(opinionId) returns (uint256) {
        return opinionNumberExtensions[opinionId][key];
    }

    function getOpinionBoolExtension(uint256 opinionId, string calldata key) external view validOpinion(opinionId) returns (bool) {
        return opinionBoolExtensions[opinionId][key];
    }

    function getOpinionAddressExtension(uint256 opinionId, string calldata key) external view validOpinion(opinionId) returns (address) {
        return opinionAddressExtensions[opinionId][key];
    }

    function hasOpinionExtension(uint256 opinionId, string calldata key) external view validOpinion(opinionId) returns (bool) {
        return _hasExtensionKey(opinionId, key);
    }

    function getOpinionExtensionCount(uint256 opinionId) external view validOpinion(opinionId) returns (uint256) {
        return opinionExtensionKeys[opinionId].length;
    }

    // --- CATEGORIES FUNCTIONS ---

    function addCategoryToCategories(string calldata newCategory) external onlyRole(ADMIN_ROLE) {
        // Check for duplicates
        for (uint256 i = 0; i < categories.length; i++) {
            if (keccak256(bytes(categories[i])) == keccak256(bytes(newCategory))) {
                revert("Category already exists");
            }
        }

        categories.push(newCategory);
        emit CategoryAction(0, categories.length - 1, newCategory, msg.sender, 0);
    }

    function getAvailableCategories() external view returns (string[] memory) {
        return categories;
    }

    function getOpinionCategories(uint256 opinionId) external view validOpinion(opinionId) returns (string[] memory) {
        return opinionCategories[opinionId];
    }

    function getCategoryCount() external view returns (uint256) {
        return categories.length;
    }

    // --- INTERNAL INTERFACE IMPLEMENTATION ---

    function validateExtensionAccess(uint256 opinionId, address caller) external view onlyCoreContract returns (bool) {
        if (!coreContract.validateOpinionExists(opinionId)) return false;
        return coreContract.getOpinionCreator(opinionId) == caller;
    }

    function initializeOpinionCategories(uint256 opinionId, string[] memory _categories) external onlyCoreContract {
        require(_categories.length <= MAX_CATEGORIES_PER_OPINION, "Too many categories");

        // Validate each category exists
        for (uint256 i = 0; i < _categories.length; i++) {
            bool found = false;
            for (uint256 j = 0; j < categories.length; j++) {
                if (keccak256(bytes(categories[j])) == keccak256(bytes(_categories[i]))) {
                    found = true;
                    break;
                }
            }
            require(found, "Invalid category");
        }

        opinionCategories[opinionId] = _categories;
    }

    function validateCategories(string[] memory _categories) external view onlyCoreContract returns (bool) {
        if (_categories.length > MAX_CATEGORIES_PER_OPINION) return false;

        for (uint256 i = 0; i < _categories.length; i++) {
            bool found = false;
            for (uint256 j = 0; j < categories.length; j++) {
                if (keccak256(bytes(categories[j])) == keccak256(bytes(_categories[i]))) {
                    found = true;
                    break;
                }
            }
            if (!found) return false;
        }

        return true;
    }

    function getCategoriesCount() external view onlyCoreContract returns (uint256) {
        return categories.length;
    }

    // --- INTERNAL FUNCTIONS ---

    function _hasExtensionKey(uint256 opinionId, string calldata key) internal view returns (bool) {
        string[] storage keys = opinionExtensionKeys[opinionId];
        bytes32 keyHash = keccak256(bytes(key));

        for (uint256 i = 0; i < keys.length; i++) {
            if (keccak256(bytes(keys[i])) == keyHash) {
                return true;
            }
        }
        return false;
    }

    // --- UUPS UPGRADE AUTHORIZATION ---
    function _authorizeUpgrade(address newImplementation) internal override onlyRole(ADMIN_ROLE) {}

    // --- ADMIN TRANSFER ---
    function transferFullAdmin(address newAdmin) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newAdmin != address(0), "Invalid address");
        _grantRole(DEFAULT_ADMIN_ROLE, newAdmin);
        _grantRole(ADMIN_ROLE, newAdmin);
        _revokeRole(ADMIN_ROLE, msg.sender);
        _revokeRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }
}

contracts/active/PoolManager.sol.content:
// PoolManager.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IPoolManager.sol";
import "./interfaces/IOpinionCore.sol";
import "./interfaces/IFeeManager.sol";
import "./interfaces/IOpinionMarketEvents.sol";
import "./interfaces/IOpinionMarketErrors.sol";
import "./structs/PoolStructs.sol";
import "./libraries/PoolLibrary.sol";
import "./libraries/ValidationLibrary.sol";

/**
 * @title PoolManager
 * @dev Manages pools for collective funding of answer changes
 */
contract PoolManager is
    Initializable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable,
    IPoolManager,
    IOpinionMarketEvents,
    IOpinionMarketErrors
{
    using SafeERC20 for IERC20;

    // --- STATE VARIABLES ---
    // Core references
    IOpinionCore public opinionCore;
    IFeeManager public feeManager;
    IERC20 public usdcToken;
    address public treasury;

    // Pool configuration
    uint96 public poolCreationFee;
    uint96 public poolContributionFee;
    uint32 public minPoolDuration;
    uint32 public maxPoolDuration;

    // Constants
    uint256 public constant MAX_POOL_NAME_LENGTH = 30;
    uint256 public constant MAX_IPFS_HASH_LENGTH = 64;

    // Pool storage
    uint256 public poolCount;
    mapping(uint256 => PoolStructs.PoolInfo) public pools;
    mapping(uint256 => PoolStructs.PoolContribution[])
        private poolContributions;
    mapping(uint256 => mapping(address => uint96))
        public poolContributionAmounts;
    mapping(uint256 => address[]) public poolContributors;
    mapping(uint256 => uint256[]) public opinionPools;
    mapping(address => uint256[]) public userPools;

    // --- ROLES ---
    // --- ROLES ---
    /**
     * @dev Administrative role for the pool management system
     * Accounts with this role can:
     * - Configure pool creation and contribution fees
     * - Set minimum/maximum amounts for pool contributions
     * - Update global pool parameters and thresholds
     * - Configure time-based settings (deadlines, extension periods)
     * - Enable or disable specific pool types
     * - Define pool validation rules
     * - Grant or revoke other roles in this contract
     * - Update contract integrations and dependencies
     */
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    /**
     * @dev Moderation role for pool operations
     * Accounts with this role can:
     * - Extend pool deadlines in exceptional cases
     * - Cancel problematic or invalid pools
     * - Force finalize pools under specific conditions
     * - Moderate pool metadata and descriptions
     * - Handle dispute resolution for pools
     * - Cannot modify system parameters (requires ADMIN_ROLE)
     * - Typically assigned to trusted community moderators or governance participants
     */
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initializes the pool manager
     */
    function initialize(
        address _opinionCore,
        address _feeManager,
        address _usdcToken,
        address _treasury,
        address _admin
    ) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();

        // Set contract references (opinionCore can be zero, set via setOpinionCore later)
        ValidationLibrary.validateAddress(_feeManager);
        ValidationLibrary.validateAddress(_usdcToken);
        ValidationLibrary.validateAddress(_treasury);
        ValidationLibrary.validateAddress(_admin);

        // OpinionCore can be set later via setOpinionCore() for circular dependency resolution
        if (_opinionCore != address(0)) {
            opinionCore = IOpinionCore(_opinionCore);
        }
        feeManager = IFeeManager(_feeManager);
        usdcToken = IERC20(_usdcToken);
        treasury = _treasury;

        // Set initial configuration
        poolCreationFee = 5 * 10 ** 6; // 5 USDC
        poolContributionFee = 0; // Free to contribute to pools
        minPoolDuration = 1 days;
        maxPoolDuration = 60 days;

        // Setup access control
        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);
        _grantRole(MODERATOR_ROLE, _admin);
    }

    /**
     * @dev Creates a new pool to collectively fund an answer change
     */
    function createPool(
        uint256 opinionId,
        string calldata proposedAnswer,
        uint256 deadline,
        uint256 initialContribution,
        string calldata name,
        string calldata ipfsHash
    ) external override nonReentrant {
        // Get current answer
        OpinionStructs.Opinion memory opinion = opinionCore.getOpinionDetails(
            opinionId
        );

        // Check NextPrice >= 100 USDC requirement for pool creation
        uint256 nextPrice = opinionCore.getNextPrice(opinionId);
        if (nextPrice < 100_000_000) { // 100 USDC (6 decimals)
            revert PoolNextPriceTooLow(nextPrice, 100_000_000);
        }

        // Validate parameters
        PoolStructs.PoolCreationParams memory params = PoolStructs
            .PoolCreationParams({
                opinionId: opinionId,
                proposedAnswer: proposedAnswer,
                deadline: uint32(deadline),
                initialContribution: uint96(initialContribution),
                name: name,
                ipfsHash: ipfsHash
            });

        PoolLibrary.validatePoolCreationParams(
            params,
            minPoolDuration,
            maxPoolDuration,
            1_000_000, // Minimum 1 USDC
            block.timestamp,
            MAX_POOL_NAME_LENGTH,
            50, // Max answer length from opinion core
            MAX_POOL_NAME_LENGTH,
            MAX_IPFS_HASH_LENGTH,
            opinion.currentAnswer
        );

        // Calculate total required amount
        uint96 totalRequired = poolCreationFee + uint96(initialContribution);

        // Check allowance
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < totalRequired)
            revert InsufficientAllowance(totalRequired, allowance);

        // Create the pool and get pool ID
        uint256 poolId = _createPoolRecord(
            opinionId,
            proposedAnswer,
            deadline,
            initialContribution,
            name,
            ipfsHash
        );

        // Handle funds transfer
        usdcToken.safeTransferFrom(msg.sender, address(this), totalRequired);

        // Send creation fee directly to treasury (full treasury model)
        usdcToken.safeTransfer(treasury, poolCreationFee);

        // Emit creation event
        emit PoolCreated(
            poolId,
            opinionId,
            proposedAnswer,
            msg.sender,
            initialContribution,
            deadline,
            name,
            block.timestamp
        );
    }

    /**
     * @dev Allows users to contribute to an existing pool
     */
    function contributeToPool(
        uint256 poolId,
        uint256 amount
    ) external override nonReentrant {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Validate pool is active
        if (pool.status != PoolStructs.PoolStatus.Active)
            revert PoolNotActive(poolId, uint8(pool.status));

        // Check deadline
        if (block.timestamp > pool.deadline)
            revert PoolDeadlinePassed(poolId, pool.deadline);

        // Use dynamic NextPrice as target (real-time)
        uint256 opinionId = pool.opinionId;
        uint96 targetPrice = uint96(opinionCore.getNextPrice(opinionId)); //  DYNAMIC: Always current NextPrice

        // Check if pool already has enough funds
        if (pool.totalAmount >= targetPrice) revert PoolAlreadyFunded(poolId);

        // Calculate maximum allowed contribution
        uint96 maxAllowed = targetPrice - pool.totalAmount;

        // Adjust contribution if needed
        uint96 actualAmount = amount > maxAllowed ? maxAllowed : uint96(amount);

        // Ensure non-zero contribution
        if (actualAmount == 0) revert PoolContributionTooLow(actualAmount, 1);

        // No contribution fee anymore - just transfer the contribution amount
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < actualAmount)
            revert InsufficientAllowance(actualAmount, allowance);

        // Update pool state
        _updatePoolForContribution(poolId, actualAmount);

        // Transfer funds (no fee)
        usdcToken.safeTransferFrom(msg.sender, address(this), actualAmount);

        // Emit contribution event
        emit PoolContribution(
            poolId,
            opinionId,
            msg.sender,
            actualAmount,
            pool.totalAmount,
            block.timestamp
        );

        // Check if pool is ready to execute
        _checkAndExecutePoolIfReady(poolId);
    }

    /**
     * @dev Complete a pool by contributing the exact remaining amount
     * @param poolId Pool ID to complete
     */
    function completePool(uint256 poolId) external nonReentrant {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Validate pool is active
        if (pool.status != PoolStructs.PoolStatus.Active)
            revert PoolNotActive(poolId, uint8(pool.status));

        // Check deadline
        if (block.timestamp > pool.deadline)
            revert PoolDeadlinePassed(poolId, pool.deadline);

        // Calculate exact remaining amount using current NextPrice
        uint96 targetPrice = uint96(opinionCore.getNextPrice(pool.opinionId));
        if (pool.totalAmount >= targetPrice) revert PoolAlreadyFunded(poolId);

        uint96 remainingAmount = targetPrice - pool.totalAmount;

        //  FIX: If remaining amount is tiny (< 0.01 USDC), allow free completion
        // This handles precision issues from bonding curve calculations
        if (remainingAmount > 0 && remainingAmount < 10000) { // < 0.01 USDC
            // Micro-amount completion - no additional payment needed
            pool.totalAmount = targetPrice; // Set to exact target

            emit PoolContribution(
                poolId,
                pool.opinionId,
                msg.sender,
                remainingAmount, // Show the micro-amount as contributed
                pool.totalAmount,
                block.timestamp
            );

            // Pool should be ready to execute now
            _checkAndExecutePoolIfReady(poolId);
            return;
        }

        // Ensure there's actually a meaningful amount to contribute
        if (remainingAmount == 0) revert PoolAlreadyFunded(poolId);

        // No contribution fee anymore - just transfer the remaining amount
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < remainingAmount)
            revert InsufficientAllowance(remainingAmount, allowance);

        // Update pool state
        _updatePoolForContribution(poolId, remainingAmount);

        // Transfer funds (no fee)
        usdcToken.safeTransferFrom(msg.sender, address(this), remainingAmount);

        // Emit contribution event
        emit PoolContribution(
            poolId,
            pool.opinionId,
            msg.sender,
            remainingAmount,
            pool.totalAmount,
            block.timestamp
        );

        // Pool should be ready to execute now
        _checkAndExecutePoolIfReady(poolId);
    }

    /**
     * @dev Allows contributor to withdraw from an expired pool
     */
    function withdrawFromExpiredPool(
        uint256 poolId
    ) external override nonReentrant {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Check if already expired or needs to be marked
        bool isExpired = pool.status == PoolStructs.PoolStatus.Expired;
        if (!isExpired) {
            // If not already marked, check if it should be
            isExpired = block.timestamp > pool.deadline;

            if (isExpired) {
                // Update status if expired
                pool.status = PoolStructs.PoolStatus.Expired;

                // Emit expiry event
                emit PoolExpired(
                    poolId,
                    pool.opinionId,
                    pool.totalAmount,
                    poolContributors[poolId].length,
                    block.timestamp
                );
            }
        }

        // Only allow withdrawals from expired pools
        if (!isExpired) revert PoolNotExpired(poolId, pool.deadline);

        // Check user contribution
        uint96 userContribution = poolContributionAmounts[poolId][msg.sender];
        if (userContribution == 0)
            revert PoolNoContribution(poolId, msg.sender);

        // Reset contribution before transfer
        poolContributionAmounts[poolId][msg.sender] = 0;

        // Transfer funds back to contributor
        usdcToken.safeTransfer(msg.sender, userContribution);

        // Emit refund event
        emit PoolRefund(poolId, msg.sender, userContribution, block.timestamp);
    }

    /**
     * @dev Extends the deadline of a pool
     */
    function extendPoolDeadline(
        uint256 poolId,
        uint256 newDeadline
    ) external override nonReentrant {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Ensure pool can be extended
        bool canExtend = pool.status == PoolStructs.PoolStatus.Active ||
            (pool.status == PoolStructs.PoolStatus.Expired &&
                block.timestamp <= pool.deadline + 7 days);

        if (!canExtend) revert("Pool cannot be extended");

        // Validate new deadline
        if (newDeadline <= pool.deadline) revert("New deadline must be later");

        if (newDeadline > block.timestamp + maxPoolDuration)
            revert PoolDeadlineTooLong(uint256(newDeadline), maxPoolDuration);

        // Update pool deadline and status
        pool.deadline = uint32(newDeadline);
        pool.status = PoolStructs.PoolStatus.Extended;

        // Emit deadline extension event
        emit PoolExtended(
            poolId,
            pool.opinionId,
            msg.sender,
            newDeadline,
            block.timestamp
        );
    }

    /**
     * @dev Checks if a pool has expired and updates its status
     */
    function checkPoolExpiry(uint256 poolId) external override returns (bool) {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Only check active pools
        if (pool.status != PoolStructs.PoolStatus.Active) {
            return pool.status == PoolStructs.PoolStatus.Expired;
        }

        // Check if deadline has passed
        if (block.timestamp > pool.deadline) {
            pool.status = PoolStructs.PoolStatus.Expired;

            // Emit expiry event
            emit PoolExpired(
                poolId,
                pool.opinionId,
                pool.totalAmount,
                poolContributors[poolId].length,
                block.timestamp
            );

            return true;
        }

        return false;
    }

    /**
     * @dev Distributes rewards when a pool-owned answer is purchased
     */
    function distributePoolRewards(
        uint256 opinionId,
        uint256 purchasePrice,
        address buyer
    ) external override {
        // Only allow calls from the Opinion Core contract
        require(msg.sender == address(opinionCore), "Unauthorized caller");

        // Find the pool that owns this answer
        uint256[] memory poolsForOpinion = opinionPools[opinionId];
        uint256 ownerPoolId;
        bool foundPool = false;

        for (uint256 i = 0; i < poolsForOpinion.length; i++) {
            uint256 poolId = poolsForOpinion[i];
            PoolStructs.PoolInfo storage pool = pools[poolId];

            // Check if this pool owns the answer (is executed and has matching answer)
            if (pool.status == PoolStructs.PoolStatus.Executed) {
                OpinionStructs.Opinion memory opinion = opinionCore
                    .getOpinionDetails(opinionId);
                if (
                    keccak256(bytes(pool.proposedAnswer)) ==
                    keccak256(bytes(opinion.currentAnswer))
                ) {
                    ownerPoolId = poolId;
                    foundPool = true;
                    break;
                }
            }
        }

        if (!foundPool) return;

        // Calculate reward amount (after platform and creator fees)
        (uint96 platformFee, uint96 creatorFee, ) = feeManager
            .calculateFeeDistribution(purchasePrice);
        uint96 rewardAmount = uint96(purchasePrice) - platformFee - creatorFee;

        // Get pool contributors and distribute rewards
        address[] memory contributors = poolContributors[ownerPoolId];
        uint96 totalContributed = pools[ownerPoolId].totalAmount;

        for (uint256 i = 0; i < contributors.length; i++) {
            address contributor = contributors[i];
            uint96 contribution = poolContributionAmounts[ownerPoolId][
                contributor
            ];

            if (contribution > 0) {
                // Calculate contributor's share
                uint256 sharePercent = (uint256(contribution) * 100) /
                    totalContributed;
                uint96 reward = uint96(
                    (uint256(rewardAmount) * sharePercent) / 100
                );

                // Accumulate reward
                feeManager.accumulateFee(contributor, reward);

                // Emit reward event
                emit PoolRewardDistributed(
                    ownerPoolId,
                    opinionId,
                    contributor,
                    contribution,
                    sharePercent,
                    reward,
                    block.timestamp
                );
            }
        }
    }

    /**
     * @dev Executes a pool if ready (reached target price)
     */
    function executePoolIfReady(
        uint256 poolId,
        uint256 opinionId
    ) external override {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        // Only allow calls from within contract or Opinion Core
        require(
            msg.sender == address(this) || msg.sender == address(opinionCore),
            "Unauthorized caller"
        );

        _checkAndExecutePoolIfReady(poolId);
    }

    /**
     * @dev Returns detailed information about a pool
     */
    function getPoolDetails(
        uint256 poolId
    )
        external
        view
        override
        returns (
            PoolStructs.PoolInfo memory info,
            uint256 currentPrice,
            uint256 remainingAmount,
            uint256 timeRemaining
        )
    {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        info = pools[poolId];

        // Use dynamic NextPrice as target (real-time)
        currentPrice = opinionCore.getNextPrice(info.opinionId); //  DYNAMIC: Current NextPrice

        // Calculate remaining amount needed
        if (info.totalAmount >= currentPrice) {
            remainingAmount = 0;
        } else {
            remainingAmount = currentPrice - info.totalAmount;
        }

        // Calculate time remaining
        if (block.timestamp >= info.deadline) {
            timeRemaining = 0;
        } else {
            timeRemaining = info.deadline - block.timestamp;
        }
    }

    /**
     * @dev Returns all contributor addresses for a pool
     */
    function getPoolContributors(
        uint256 poolId
    ) external view override returns (address[] memory) {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);
        return poolContributors[poolId];
    }

    /**
     * @dev Returns all pools for a specific opinion
     */
    function getOpinionPools(
        uint256 opinionId
    ) external view override returns (uint256[] memory) {
        return opinionPools[opinionId];
    }

    /**
     * @dev Returns reward information for a pool
     */
    function getPoolRewardInfo(
        uint256 poolId
    )
        external
        view
        override
        returns (
            address[] memory contributors,
            uint96[] memory amounts,
            uint96 totalAmount
        )
    {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        contributors = poolContributors[poolId];
        amounts = new uint96[](contributors.length);

        for (uint256 i = 0; i < contributors.length; i++) {
            amounts[i] = poolContributionAmounts[poolId][contributors[i]];
        }

        totalAmount = pools[poolId].totalAmount;
    }

    // --- INTERNAL FUNCTIONS ---

    /**
     * @dev Creates a new pool record
     */
    function _createPoolRecord(
        uint256 opinionId,
        string memory proposedAnswer,
        uint256 deadline,
        uint256 initialContribution,
        string memory name,
        string memory ipfsHash
    ) internal returns (uint256) {
        uint256 poolId = poolCount++;
        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Initialize pool
        pool.id = poolId;
        pool.opinionId = opinionId;
        pool.proposedAnswer = proposedAnswer;
        pool.totalAmount = uint96(initialContribution);
        pool.targetPrice = uint96(opinionCore.getNextPrice(opinionId)); //  INITIAL: Store initial NextPrice (for reference only)
        pool.deadline = uint32(deadline);
        pool.creator = msg.sender;
        pool.status = PoolStructs.PoolStatus.Active;
        pool.name = name;
        pool.ipfsHash = ipfsHash;

        // Track initial contribution
        poolContributions[poolId].push(
            PoolStructs.PoolContribution({
                contributor: msg.sender,
                amount: uint96(initialContribution),
                timestamp: uint32(block.timestamp)
            })
        );

        poolContributionAmounts[poolId][msg.sender] = uint96(
            initialContribution
        );
        poolContributors[poolId].push(msg.sender);

        // Update mappings
        opinionPools[opinionId].push(poolId);
        userPools[msg.sender].push(poolId);

        return poolId;
    }

    /**
     * @dev Updates pool state for a contribution
     */
    function _updatePoolForContribution(
        uint256 poolId,
        uint96 amount
    ) internal {
        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Update pool state
        if (poolContributionAmounts[poolId][msg.sender] == 0) {
            // First contribution from this user
            poolContributors[poolId].push(msg.sender);
            userPools[msg.sender].push(poolId);
        }

        // Record contribution
        poolContributions[poolId].push(
            PoolStructs.PoolContribution({
                contributor: msg.sender,
                amount: amount,
                timestamp: uint32(block.timestamp)
            })
        );

        poolContributionAmounts[poolId][msg.sender] += amount;
        pool.totalAmount += amount;
    }

    /**
     * @dev Checks if pool has reached target price and executes if so
     *  FIX: Added completion tolerance to handle precision issues
     */
    function _checkAndExecutePoolIfReady(uint256 poolId) internal {
        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Only execute active pools
        if (pool.status != PoolStructs.PoolStatus.Active) {
            return;
        }

        uint256 opinionId = pool.opinionId;
        uint96 currentNextPrice = uint96(opinionCore.getNextPrice(opinionId)); //  DYNAMIC: Current NextPrice
        uint96 currentAmount = pool.totalAmount;

        //  DYNAMIC: Execute if we have enough funds for CURRENT NextPrice
        // Allow 0.01% tolerance for precision issues
        uint96 tolerance = currentNextPrice / 10000; // 0.01% tolerance
        if (tolerance < 1) tolerance = 1; // Minimum 1 wei tolerance

        // Execute if we're within tolerance of CURRENT NextPrice
        if (currentAmount >= currentNextPrice - tolerance) {
            _executePool(poolId, opinionId, currentNextPrice);
        }
    }

    /**
     * @dev Executes a pool when it has reached the target price
     */
    function _executePool(
        uint256 poolId,
        uint256 opinionId,
        uint96 targetPrice
    ) internal {
        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Double-check status
        if (pool.status != PoolStructs.PoolStatus.Active)
            revert PoolNotActive(poolId, uint8(pool.status));

        // Double-check funds against CURRENT NextPrice (dynamic)
        uint96 currentNextPrice = uint96(opinionCore.getNextPrice(opinionId));
        if (pool.totalAmount < currentNextPrice)
            revert PoolInsufficientFunds(pool.totalAmount, currentNextPrice);

        // Get current answer owner before execution
        OpinionStructs.Opinion memory opinion = opinionCore.getOpinionDetails(
            opinionId
        );
        address currentOwner = opinion.currentAnswerOwner;

        // Update opinion through core contract using CURRENT NextPrice
        opinionCore.updateOpinionOnPoolExecution(
            opinionId,
            pool.proposedAnswer,
            address(this), // Pool address as owner
            targetPrice // This is now the current NextPrice, not fixed
        );

        // Update pool status
        pool.status = PoolStructs.PoolStatus.Executed;

        // Emit execution event
        emit PoolExecuted(
            poolId,
            opinionId,
            pool.proposedAnswer,
            targetPrice,
            block.timestamp
        );
    }

    // --- ADMIN FUNCTIONS ---

    /**
     * @dev Sets the OpinionCore contract address. Essential for post-deployment linking.
     */
    function setOpinionCore(address _opinionCore) external onlyRole(ADMIN_ROLE) {
        require(_opinionCore != address(0), "Zero address");
        opinionCore = IOpinionCore(_opinionCore);
        emit ParameterUpdated(
            100,
            0,
            uint256(uint160(_opinionCore)),
            msg.sender,
            block.timestamp
        );
    }

    /**
     * @dev Sets pool creation fee
     */
    function setPoolCreationFee(uint96 newFee) external onlyRole(ADMIN_ROLE) {
        poolCreationFee = newFee;
        emit ParameterUpdated(
            8,
            poolCreationFee,
            newFee,
            msg.sender,
            block.timestamp
        );
    }

    /**
     * @dev Sets pool contribution fee
     */
    function setPoolContributionFee(
        uint96 newFee
    ) external onlyRole(ADMIN_ROLE) {
        poolContributionFee = newFee;
        emit ParameterUpdated(
            9,
            poolContributionFee,
            newFee,
            msg.sender,
            block.timestamp
        );
    }

    /**
     * @dev Sets minimum pool duration
     */
    function setMinPoolDuration(
        uint32 newDuration
    ) external onlyRole(ADMIN_ROLE) {
        minPoolDuration = newDuration;
        emit ParameterUpdated(
            10,
            minPoolDuration,
            newDuration,
            msg.sender,
            block.timestamp
        );
    }

    /**
     * @dev Sets maximum pool duration
     */
    function setMaxPoolDuration(
        uint32 newDuration
    ) external onlyRole(ADMIN_ROLE) {
        maxPoolDuration = newDuration;
        emit ParameterUpdated(
            11,
            maxPoolDuration,
            newDuration,
            msg.sender,
            block.timestamp
        );
    }

    // --- EARLY WITHDRAWAL FEATURE ---

    /**
     * @dev Allows contributor to withdraw early from pool with 10% penalty
     * OBLIGATOIRE: Cette signature exacte
     * @param poolId Pool ID to withdraw from
     */
    function withdrawFromPoolEarly(uint256 poolId) external nonReentrant {
        // === CHECKS === (OBLIGATOIRE: Cet ordre exact de validations)

        // 1. Pool ID validation FIRST
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // 2. Pool status validation  
        if (pool.status != PoolStructs.PoolStatus.Active) revert PoolNotActive(poolId, uint8(pool.status));

        // 3. Deadline validation
        if (block.timestamp >= pool.deadline) revert PoolDeadlinePassed(poolId, pool.deadline);

        // 4. User contribution validation
        uint96 userContribution = poolContributionAmounts[poolId][msg.sender];
        if (userContribution == 0) revert PoolNoContribution(poolId, msg.sender);

        // === EFFECTS === (OBLIGATOIRE: State updates BEFORE transfers for reentrancy protection)

        // 5. Calculate penalty and splits (SECURITY FIX: Enhanced penalty, 100% treasury)
        uint96 penalty = userContribution * 20 / 100;           //  20% total penalty (enhanced deterrent)
        uint96 userAmount = userContribution - penalty;         // 80% vers user
        //  SECURITY: 100% penalty vers treasury (ZERO creator incentive)

        // 6. Update state (OBLIGATOIRE: Reset user contribution BEFORE transfers)
        poolContributionAmounts[poolId][msg.sender] = 0;

        // 7. Update pool total amount
        pool.totalAmount -= userContribution;

        // === INTERACTIONS === (SECURITY FIX: Safe transfer pattern)

        // 8. Transfer to user (80%) - Enhanced penalty applied
        usdcToken.safeTransfer(msg.sender, userAmount);

        // 9. Transfer to treasury (100% of penalty) -  SECURITY FIX: NO creator share
        if (penalty > 0) {
            usdcToken.safeTransfer(treasury, penalty);
        }

        //  REMOVED: All creator penalty sharing logic eliminated
        //  SECURITY: Zero incentive for gaming attacks

        // 10. Emit event (Updated with new penalty structure)
        emit PoolEarlyWithdrawal(poolId, msg.sender, userContribution, penalty, userAmount, block.timestamp);
    }

    // --- HELPER VIEW FUNCTIONS (CREATIVE FREEDOM ZONE) ---

    /**
     * @dev Preview early withdrawal amounts for a user
     * @param poolId Pool ID
     * @param user User address
     * @return userContribution Current user contribution
     * @return penalty Total penalty amount (10%)
     * @return userWillReceive Amount user will receive (90%)
     * @return canWithdraw Whether withdrawal is currently possible
     */
    function getEarlyWithdrawalPreview(uint256 poolId, address user) external view returns (
        uint96 userContribution,
        uint96 penalty,
        uint96 userWillReceive,
        bool canWithdraw
    ) {
        // Validate pool exists
        if (poolId >= poolCount) {
            return (0, 0, 0, false);
        }

        PoolStructs.PoolInfo storage pool = pools[poolId];
        userContribution = poolContributionAmounts[poolId][user];

        // Calculate amounts if user has contribution
        if (userContribution > 0) {
            penalty = userContribution * 20 / 100;           //  20% total penalty (security fix)
            userWillReceive = userContribution - penalty;    // 80% to user

            // Check if withdrawal is possible
            canWithdraw = pool.status == PoolStructs.PoolStatus.Active && 
                         block.timestamp < pool.deadline;
        } else {
            penalty = 0;
            userWillReceive = 0;
            canWithdraw = false;
        }
    }

    /**
     * @dev Get detailed penalty breakdown for early withdrawal (SECURITY FIX)
     * @param poolId Pool ID  
     * @param user User address
     * @return userContribution Current user contribution
     * @return totalPenalty Total penalty (20% - enhanced)
     * @return treasuryReceives Treasury receives (100% of penalty)
     * @return userReceives Amount user receives (80%)
     */
    function getEarlyWithdrawalBreakdown(uint256 poolId, address user) external view returns (
        uint96 userContribution,
        uint96 totalPenalty,
        uint96 treasuryReceives,
        uint96 userReceives
    ) {
        if (poolId >= poolCount) {
            return (0, 0, 0, 0);
        }

        userContribution = poolContributionAmounts[poolId][user];

        if (userContribution > 0) {
            totalPenalty = userContribution * 20 / 100;        //  20% total penalty (security fix)
            treasuryReceives = totalPenalty;                   //  100% vers treasury (no creator share)
            userReceives = userContribution - totalPenalty;    // 80% vers user
        }
    }

    /**
     * @dev Check if early withdrawal is possible for a user
     * @param poolId Pool ID
     * @param user User address
     * @return possible Whether early withdrawal is possible
     * @return reason Reason if not possible (0=possible, 1=invalid_pool, 2=not_active, 3=deadline_passed, 4=no_contribution)
     */
    function canWithdrawEarly(uint256 poolId, address user) external view returns (bool possible, uint8 reason) {
        // Check pool exists
        if (poolId >= poolCount) {
            return (false, 1); // invalid_pool
        }

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Check pool status
        if (pool.status != PoolStructs.PoolStatus.Active) {
            return (false, 2); // not_active
        }

        // Check deadline
        if (block.timestamp >= pool.deadline) {
            return (false, 3); // deadline_passed
        }

        // Check user contribution
        if (poolContributionAmounts[poolId][user] == 0) {
            return (false, 4); // no_contribution
        }

        return (true, 0); // possible
    }

    /**
     * @dev Authorize contract upgrades - only admin can upgrade
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyRole(ADMIN_ROLE) {}

    // --- ADMIN TRANSFER ---
    function transferFullAdmin(address newAdmin) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newAdmin != address(0), "Invalid address");
        _grantRole(DEFAULT_ADMIN_ROLE, newAdmin);
        _grantRole(ADMIN_ROLE, newAdmin);
        _grantRole(MODERATOR_ROLE, newAdmin);
        _revokeRole(MODERATOR_ROLE, msg.sender);
        _revokeRole(ADMIN_ROLE, msg.sender);
        _revokeRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }
}

