    ) external override nonReentrant whenNotPaused {
        OpinionTradingLib.TradingContext memory ctx = OpinionTradingLib.TradingContext({
            sender: msg.sender,
            usdcToken: usdcToken,
            feeManager: feeManager,
            poolManager: poolManager,
            treasury: treasury,
            minimumPrice: minimumPrice,
