        emit OpinionAddressExtensionSet(opinionId, key, value);
    }

    function getOpinionExtensions(uint256 opinionId) external view returns (
        string[] memory keys, string[] memory stringValues, uint256[] memory numberValues, 
        bool[] memory boolValues, address[] memory addressValues
    ) {
        keys = opinionExtensionKeys[opinionId];
        uint256 length = keys.length;
        stringValues = new string[](length);
        numberValues = new uint256[](length);
        boolValues = new bool[](length);
        addressValues = new address[](length);
        for (uint256 i = 0; i < length; i++) {
            string memory key = keys[i];
            stringValues[i] = opinionStringExtensions[opinionId][key];
            numberValues[i] = opinionNumberExtensions[opinionId][key];
            boolValues[i] = opinionBoolExtensions[opinionId][key];
            addressValues[i] = opinionAddressExtensions[opinionId][key];
        }
    }

    function getOpinionStringExtension(uint256 opinionId, string calldata key) external view returns (string memory) {
        return opinionStringExtensions[opinionId][key];
    }

    function getOpinionNumberExtension(uint256 opinionId, string calldata key) external view returns (uint256) {
        return opinionNumberExtensions[opinionId][key];
    }

    function getOpinionBoolExtension(uint256 opinionId, string calldata key) external view returns (bool) {
        return opinionBoolExtensions[opinionId][key];
    }

    function getOpinionAddressExtension(uint256 opinionId, string calldata key) external view returns (address) {
        return opinionAddressExtensions[opinionId][key];
    }

    function hasOpinionExtension(uint256 opinionId, string calldata key) external view returns (bool) {
        string[] storage keys = opinionExtensionKeys[opinionId];
        for (uint256 i = 0; i < keys.length; i++) {
            if (keccak256(bytes(keys[i])) == keccak256(bytes(key))) return true;
        }
        return false;
    }

    function getOpinionExtensionCount(uint256 opinionId) external view returns (uint256) {
        return opinionExtensionKeys[opinionId].length;
    }

    // 
    // SOLO TIMELOCK UPGRADE SYSTEM (CRIT-003 FIX)
    // 

    /**
     * @dev Schedules a contract upgrade with 72-hour timelock (Solo Developer Security)
     * @param newImplementation Address of the new implementation contract
     * @param description Description of the upgrade for audit trail
     * @return actionId The action ID for later execution
     */
    function scheduleContractUpgrade(
        address newImplementation,
        string calldata description
    ) external onlyRole(ADMIN_ROLE) returns (bytes32 actionId) {
        actionId = TimelockAdminLib.scheduleContractUpgrade(
            newImplementation,
            description,
            msg.sender
        );

        // Still need to call the actual timelock scheduling
        bytes32 timelockActionId = scheduleUpgrade(newImplementation, description);

        emit AdminAction(5, msg.sender, timelockActionId, uint256(uint160(newImplementation)));

        return timelockActionId;
    }

    /**
     * @dev Executes a scheduled upgrade after 72-hour timelock expires
     * @param actionId The action ID returned from scheduleContractUpgrade
     */
    function executeScheduledUpgrade(bytes32 actionId) 
        external 
        onlyRole(ADMIN_ROLE) 
        onlyAfterTimelock(actionId) 
    {
        TimelockAdminLib.executeScheduledUpgrade(actionId, msg.sender);

        emit AdminAction(6, msg.sender, actionId, 0);
    }

    /**
     * @dev Schedules admin parameter changes with 24-hour timelock (Solo Developer Security)
     * Example usage for setMinimumPrice, setMaxTradesPerBlock, etc.
     * @param functionSelector The function selector (e.g., this.setMinimumPrice.selector)
     * @param params Encoded function parameters
     * @param description Description of the change
     * @return actionId The action ID for later execution
     */
    function scheduleAdminParameterChange(
        bytes4 functionSelector,
        bytes calldata params,
        string calldata description
    ) external onlyRole(ADMIN_ROLE) returns (bytes32 actionId) {
        actionId = TimelockAdminLib.scheduleAdminParameterChange(
            functionSelector,
            params,
            description,
            msg.sender
        );

        // Still need to call the actual timelock scheduling
        bytes32 timelockActionId = scheduleAdminAction(functionSelector, params, description);

        emit AdminAction(7, msg.sender, timelockActionId, uint256(uint32(functionSelector)));

        return timelockActionId;
    }

    /**
     * @dev Executes a scheduled admin parameter change after 24-hour timelock expires
     * @param actionId The action ID returned from scheduleAdminParameterChange
     */
    function executeScheduledParameterChange(bytes32 actionId)
        external
        onlyRole(ADMIN_ROLE)
        onlyAfterTimelock(actionId)
    {
        TimelockAdminLib.executeScheduledParameterChange(actionId, msg.sender);

        emit AdminAction(8, msg.sender, actionId, 0);
    }

    /**
     * @dev Cancels a scheduled action (upgrade or parameter change)
     * @param actionId The action ID to cancel
     * @param reason Reason for cancellation
     */
    function cancelTimelockAction(
        bytes32 actionId,
        string calldata reason
    ) external onlyRole(ADMIN_ROLE) {
        TimelockAdminLib.cancelTimelockAction(actionId, reason, msg.sender);

        super.cancelScheduledAction(actionId, reason);

        emit AdminAction(9, msg.sender, actionId, 0);
    }


    /**
     * @dev Authorize upgrade (required for UUPS proxy pattern)
     * NOW SECURED WITH 72-HOUR TIMELOCK - NO INSTANT UPGRADES
     * @param newImplementation Address of the new implementation
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyRole(ADMIN_ROLE) {
        // This function is now only called through the timelock system
        // Direct calls to upgradeToAndCall will fail without proper timelock
        // The actual authorization happens in executeScheduledUpgrade

        // Additional security: verify this is being called from a scheduled upgrade
        // This prevents bypassing the timelock through other upgrade mechanisms
    }
}

contracts/core/OpinionCoreNoMod.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IOpinionCore.sol";
import "./interfaces/IFeeManager.sol";
import "./interfaces/IPoolManager.sol";
import "./interfaces/IMonitoringManager.sol";
import "./interfaces/ISecurityManager.sol";
import "./interfaces/IOpinionMarketEvents.sol";
import "./interfaces/IOpinionMarketErrors.sol";
import "./structs/OpinionStructs.sol";
import "./libraries/ValidationLibrary.sol";
import "./libraries/PriceCalculator.sol";
import "./interfaces/IValidationErrors.sol";

/**
 * @title OpinionCoreSimplified
 * @dev Simplified core contract for managing opinions, answers, and related functionality
 * Security and monitoring features have been extracted to separate contracts
 */
contract OpinionCoreNoMod is
    Initializable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    IOpinionCore,
    IOpinionMarketEvents,
    IOpinionMarketErrors,
    IValidationErrors
{
    using SafeERC20 for IERC20;

    // --- ROLES ---
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");
    bytes32 public constant MARKET_CONTRACT_ROLE = keccak256("MARKET_CONTRACT_ROLE");
    bytes32 public constant POOL_MANAGER_ROLE = keccak256("POOL_MANAGER_ROLE");

    // --- CONSTANTS ---
    uint256 public constant MAX_QUESTION_LENGTH = 52;
    uint256 public constant MAX_ANSWER_LENGTH = 52;
    uint256 public constant MAX_LINK_LENGTH = 260;
    uint256 public constant MAX_IPFS_HASH_LENGTH = 68;
    uint256 public constant MAX_DESCRIPTION_LENGTH = 120;
    uint256 public constant MAX_CATEGORIES_PER_OPINION = 3;

    // --- INITIAL PRICE RANGE CONSTANTS ---
    uint96 public constant MIN_INITIAL_PRICE = 2_000_000;   // 2 USDC (6 decimals)
    uint96 public constant MAX_INITIAL_PRICE = 100_000_000; // 100 USDC (6 decimals)

    // --- STATE VARIABLES ---
    IERC20 public usdcToken;
    IFeeManager public feeManager;
    IPoolManager public poolManager;
    IMonitoringManager public monitoringManager;
    ISecurityManager public securityManager;

    address public treasury;
    address public pendingTreasury;
    uint256 public treasuryChangeTimestamp;
    uint256 public constant TREASURY_CHANGE_DELAY = 48 hours;

    bool public isPublicCreationEnabled;
    uint256 public nextOpinionId;

    // --- CATEGORIES STORAGE ---
    string[] public categories;

    // --- EXTENSION SLOTS STORAGE ---
    mapping(uint256 => mapping(string => string)) public opinionStringExtensions;
    mapping(uint256 => mapping(string => uint256)) public opinionNumberExtensions;  
    mapping(uint256 => mapping(string => bool)) public opinionBoolExtensions;
    mapping(uint256 => mapping(string => address)) public opinionAddressExtensions;
    mapping(uint256 => string[]) public opinionExtensionKeys;

    // Security and rate limiting
    uint256 public maxTradesPerBlock;
    mapping(address => uint256) private userLastBlock;
    mapping(address => uint256) private userTradesInBlock;
    mapping(address => mapping(uint256 => uint256)) private userLastTradeBlock;

    // Price calculation
    uint256 private nonce;
    mapping(uint256 => uint256) private priceMetadata;
    mapping(uint256 => uint256) private priceHistory;

    // Configurable parameters
    uint96 public minimumPrice;
    uint96 public questionCreationFee;
    uint96 public initialAnswerPrice;
    uint256 public absoluteMaxPriceChange;

    // Core data structures
    mapping(uint256 => OpinionStructs.Opinion) public opinions;
    mapping(uint256 => OpinionStructs.AnswerHistory[]) public answerHistory;

    // --- INITIALIZATION ---
    function initialize(
        address _usdcToken,
        address _opinionMarket,
        address _feeManager,
        address _poolManager,
        address _monitoringManager,
        address _securityManager,
        address _treasury
    ) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();
        __Pausable_init();

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(MODERATOR_ROLE, msg.sender);

        // Set external contracts
        if (
            _usdcToken == address(0) ||
            _opinionMarket == address(0) ||
            _feeManager == address(0) ||
            _poolManager == address(0) ||
            _treasury == address(0)
        ) revert ZeroAddressNotAllowed();

        usdcToken = IERC20(_usdcToken);
        feeManager = IFeeManager(_feeManager);
        poolManager = IPoolManager(_poolManager);
        treasury = _treasury;

        // Grant role to opinion market contract
        _grantRole(MARKET_CONTRACT_ROLE, _opinionMarket);

        // Set optional modules (can be zero initially)
        if (_monitoringManager != address(0)) {
            monitoringManager = IMonitoringManager(_monitoringManager);
        }
        if (_securityManager != address(0)) {
            securityManager = ISecurityManager(_securityManager);
        }

        // Initialize parameters
        nextOpinionId = 1;
        isPublicCreationEnabled = false;
        maxTradesPerBlock = 3;
        minimumPrice = 1_000_000; // 1 USDC (6 decimals)
        questionCreationFee = 5_000_000; // 5 USDC
        initialAnswerPrice = 2_000_000; // 2 USDC
        absoluteMaxPriceChange = 200; // 200%

        // Initialize default categories
        categories = ["Crypto", "Politics", "Science", "Technology", "Sports", 
                      "Entertainment", "Culture", "Web", "Social Media", "Other"];
    }

    // --- MODIFIERS ---
    modifier onlyMarketContract() {
        if (!hasRole(MARKET_CONTRACT_ROLE, msg.sender))
            revert AccessControlUnauthorizedAccount(msg.sender, MARKET_CONTRACT_ROLE);
        _;
    }

    modifier onlyPoolManager() {
        if (!hasRole(POOL_MANAGER_ROLE, msg.sender))
            revert AccessControlUnauthorizedAccount(msg.sender, POOL_MANAGER_ROLE);
        _;
    }

    // --- CORE OPINION FUNCTIONS ---

    function createOpinion(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) external override nonReentrant whenNotPaused {
        // Access control check
        if (!isPublicCreationEnabled && !hasRole(ADMIN_ROLE, msg.sender))
            revert UnauthorizedCreator();

        // Enhanced validation if SecurityManager is available
        if (address(securityManager) != address(0)) {
            try securityManager.validateCreateOpinionInputs(
                question, answer, description, initialPrice, opinionCategories, msg.sender
            ) {} catch {
                // Fallback to basic validation if security manager fails
                _basicValidation(question, answer, description, initialPrice, opinionCategories);
            }
        } else {
            _basicValidation(question, answer, description, initialPrice, opinionCategories);
        }

        // Check allowance for initialPrice
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < initialPrice)
            revert InsufficientAllowance(initialPrice, allowance);

        // Create opinion record
        uint256 opinionId = _createOpinionRecord(
            question, answer, description, "", "", initialPrice, opinionCategories
        );

        // Transfer payment to treasury
        usdcToken.safeTransferFrom(msg.sender, treasury, initialPrice);

        // Track with monitoring manager if available
        if (address(monitoringManager) != address(0)) {
            try monitoringManager.trackOpinionCreation(opinionId, msg.sender, initialPrice) {} catch {}
        }

        // Emit events
        emit OpinionAction(opinionId, 0, question, msg.sender, initialPrice);
        emit OpinionAction(opinionId, 1, answer, msg.sender, initialPrice);
    }

    function createOpinionWithExtras(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories,
        string calldata ipfsHash,
        string calldata link
    ) external override nonReentrant whenNotPaused {
        // Access control check
        if (!isPublicCreationEnabled && !hasRole(ADMIN_ROLE, msg.sender))
            revert UnauthorizedCreator();

        // Enhanced validation if SecurityManager is available
        if (address(securityManager) != address(0)) {
            try securityManager.validateCreateOpinionInputs(
                question, answer, description, initialPrice, opinionCategories, msg.sender
            ) {} catch {
                _basicValidation(question, answer, description, initialPrice, opinionCategories);
            }
        } else {
            _basicValidation(question, answer, description, initialPrice, opinionCategories);
        }

        // Validate IPFS hash and link
        _validateExtras(ipfsHash, link);

        // Check allowance
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < initialPrice)
            revert InsufficientAllowance(initialPrice, allowance);

        // Create opinion record
        uint256 opinionId = _createOpinionRecord(
            question, answer, description, ipfsHash, link, initialPrice, opinionCategories
        );

        // Transfer payment to treasury
        usdcToken.safeTransferFrom(msg.sender, treasury, initialPrice);

        // Track with monitoring manager if available
        if (address(monitoringManager) != address(0)) {
            try monitoringManager.trackOpinionCreation(opinionId, msg.sender, initialPrice) {} catch {}
        }

        // Emit events
        emit OpinionAction(opinionId, 0, question, msg.sender, initialPrice);
        emit OpinionAction(opinionId, 1, answer, msg.sender, initialPrice);
    }

    function submitAnswer(
        uint256 opinionId,
        string calldata answer,
        string calldata description,
        string calldata link
    ) external override nonReentrant whenNotPaused {
        _checkAndUpdateTradesInBlock();
        _checkTradeAllowed(opinionId);

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (!opinion.isActive) revert OpinionNotActive();
        if (opinion.currentAnswerOwner == msg.sender) revert SameOwner();

        // Basic validation
        _validateAnswerInput(answer, description);

        // Validate link (optional)
        bytes memory linkBytes = bytes(link);
        if (linkBytes.length > MAX_LINK_LENGTH) revert InvalidLinkLength();

        // Calculate price
        uint96 price = opinion.nextPrice > 0
            ? opinion.nextPrice
            : uint96(_calculateNextPrice(opinionId, opinion.lastPrice));

        // Check USDC allowance
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < price) revert InsufficientAllowance(price, allowance);

        // Security analysis if SecurityManager is available
        if (address(securityManager) != address(0)) {
            // Check MEV risk
            try securityManager.analyzeMevRisk(msg.sender, price, opinionId) {} catch {}

            // Analyze bot patterns
            // Note: We can't easily determine trade success here, so we skip bot analysis for now
        }

        // Calculate fees
        (uint96 platformFee, uint96 creatorFee, uint96 ownerAmount) = feeManager
            .calculateFeeDistribution(price);

        // Apply MEV penalty if needed
        (platformFee, ownerAmount) = feeManager.applyMEVPenalty(
            price, ownerAmount, msg.sender, opinionId
        );

        // Get addresses for fee distribution
        address creator = opinion.creator;
        address currentAnswerOwner = opinion.currentAnswerOwner;
        bool answerIsPoolOwned = currentAnswerOwner == address(poolManager);

        // Accumulate fees
        feeManager.accumulateFee(creator, creatorFee);

        if (answerIsPoolOwned) {
            poolManager.distributePoolRewards(opinionId, price, msg.sender);
        } else {
            feeManager.accumulateFee(currentAnswerOwner, ownerAmount);
        }

        // Record answer history
        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: description,
                owner: msg.sender,
                price: price,
                timestamp: uint32(block.timestamp)
            })
        );

        // Update opinion state
        uint96 oldPrice = opinion.lastPrice;
        opinion.currentAnswer = answer;
        opinion.currentAnswerDescription = description;
        opinion.currentAnswerOwner = msg.sender;
        opinion.link = link;
        opinion.lastPrice = price;
        opinion.totalVolume += price;
        opinion.nextPrice = uint96(_calculateNextPrice(opinionId, price));

        // Transfer tokens
        usdcToken.safeTransferFrom(msg.sender, address(this), price);

        // Track with monitoring manager if available
        if (address(monitoringManager) != address(0)) {
            try monitoringManager.trackTradingActivity(
                opinionId, msg.sender, price, oldPrice, opinion.nextPrice, platformFee
            ) {} catch {}
        }

        // Update security profiles if SecurityManager is available
        if (address(securityManager) != address(0)) {
            try securityManager.updateMevProfile(msg.sender, opinionId, price) {} catch {}
        }

        // Emit events
        emit FeesAction(opinionId, 0, currentAnswerOwner, price, platformFee, creatorFee, ownerAmount);
        emit OpinionAction(opinionId, 1, answer, msg.sender, price);
    }

    // --- QUESTION TRADING FUNCTIONS ---

    function listQuestionForSale(uint256 opinionId, uint256 price) external override nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (opinion.questionOwner != msg.sender) revert NotTheOwner(msg.sender, opinion.questionOwner);

        opinion.salePrice = uint96(price);
        emit QuestionSaleAction(opinionId, 0, msg.sender, address(0), price);
    }

    function buyQuestion(uint256 opinionId) external override nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();

        uint96 salePrice = opinion.salePrice;
        if (salePrice == 0) revert NotForSale(opinionId);

        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < salePrice) revert InsufficientAllowance(salePrice, allowance);

        address currentOwner = opinion.questionOwner;
        uint96 platformFee = uint96((salePrice * 10) / 100);
        uint96 sellerAmount = salePrice - platformFee;

        opinion.questionOwner = msg.sender;
        opinion.salePrice = 0;

        usdcToken.safeTransferFrom(msg.sender, address(this), salePrice);
        feeManager.accumulateFee(currentOwner, sellerAmount);

        emit QuestionSaleAction(opinionId, 1, currentOwner, msg.sender, salePrice);
    }

    function cancelQuestionSale(uint256 opinionId) external override nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (opinion.questionOwner != msg.sender) revert NotTheOwner(msg.sender, opinion.questionOwner);

        opinion.salePrice = 0;
        emit QuestionSaleAction(opinionId, 2, msg.sender, address(0), 0);
    }

    // --- MODERATION FUNCTIONS (REMOVED FOR SIZE OPTIMIZATION) ---
    // Can be added back in future upgrades

    function deactivateOpinion(uint256) external pure override {
        revert("Moderation not implemented");
    }

    function reactivateOpinion(uint256) external pure override {
        revert("Moderation not implemented");  
    }

    // --- POOL INTEGRATION ---

    function updateOpinionOnPoolExecution(
        uint256 opinionId,
        string calldata answer,
        address poolAddress,
        uint256 price
    ) external override onlyPoolManager {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (!opinion.isActive) revert OpinionNotActive();

        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: "",
                owner: poolAddress,
                price: uint96(price),
                timestamp: uint32(block.timestamp)
            })
        );

        opinion.currentAnswer = answer;
        opinion.currentAnswerDescription = "";
        opinion.currentAnswerOwner = poolAddress;
        opinion.lastPrice = uint96(price);
        opinion.totalVolume += uint96(price);
        opinion.nextPrice = uint96(_calculateNextPrice(opinionId, price));

        emit OpinionAction(opinionId, 1, answer, address(poolManager), price);
    }

    // --- VIEW FUNCTIONS ---

    function getAnswerHistory(uint256 opinionId) external view override returns (OpinionStructs.AnswerHistory[] memory) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return answerHistory[opinionId];
    }

    function getNextPrice(uint256 opinionId) external view override returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (!opinion.isActive) revert OpinionNotActive();

        if (opinion.nextPrice == 0) {
            return _estimateNextPrice(opinion.lastPrice);
        }
        return opinion.nextPrice;
    }

    function getOpinionDetails(uint256 opinionId) external view override returns (OpinionStructs.Opinion memory) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId];
    }

    function getTradeCount(uint256 opinionId) external view override returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return answerHistory[opinionId].length;
    }

    function getCreatorGain(uint256 opinionId) external view override returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];

        (, uint96 creatorFeePercent, ) = feeManager.calculateFeeDistribution(1_000_000);
        uint256 creatorFeeRate = creatorFeePercent / 10;
        return (opinion.totalVolume * creatorFeeRate) / 100;
    }

    function isPoolOwned(uint256 opinionId) external view override returns (bool) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId].currentAnswerOwner == address(poolManager);
    }

    // --- CATEGORIES FUNCTIONS ---

    function addCategoryToCategories(string calldata newCategory) external onlyRole(ADMIN_ROLE) {
        bytes32 newCategoryHash = keccak256(bytes(newCategory));
        uint256 length = categories.length;

        for (uint256 i = 0; i < length; i++) {
            if (keccak256(bytes(categories[i])) == newCategoryHash) {
                revert CategoryAlreadyExists();
            }
        }
        categories.push(newCategory);
    }

    function getAvailableCategories() external view returns (string[] memory) {
        return categories;
    }

    function getOpinionCategories(uint256 opinionId) external view returns (string[] memory) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId].categories;
    }

    function getCategoryCount() external view returns (uint256) {
        return categories.length;
    }

    // --- EXTENSION SLOTS FUNCTIONS ---

    function setOpinionStringExtension(uint256 opinionId, string calldata key, string calldata value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionStringExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionStringExtensionSet(opinionId, key, value);
    }

    function setOpinionNumberExtension(uint256 opinionId, string calldata key, uint256 value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionNumberExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionNumberExtensionSet(opinionId, key, value);
    }

    function setOpinionBoolExtension(uint256 opinionId, string calldata key, bool value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionBoolExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionBoolExtensionSet(opinionId, key, value);
    }

    function setOpinionAddressExtension(uint256 opinionId, string calldata key, address value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionAddressExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionAddressExtensionSet(opinionId, key, value);
    }

    function getOpinionExtensions(uint256 opinionId) external view returns (
        string[] memory keys,
        string[] memory stringValues,
        uint256[] memory numberValues,
        bool[] memory boolValues,
        address[] memory addressValues
    ) {
        keys = opinionExtensionKeys[opinionId];
        uint256 length = keys.length;

        stringValues = new string[](length);
        numberValues = new uint256[](length);
        boolValues = new bool[](length);
        addressValues = new address[](length);

        for (uint256 i = 0; i < length; i++) {
            string memory key = keys[i];
            stringValues[i] = opinionStringExtensions[opinionId][key];
            numberValues[i] = opinionNumberExtensions[opinionId][key];
            boolValues[i] = opinionBoolExtensions[opinionId][key];
            addressValues[i] = opinionAddressExtensions[opinionId][key];
        }

        return (keys, stringValues, numberValues, boolValues, addressValues);
    }

    function getOpinionStringExtension(uint256 opinionId, string calldata key) external view returns (string memory) {
        return opinionStringExtensions[opinionId][key];
    }

    function getOpinionNumberExtension(uint256 opinionId, string calldata key) external view returns (uint256) {
        return opinionNumberExtensions[opinionId][key];
    }

    function getOpinionBoolExtension(uint256 opinionId, string calldata key) external view returns (bool) {
        return opinionBoolExtensions[opinionId][key];
    }

    function getOpinionAddressExtension(uint256 opinionId, string calldata key) external view returns (address) {
        return opinionAddressExtensions[opinionId][key];
    }

    function hasOpinionExtension(uint256 opinionId, string calldata key) external view returns (bool) {
        string[] memory keys = opinionExtensionKeys[opinionId];
        bytes32 keyHash = keccak256(bytes(key));

        for (uint256 i = 0; i < keys.length; i++) {
            if (keccak256(bytes(keys[i])) == keyHash) {
                return true;
            }
        }
        return false;
    }

    function getOpinionExtensionCount(uint256 opinionId) external view returns (uint256) {
        return opinionExtensionKeys[opinionId].length;
    }

    // --- ADMIN FUNCTIONS ---

    function setMinimumPrice(uint96 _minimumPrice) external onlyRole(ADMIN_ROLE) {
        minimumPrice = _minimumPrice;
        emit ParameterUpdated(0, _minimumPrice);
    }

    function setQuestionCreationFee(uint96 _questionCreationFee) external onlyRole(ADMIN_ROLE) {
        questionCreationFee = _questionCreationFee;
        emit ParameterUpdated(6, _questionCreationFee);
    }

    function setInitialAnswerPrice(uint96 _initialAnswerPrice) external onlyRole(ADMIN_ROLE) {
        initialAnswerPrice = _initialAnswerPrice;
        emit ParameterUpdated(7, _initialAnswerPrice);
    }

    function setMaxPriceChange(uint256 _maxPriceChange) external onlyRole(ADMIN_ROLE) {
        absoluteMaxPriceChange = _maxPriceChange;
        emit ParameterUpdated(3, _maxPriceChange);
    }

    function setMaxTradesPerBlock(uint256 _maxTradesPerBlock) external onlyRole(ADMIN_ROLE) {
        maxTradesPerBlock = _maxTradesPerBlock;
        emit ParameterUpdated(4, _maxTradesPerBlock);
    }

    function togglePublicCreation() external onlyRole(ADMIN_ROLE) {
        isPublicCreationEnabled = !isPublicCreationEnabled;
        emit AdminAction(1, msg.sender, bytes32(0), 0);
    }

    function setFeeManager(address _feeManager) external onlyRole(ADMIN_ROLE) {
        if (_feeManager == address(0)) revert ZeroAddressNotAllowed();
        feeManager = IFeeManager(_feeManager);
    }

    function setPoolManager(address _poolManager) external onlyRole(ADMIN_ROLE) {
        if (_poolManager == address(0)) revert ZeroAddressNotAllowed();
        poolManager = IPoolManager(_poolManager);
    }

    function setMonitoringManager(address _monitoringManager) external onlyRole(ADMIN_ROLE) {
        monitoringManager = IMonitoringManager(_monitoringManager);
    }

    function setSecurityManager(address _securityManager) external onlyRole(ADMIN_ROLE) {
        securityManager = ISecurityManager(_securityManager);
    }

    function grantMarketContractRole(address contractAddress) external onlyRole(ADMIN_ROLE) {
        if (contractAddress == address(0)) revert ZeroAddressNotAllowed();
        _grantRole(MARKET_CONTRACT_ROLE, contractAddress);
    }

    function revokeMarketContractRole(address contractAddress) external onlyRole(ADMIN_ROLE) {
        _revokeRole(MARKET_CONTRACT_ROLE, contractAddress);
    }

    function setTreasury(address newTreasury) external onlyRole(ADMIN_ROLE) {
        if (newTreasury == address(0)) revert ZeroAddressNotAllowed();

        pendingTreasury = newTreasury;
        treasuryChangeTimestamp = block.timestamp + TREASURY_CHANGE_DELAY;

        emit TreasuryUpdated(treasury, newTreasury, msg.sender, block.timestamp);
    }

    function confirmTreasuryChange() external onlyRole(ADMIN_ROLE) {
        if (block.timestamp < treasuryChangeTimestamp) 
            revert("Treasury: Timelock not elapsed");
        if (pendingTreasury == address(0)) 
            revert("Treasury: No pending treasury");

        address oldTreasury = treasury;
        treasury = pendingTreasury;
        pendingTreasury = address(0);
        treasuryChangeTimestamp = 0;

        emit TreasuryUpdated(oldTreasury, treasury, msg.sender, block.timestamp);
    }

    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    function emergencyWithdraw(address token) external nonReentrant whenPaused onlyRole(ADMIN_ROLE) {
        if (token == address(0)) revert ZeroAddressNotAllowed();

        IERC20 tokenContract = IERC20(token);
        uint256 balance = tokenContract.balanceOf(address(this));

        if (token == address(usdcToken)) {
            uint256 totalFees = feeManager.getTotalAccumulatedFees();
            if (balance <= totalFees) revert("Insufficient balance after fees");
            balance -= totalFees;
        }

        tokenContract.safeTransfer(msg.sender, balance);
        emit AdminAction(0, msg.sender, bytes32(0), balance);
    }

    // --- INTERNAL FUNCTIONS ---

    function _basicValidation(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) internal view {
        ValidationLibrary.validateOpinionCategories(opinionCategories, categories);
        ValidationLibrary.validateOpinionParams(question, answer, MAX_QUESTION_LENGTH, MAX_ANSWER_LENGTH);
        ValidationLibrary.validateDescription(description);

        if (initialPrice < MIN_INITIAL_PRICE || initialPrice > MAX_INITIAL_PRICE) {
            revert InvalidInitialPrice();
        }
    }

    function _validateExtras(string calldata ipfsHash, string calldata link) internal pure {
        bytes memory ipfsHashBytes = bytes(ipfsHash);
        bytes memory linkBytes = bytes(link);

        if (ipfsHashBytes.length > MAX_IPFS_HASH_LENGTH) revert InvalidIpfsHashLength();
        if (linkBytes.length > MAX_LINK_LENGTH) revert InvalidLinkLength();

        if (ipfsHashBytes.length > 0) {
            _validateIpfsHash(ipfsHash);
        }
    }

    function _validateAnswerInput(string calldata answer, string calldata description) internal pure {
        bytes memory answerBytes = bytes(answer);
        if (answerBytes.length == 0) revert EmptyString();
        if (answerBytes.length > MAX_ANSWER_LENGTH) revert InvalidAnswerLength();

        ValidationLibrary.validateDescription(description);
    }

    function _createOpinionRecord(
        string memory question,
        string memory answer,
        string memory description,
        string memory ipfsHash,
        string memory link,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) internal returns (uint256) {
        uint256 opinionId = nextOpinionId++;
        OpinionStructs.Opinion storage opinion = opinions[opinionId];

        opinion.creator = msg.sender;
        opinion.questionOwner = msg.sender;
        opinion.lastPrice = initialPrice;
        opinion.nextPrice = uint96(_calculateNextPrice(opinionId, initialPrice));
        opinion.isActive = true;
        opinion.question = question;
        opinion.currentAnswer = answer;
        opinion.currentAnswerDescription = description;
        opinion.currentAnswerOwner = msg.sender;
        opinion.totalVolume = initialPrice;
        opinion.ipfsHash = ipfsHash;
        opinion.link = link;
        opinion.categories = opinionCategories;

        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: description,
                owner: msg.sender,
                price: initialPrice,
                timestamp: uint32(block.timestamp)
            })
        );

        return opinionId;
    }

    function _validateIpfsHash(string memory _ipfsHash) internal pure {
        bytes memory ipfsHashBytes = bytes(_ipfsHash);

        bool isValidCIDv0 = ipfsHashBytes.length == 46 &&
            ipfsHashBytes[0] == "Q" &&
            ipfsHashBytes[1] == "m";

        bool isValidCIDv1 = ipfsHashBytes.length >= 48 &&
            ipfsHashBytes[0] == "b";

        if (!isValidCIDv0 && !isValidCIDv1) {
            revert InvalidIpfsHashFormat();
        }
    }

    function _calculateNextPrice(uint256 opinionId, uint256 lastPrice) internal returns (uint256) {
        uint256 newPrice = PriceCalculator.calculateNextPrice(
            opinionId,
            lastPrice,
            minimumPrice,
            absoluteMaxPriceChange,
            nonce++,
            priceMetadata,
            priceHistory
        );

        _updatePriceHistory(opinionId, newPrice);
        return newPrice;
    }

    function _estimateNextPrice(uint256 lastPrice) internal pure returns (uint256) {
        return (lastPrice * 130) / 100;
    }

    function _updatePriceHistory(uint256 opinionId, uint256 newPrice) internal {
        uint256 meta = priceMetadata[opinionId];
        uint8 count = uint8(meta);

        priceMetadata[opinionId] = (block.timestamp << 8) | (count < 3 ? count + 1 : 3);

        uint256 history = priceHistory[opinionId];
        history = (history << 80) & (~uint256(0) << 160);
        history |= (newPrice & ((1 << 80) - 1));
        priceHistory[opinionId] = history;
    }

    function _checkAndUpdateTradesInBlock() internal {
        if (userLastBlock[msg.sender] != block.number) {
            userTradesInBlock[msg.sender] = 1;
            userLastBlock[msg.sender] = block.number;
        } else {
            userTradesInBlock[msg.sender]++;
            if (userTradesInBlock[msg.sender] > maxTradesPerBlock) {
                revert MaxTradesPerBlockExceeded(userTradesInBlock[msg.sender], maxTradesPerBlock);
            }
        }
    }

    function _checkTradeAllowed(uint256 opinionId) internal {
        if (userLastTradeBlock[msg.sender][opinionId] == block.number)
            revert OneTradePerBlock();
        userLastTradeBlock[msg.sender][opinionId] = block.number;
    }

    function isValidExtensionKey(string memory key) internal pure returns (bool) {
        bytes memory keyBytes = bytes(key);

        if (keyBytes.length == 0 || keyBytes.length > 32) return false;

        for (uint i = 0; i < keyBytes.length; i++) {
            uint8 char = uint8(keyBytes[i]);
            bool isAlpha = (char >= 65 && char <= 90) || (char >= 97 && char <= 122);
            bool isNumeric = (char >= 48 && char <= 57);
            bool isUnderscore = (char == 95);

            if (!isAlpha && !isNumeric && !isUnderscore) return false;
        }

        return true;
    }

    function _trackExtensionKey(uint256 opinionId, string memory key) internal {
        string[] storage keys = opinionExtensionKeys[opinionId];
        bytes32 keyHash = keccak256(bytes(key));

        for (uint i = 0; i < keys.length; i++) {
            if (keccak256(bytes(keys[i])) == keyHash) {
                return;
            }
        }

        keys.push(key);
    }

    function moderateAnswer(uint256, string calldata) external pure {
        revert("Moderation not implemented");
    }

    // --- MISSING INTERFACE IMPLEMENTATIONS ---

    /**
     * @dev Transfer answer ownership (simplified implementation)
     */
    function transferAnswerOwnership(
        uint256 opinionId,
        address newOwner
    ) external override nonReentrant whenNotPaused {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (newOwner == address(0)) revert ZeroAddressNotAllowed();

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.currentAnswerOwner != msg.sender) revert NotAnswerOwner();
        if (newOwner == msg.sender) revert("Cannot transfer to yourself");

        address previousOwner = opinion.currentAnswerOwner;
        opinion.currentAnswerOwner = newOwner;

        emit AnswerOwnershipTransferred(opinionId, previousOwner, newOwner, block.timestamp);
        emit OpinionAction(opinionId, 5, "", newOwner, 0);
    }

    /**
     * @dev Update opinion (simplified implementation)  
     */
    function updateOpinion(
        uint256 opinionId,
        string calldata question,
        string calldata ipfsHash,
        string calldata link,
        string[] calldata opinionCategories
    ) external override nonReentrant whenNotPaused {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator != msg.sender) revert("Only creator can update");

        // Basic validation
        if (bytes(question).length > MAX_QUESTION_LENGTH) revert InvalidQuestionLength();
        if (bytes(ipfsHash).length > MAX_IPFS_HASH_LENGTH) revert InvalidIpfsHashLength();
        if (bytes(link).length > MAX_LINK_LENGTH) revert InvalidLinkLength();
        if (opinionCategories.length > MAX_CATEGORIES_PER_OPINION) revert TooManyCategories();

        // Update the opinion
        opinion.question = question;
        opinion.ipfsHash = ipfsHash;
        opinion.link = link;
        opinion.categories = opinionCategories;

        emit OpinionAction(opinionId, 6, question, msg.sender, 0);
    }

    // Events are inherited from IOpinionMarketEvents
}

contracts/core/OpinionCoreSimplified.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IOpinionCore.sol";
import "./interfaces/IFeeManager.sol";
import "./interfaces/IPoolManager.sol";
import "./interfaces/IMonitoringManager.sol";
import "./interfaces/ISecurityManager.sol";
import "./interfaces/IOpinionMarketEvents.sol";
import "./interfaces/IOpinionMarketErrors.sol";
import "./structs/OpinionStructs.sol";
import "./libraries/ValidationLibrary.sol";
import "./libraries/PriceCalculator.sol";
import "./interfaces/IValidationErrors.sol";

/**
 * @title OpinionCoreSimplified
 * @dev Simplified core contract for managing opinions, answers, and related functionality
 * Security and monitoring features have been extracted to separate contracts
 */
contract OpinionCoreSimplified is
    Initializable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    IOpinionCore,
    IOpinionMarketEvents,
    IOpinionMarketErrors,
    IValidationErrors
{
    using SafeERC20 for IERC20;

    // --- ROLES ---
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");
    bytes32 public constant MARKET_CONTRACT_ROLE = keccak256("MARKET_CONTRACT_ROLE");
    bytes32 public constant POOL_MANAGER_ROLE = keccak256("POOL_MANAGER_ROLE");

    // --- CONSTANTS ---
    uint256 public constant MAX_QUESTION_LENGTH = 52;
    uint256 public constant MAX_ANSWER_LENGTH = 52;
    uint256 public constant MAX_LINK_LENGTH = 260;
    uint256 public constant MAX_IPFS_HASH_LENGTH = 68;
    uint256 public constant MAX_DESCRIPTION_LENGTH = 120;
    uint256 public constant MAX_CATEGORIES_PER_OPINION = 3;

    // --- INITIAL PRICE RANGE CONSTANTS ---
    uint96 public constant MIN_INITIAL_PRICE = 2_000_000;   // 2 USDC (6 decimals)
    uint96 public constant MAX_INITIAL_PRICE = 100_000_000; // 100 USDC (6 decimals)

    // --- STATE VARIABLES ---
    IERC20 public usdcToken;
    IFeeManager public feeManager;
    IPoolManager public poolManager;
    IMonitoringManager public monitoringManager;
    ISecurityManager public securityManager;

    address public treasury;
    address public pendingTreasury;
    uint256 public treasuryChangeTimestamp;
    uint256 public constant TREASURY_CHANGE_DELAY = 48 hours;

    bool public isPublicCreationEnabled;
    uint256 public nextOpinionId;

    // --- CATEGORIES STORAGE ---
    string[] public categories;

    // --- EXTENSION SLOTS STORAGE ---
    mapping(uint256 => mapping(string => string)) public opinionStringExtensions;
    mapping(uint256 => mapping(string => uint256)) public opinionNumberExtensions;  
    mapping(uint256 => mapping(string => bool)) public opinionBoolExtensions;
    mapping(uint256 => mapping(string => address)) public opinionAddressExtensions;
    mapping(uint256 => string[]) public opinionExtensionKeys;

    // Security and rate limiting
    uint256 public maxTradesPerBlock;
    mapping(address => uint256) private userLastBlock;
    mapping(address => uint256) private userTradesInBlock;
    mapping(address => mapping(uint256 => uint256)) private userLastTradeBlock;

    // Price calculation
    uint256 private nonce;
    mapping(uint256 => uint256) private priceMetadata;
    mapping(uint256 => uint256) private priceHistory;

    // Configurable parameters
    uint96 public minimumPrice;
    uint96 public questionCreationFee;
    uint96 public initialAnswerPrice;
    uint256 public absoluteMaxPriceChange;

    // Core data structures
    mapping(uint256 => OpinionStructs.Opinion) public opinions;
    mapping(uint256 => OpinionStructs.AnswerHistory[]) public answerHistory;

    // --- INITIALIZATION ---
    function initialize(
        address _usdcToken,
        address _opinionMarket,
        address _feeManager,
        address _poolManager,
        address _monitoringManager,
        address _securityManager,
        address _treasury
    ) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();
        __Pausable_init();

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(MODERATOR_ROLE, msg.sender);

        // Set external contracts
        if (
            _usdcToken == address(0) ||
            _opinionMarket == address(0) ||
            _feeManager == address(0) ||
            _poolManager == address(0) ||
            _treasury == address(0)
        ) revert ZeroAddressNotAllowed();

        usdcToken = IERC20(_usdcToken);
        feeManager = IFeeManager(_feeManager);
        poolManager = IPoolManager(_poolManager);
        treasury = _treasury;

        // Grant role to opinion market contract
        _grantRole(MARKET_CONTRACT_ROLE, _opinionMarket);

        // Set optional modules (can be zero initially)
        if (_monitoringManager != address(0)) {
            monitoringManager = IMonitoringManager(_monitoringManager);
        }
        if (_securityManager != address(0)) {
            securityManager = ISecurityManager(_securityManager);
        }

        // Initialize parameters
        nextOpinionId = 1;
        isPublicCreationEnabled = false;
        maxTradesPerBlock = 3;
        minimumPrice = 1_000_000; // 1 USDC (6 decimals)
        questionCreationFee = 5_000_000; // 5 USDC
        initialAnswerPrice = 2_000_000; // 2 USDC
        absoluteMaxPriceChange = 200; // 200%

        // Initialize default categories
        categories = ["Crypto", "Politics", "Science", "Technology", "Sports", 
                      "Entertainment", "Culture", "Web", "Social Media", "Other"];
    }

    // --- MODIFIERS ---
    modifier onlyMarketContract() {
        if (!hasRole(MARKET_CONTRACT_ROLE, msg.sender))
            revert AccessControlUnauthorizedAccount(msg.sender, MARKET_CONTRACT_ROLE);
        _;
    }

    modifier onlyPoolManager() {
        if (!hasRole(POOL_MANAGER_ROLE, msg.sender))
            revert AccessControlUnauthorizedAccount(msg.sender, POOL_MANAGER_ROLE);
        _;
    }

    // --- CORE OPINION FUNCTIONS ---

    function createOpinion(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) external override nonReentrant whenNotPaused {
        // Access control check
        if (!isPublicCreationEnabled && !hasRole(ADMIN_ROLE, msg.sender))
            revert UnauthorizedCreator();

        // Enhanced validation if SecurityManager is available
        if (address(securityManager) != address(0)) {
            try securityManager.validateCreateOpinionInputs(
                question, answer, description, initialPrice, opinionCategories, msg.sender
            ) {} catch {
                // Fallback to basic validation if security manager fails
                _basicValidation(question, answer, description, initialPrice, opinionCategories);
            }
        } else {
            _basicValidation(question, answer, description, initialPrice, opinionCategories);
        }

        // Check allowance for initialPrice
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < initialPrice)
            revert InsufficientAllowance(initialPrice, allowance);

        // Create opinion record
        uint256 opinionId = _createOpinionRecord(
            question, answer, description, "", "", initialPrice, opinionCategories
        );

        // Transfer payment to treasury
        usdcToken.safeTransferFrom(msg.sender, treasury, initialPrice);

        // Track with monitoring manager if available
        if (address(monitoringManager) != address(0)) {
            try monitoringManager.trackOpinionCreation(opinionId, msg.sender, initialPrice) {} catch {}
        }

        // Emit events
        emit OpinionAction(opinionId, 0, question, msg.sender, initialPrice);
        emit OpinionAction(opinionId, 1, answer, msg.sender, initialPrice);
    }

    function createOpinionWithExtras(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories,
        string calldata ipfsHash,
        string calldata link
    ) external override nonReentrant whenNotPaused {
        // Access control check
        if (!isPublicCreationEnabled && !hasRole(ADMIN_ROLE, msg.sender))
            revert UnauthorizedCreator();

        // Enhanced validation if SecurityManager is available
        if (address(securityManager) != address(0)) {
            try securityManager.validateCreateOpinionInputs(
                question, answer, description, initialPrice, opinionCategories, msg.sender
            ) {} catch {
                _basicValidation(question, answer, description, initialPrice, opinionCategories);
            }
        } else {
            _basicValidation(question, answer, description, initialPrice, opinionCategories);
        }

        // Validate IPFS hash and link
        _validateExtras(ipfsHash, link);

        // Check allowance
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < initialPrice)
            revert InsufficientAllowance(initialPrice, allowance);

        // Create opinion record
        uint256 opinionId = _createOpinionRecord(
            question, answer, description, ipfsHash, link, initialPrice, opinionCategories
        );

        // Transfer payment to treasury
        usdcToken.safeTransferFrom(msg.sender, treasury, initialPrice);

        // Track with monitoring manager if available
        if (address(monitoringManager) != address(0)) {
            try monitoringManager.trackOpinionCreation(opinionId, msg.sender, initialPrice) {} catch {}
        }

        // Emit events
        emit OpinionAction(opinionId, 0, question, msg.sender, initialPrice);
        emit OpinionAction(opinionId, 1, answer, msg.sender, initialPrice);
    }

    function submitAnswer(
        uint256 opinionId,
        string calldata answer,
        string calldata description,
        string calldata link
    ) external override nonReentrant whenNotPaused {
        _checkAndUpdateTradesInBlock();
        _checkTradeAllowed(opinionId);

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (!opinion.isActive) revert OpinionNotActive();
        if (opinion.currentAnswerOwner == msg.sender) revert SameOwner();

        // Basic validation
        _validateAnswerInput(answer, description);

        // Validate link (optional)
        bytes memory linkBytes = bytes(link);
        if (linkBytes.length > MAX_LINK_LENGTH) revert InvalidLinkLength();

        // Calculate price
        uint96 price = opinion.nextPrice > 0
            ? opinion.nextPrice
            : uint96(_calculateNextPrice(opinionId, opinion.lastPrice));

        // Check USDC allowance
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < price) revert InsufficientAllowance(price, allowance);

        // Security analysis if SecurityManager is available
        if (address(securityManager) != address(0)) {
            // Check MEV risk
            try securityManager.analyzeMevRisk(msg.sender, price, opinionId) {} catch {}

            // Analyze bot patterns
            // Note: We can't easily determine trade success here, so we skip bot analysis for now
        }

        // Calculate fees
        (uint96 platformFee, uint96 creatorFee, uint96 ownerAmount) = feeManager
            .calculateFeeDistribution(price);

        // Apply MEV penalty if needed
        (platformFee, ownerAmount) = feeManager.applyMEVPenalty(
            price, ownerAmount, msg.sender, opinionId
        );

        // Get addresses for fee distribution
        address creator = opinion.creator;
        address currentAnswerOwner = opinion.currentAnswerOwner;
        bool answerIsPoolOwned = currentAnswerOwner == address(poolManager);

        // Accumulate fees
        feeManager.accumulateFee(creator, creatorFee);

        if (answerIsPoolOwned) {
            poolManager.distributePoolRewards(opinionId, price, msg.sender);
        } else {
            feeManager.accumulateFee(currentAnswerOwner, ownerAmount);
        }

        // Record answer history
        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: description,
                owner: msg.sender,
                price: price,
                timestamp: uint32(block.timestamp)
            })
        );

        // Update opinion state
        uint96 oldPrice = opinion.lastPrice;
        opinion.currentAnswer = answer;
        opinion.currentAnswerDescription = description;
        opinion.currentAnswerOwner = msg.sender;
        opinion.link = link;
        opinion.lastPrice = price;
        opinion.totalVolume += price;
        opinion.nextPrice = uint96(_calculateNextPrice(opinionId, price));

        // Transfer tokens
        usdcToken.safeTransferFrom(msg.sender, address(this), price);

        // Track with monitoring manager if available
        if (address(monitoringManager) != address(0)) {
            try monitoringManager.trackTradingActivity(
                opinionId, msg.sender, price, oldPrice, opinion.nextPrice, platformFee
            ) {} catch {}
        }

        // Update security profiles if SecurityManager is available
        if (address(securityManager) != address(0)) {
            try securityManager.updateMevProfile(msg.sender, opinionId, price) {} catch {}
        }

        // Emit events
        emit FeesAction(opinionId, 0, currentAnswerOwner, price, platformFee, creatorFee, ownerAmount);
        emit OpinionAction(opinionId, 1, answer, msg.sender, price);
    }

    // --- QUESTION TRADING FUNCTIONS ---

    function listQuestionForSale(uint256 opinionId, uint256 price) external override nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (opinion.questionOwner != msg.sender) revert NotTheOwner(msg.sender, opinion.questionOwner);

        opinion.salePrice = uint96(price);
        emit QuestionSaleAction(opinionId, 0, msg.sender, address(0), price);
    }

    function buyQuestion(uint256 opinionId) external override nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();

        uint96 salePrice = opinion.salePrice;
        if (salePrice == 0) revert NotForSale(opinionId);

        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < salePrice) revert InsufficientAllowance(salePrice, allowance);

        address currentOwner = opinion.questionOwner;
        uint96 platformFee = uint96((salePrice * 10) / 100);
        uint96 sellerAmount = salePrice - platformFee;

        opinion.questionOwner = msg.sender;
        opinion.salePrice = 0;

        usdcToken.safeTransferFrom(msg.sender, address(this), salePrice);
        feeManager.accumulateFee(currentOwner, sellerAmount);

        emit QuestionSaleAction(opinionId, 1, currentOwner, msg.sender, salePrice);
    }

    function cancelQuestionSale(uint256 opinionId) external override nonReentrant whenNotPaused {
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound();
        if (opinion.questionOwner != msg.sender) revert NotTheOwner(msg.sender, opinion.questionOwner);

        opinion.salePrice = 0;
        emit QuestionSaleAction(opinionId, 2, msg.sender, address(0), 0);
    }

    // --- MODERATION FUNCTIONS ---

    function deactivateOpinion(uint256 opinionId) external override onlyRole(MODERATOR_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (!opinion.isActive) revert OpinionNotActive();

        opinion.isActive = false;
        emit OpinionAction(opinionId, 2, "", msg.sender, 0);
    }

    function reactivateOpinion(uint256 opinionId) external override onlyRole(MODERATOR_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.isActive) revert OpinionAlreadyActive();

        opinion.isActive = true;
        emit OpinionAction(opinionId, 3, "", msg.sender, 0);
    }

    // --- POOL INTEGRATION ---

    function updateOpinionOnPoolExecution(
        uint256 opinionId,
        string calldata answer,
        address poolAddress,
        uint256 price
    ) external override onlyPoolManager {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (!opinion.isActive) revert OpinionNotActive();

        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: "",
                owner: poolAddress,
                price: uint96(price),
                timestamp: uint32(block.timestamp)
            })
        );

        opinion.currentAnswer = answer;
        opinion.currentAnswerDescription = "";
        opinion.currentAnswerOwner = poolAddress;
        opinion.lastPrice = uint96(price);
        opinion.totalVolume += uint96(price);
        opinion.nextPrice = uint96(_calculateNextPrice(opinionId, price));

        emit OpinionAction(opinionId, 1, answer, address(poolManager), price);
    }

    // --- VIEW FUNCTIONS ---

    function getAnswerHistory(uint256 opinionId) external view override returns (OpinionStructs.AnswerHistory[] memory) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return answerHistory[opinionId];
    }

    function getNextPrice(uint256 opinionId) external view override returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (!opinion.isActive) revert OpinionNotActive();

        if (opinion.nextPrice == 0) {
            return _estimateNextPrice(opinion.lastPrice);
        }
        return opinion.nextPrice;
    }

    function getOpinionDetails(uint256 opinionId) external view override returns (OpinionStructs.Opinion memory) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId];
    }

    function getTradeCount(uint256 opinionId) external view override returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return answerHistory[opinionId].length;
    }

    function getCreatorGain(uint256 opinionId) external view override returns (uint256) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        OpinionStructs.Opinion storage opinion = opinions[opinionId];

        (, uint96 creatorFeePercent, ) = feeManager.calculateFeeDistribution(1_000_000);
        uint256 creatorFeeRate = creatorFeePercent / 10;
        return (opinion.totalVolume * creatorFeeRate) / 100;
    }

    function isPoolOwned(uint256 opinionId) external view override returns (bool) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId].currentAnswerOwner == address(poolManager);
    }

    // --- CATEGORIES FUNCTIONS ---

    function addCategoryToCategories(string calldata newCategory) external onlyRole(ADMIN_ROLE) {
        bytes32 newCategoryHash = keccak256(bytes(newCategory));
        uint256 length = categories.length;

        for (uint256 i = 0; i < length; i++) {
            if (keccak256(bytes(categories[i])) == newCategoryHash) {
                revert CategoryAlreadyExists();
            }
        }
        categories.push(newCategory);
    }

    function getAvailableCategories() external view returns (string[] memory) {
        return categories;
    }

    function getOpinionCategories(uint256 opinionId) external view returns (string[] memory) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        return opinions[opinionId].categories;
    }

    function getCategoryCount() external view returns (uint256) {
        return categories.length;
    }

    // --- EXTENSION SLOTS FUNCTIONS ---

    function setOpinionStringExtension(uint256 opinionId, string calldata key, string calldata value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionStringExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionStringExtensionSet(opinionId, key, value);
    }

    function setOpinionNumberExtension(uint256 opinionId, string calldata key, uint256 value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionNumberExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionNumberExtensionSet(opinionId, key, value);
    }

    function setOpinionBoolExtension(uint256 opinionId, string calldata key, bool value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionBoolExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionBoolExtensionSet(opinionId, key, value);
    }

    function setOpinionAddressExtension(uint256 opinionId, string calldata key, address value) external onlyRole(ADMIN_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (!isValidExtensionKey(key)) revert InvalidExtensionKey();

        opinionAddressExtensions[opinionId][key] = value;
        _trackExtensionKey(opinionId, key);
        emit OpinionAddressExtensionSet(opinionId, key, value);
    }

    function getOpinionExtensions(uint256 opinionId) external view returns (
        string[] memory keys,
        string[] memory stringValues,
        uint256[] memory numberValues,
        bool[] memory boolValues,
        address[] memory addressValues
    ) {
        keys = opinionExtensionKeys[opinionId];
        uint256 length = keys.length;

        stringValues = new string[](length);
        numberValues = new uint256[](length);
        boolValues = new bool[](length);
        addressValues = new address[](length);

        for (uint256 i = 0; i < length; i++) {
            string memory key = keys[i];
            stringValues[i] = opinionStringExtensions[opinionId][key];
            numberValues[i] = opinionNumberExtensions[opinionId][key];
            boolValues[i] = opinionBoolExtensions[opinionId][key];
            addressValues[i] = opinionAddressExtensions[opinionId][key];
        }

        return (keys, stringValues, numberValues, boolValues, addressValues);
    }

    function getOpinionStringExtension(uint256 opinionId, string calldata key) external view returns (string memory) {
        return opinionStringExtensions[opinionId][key];
    }

    function getOpinionNumberExtension(uint256 opinionId, string calldata key) external view returns (uint256) {
        return opinionNumberExtensions[opinionId][key];
    }

    function getOpinionBoolExtension(uint256 opinionId, string calldata key) external view returns (bool) {
        return opinionBoolExtensions[opinionId][key];
    }

    function getOpinionAddressExtension(uint256 opinionId, string calldata key) external view returns (address) {
        return opinionAddressExtensions[opinionId][key];
    }

    function hasOpinionExtension(uint256 opinionId, string calldata key) external view returns (bool) {
        string[] memory keys = opinionExtensionKeys[opinionId];
        bytes32 keyHash = keccak256(bytes(key));

        for (uint256 i = 0; i < keys.length; i++) {
            if (keccak256(bytes(keys[i])) == keyHash) {
                return true;
            }
        }
        return false;
    }

    function getOpinionExtensionCount(uint256 opinionId) external view returns (uint256) {
        return opinionExtensionKeys[opinionId].length;
    }

    // --- ADMIN FUNCTIONS ---

    function setMinimumPrice(uint96 _minimumPrice) external onlyRole(ADMIN_ROLE) {
        minimumPrice = _minimumPrice;
        emit ParameterUpdated(0, _minimumPrice);
    }

    function setQuestionCreationFee(uint96 _questionCreationFee) external onlyRole(ADMIN_ROLE) {
        questionCreationFee = _questionCreationFee;
        emit ParameterUpdated(6, _questionCreationFee);
    }

    function setInitialAnswerPrice(uint96 _initialAnswerPrice) external onlyRole(ADMIN_ROLE) {
        initialAnswerPrice = _initialAnswerPrice;
        emit ParameterUpdated(7, _initialAnswerPrice);
    }

    function setMaxPriceChange(uint256 _maxPriceChange) external onlyRole(ADMIN_ROLE) {
        absoluteMaxPriceChange = _maxPriceChange;
        emit ParameterUpdated(3, _maxPriceChange);
    }

    function setMaxTradesPerBlock(uint256 _maxTradesPerBlock) external onlyRole(ADMIN_ROLE) {
        maxTradesPerBlock = _maxTradesPerBlock;
        emit ParameterUpdated(4, _maxTradesPerBlock);
    }

    function togglePublicCreation() external onlyRole(ADMIN_ROLE) {
        isPublicCreationEnabled = !isPublicCreationEnabled;
        emit AdminAction(1, msg.sender, bytes32(0), 0);
    }

    function setFeeManager(address _feeManager) external onlyRole(ADMIN_ROLE) {
        if (_feeManager == address(0)) revert ZeroAddressNotAllowed();
        feeManager = IFeeManager(_feeManager);
    }

    function setPoolManager(address _poolManager) external onlyRole(ADMIN_ROLE) {
        if (_poolManager == address(0)) revert ZeroAddressNotAllowed();
        poolManager = IPoolManager(_poolManager);
    }

    function setMonitoringManager(address _monitoringManager) external onlyRole(ADMIN_ROLE) {
        monitoringManager = IMonitoringManager(_monitoringManager);
    }

    function setSecurityManager(address _securityManager) external onlyRole(ADMIN_ROLE) {
        securityManager = ISecurityManager(_securityManager);
    }

    function grantMarketContractRole(address contractAddress) external onlyRole(ADMIN_ROLE) {
        if (contractAddress == address(0)) revert ZeroAddressNotAllowed();
        _grantRole(MARKET_CONTRACT_ROLE, contractAddress);
    }

    function revokeMarketContractRole(address contractAddress) external onlyRole(ADMIN_ROLE) {
        _revokeRole(MARKET_CONTRACT_ROLE, contractAddress);
    }

    function setTreasury(address newTreasury) external onlyRole(ADMIN_ROLE) {
        if (newTreasury == address(0)) revert ZeroAddressNotAllowed();

        pendingTreasury = newTreasury;
        treasuryChangeTimestamp = block.timestamp + TREASURY_CHANGE_DELAY;

        emit TreasuryUpdated(treasury, newTreasury, msg.sender, block.timestamp);
    }

    function confirmTreasuryChange() external onlyRole(ADMIN_ROLE) {
        if (block.timestamp < treasuryChangeTimestamp) 
            revert("Treasury: Timelock not elapsed");
        if (pendingTreasury == address(0)) 
            revert("Treasury: No pending treasury");

        address oldTreasury = treasury;
        treasury = pendingTreasury;
        pendingTreasury = address(0);
        treasuryChangeTimestamp = 0;

        emit TreasuryUpdated(oldTreasury, treasury, msg.sender, block.timestamp);
    }

    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    function emergencyWithdraw(address token) external nonReentrant whenPaused onlyRole(ADMIN_ROLE) {
        if (token == address(0)) revert ZeroAddressNotAllowed();

        IERC20 tokenContract = IERC20(token);
        uint256 balance = tokenContract.balanceOf(address(this));

        if (token == address(usdcToken)) {
            uint256 totalFees = feeManager.getTotalAccumulatedFees();
            if (balance <= totalFees) revert("Insufficient balance after fees");
            balance -= totalFees;
        }

        tokenContract.safeTransfer(msg.sender, balance);
        emit AdminAction(0, msg.sender, bytes32(0), balance);
    }

    // --- INTERNAL FUNCTIONS ---

    function _basicValidation(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) internal view {
        ValidationLibrary.validateOpinionCategories(opinionCategories, categories);
        ValidationLibrary.validateOpinionParams(question, answer, MAX_QUESTION_LENGTH, MAX_ANSWER_LENGTH);
        ValidationLibrary.validateDescription(description);

        if (initialPrice < MIN_INITIAL_PRICE || initialPrice > MAX_INITIAL_PRICE) {
            revert InvalidInitialPrice();
        }
    }

    function _validateExtras(string calldata ipfsHash, string calldata link) internal pure {
        bytes memory ipfsHashBytes = bytes(ipfsHash);
        bytes memory linkBytes = bytes(link);

        if (ipfsHashBytes.length > MAX_IPFS_HASH_LENGTH) revert InvalidIpfsHashLength();
        if (linkBytes.length > MAX_LINK_LENGTH) revert InvalidLinkLength();

        if (ipfsHashBytes.length > 0) {
            _validateIpfsHash(ipfsHash);
        }
    }

    function _validateAnswerInput(string calldata answer, string calldata description) internal pure {
        bytes memory answerBytes = bytes(answer);
        if (answerBytes.length == 0) revert EmptyString();
        if (answerBytes.length > MAX_ANSWER_LENGTH) revert InvalidAnswerLength();

        ValidationLibrary.validateDescription(description);
    }

    function _createOpinionRecord(
        string memory question,
        string memory answer,
        string memory description,
        string memory ipfsHash,
        string memory link,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) internal returns (uint256) {
        uint256 opinionId = nextOpinionId++;
        OpinionStructs.Opinion storage opinion = opinions[opinionId];

        opinion.creator = msg.sender;
        opinion.questionOwner = msg.sender;
        opinion.lastPrice = initialPrice;
        opinion.nextPrice = uint96(_calculateNextPrice(opinionId, initialPrice));
        opinion.isActive = true;
        opinion.question = question;
        opinion.currentAnswer = answer;
        opinion.currentAnswerDescription = description;
        opinion.currentAnswerOwner = msg.sender;
        opinion.totalVolume = initialPrice;
        opinion.ipfsHash = ipfsHash;
        opinion.link = link;
        opinion.categories = opinionCategories;

        answerHistory[opinionId].push(
            OpinionStructs.AnswerHistory({
                answer: answer,
                description: description,
                owner: msg.sender,
                price: initialPrice,
                timestamp: uint32(block.timestamp)
            })
        );

        return opinionId;
    }

    function _validateIpfsHash(string memory _ipfsHash) internal pure {
        bytes memory ipfsHashBytes = bytes(_ipfsHash);

        bool isValidCIDv0 = ipfsHashBytes.length == 46 &&
            ipfsHashBytes[0] == "Q" &&
            ipfsHashBytes[1] == "m";

        bool isValidCIDv1 = ipfsHashBytes.length >= 48 &&
            ipfsHashBytes[0] == "b";

        if (!isValidCIDv0 && !isValidCIDv1) {
            revert InvalidIpfsHashFormat();
        }
    }

    function _calculateNextPrice(uint256 opinionId, uint256 lastPrice) internal returns (uint256) {
        uint256 newPrice = PriceCalculator.calculateNextPrice(
            opinionId,
            lastPrice,
            minimumPrice,
            absoluteMaxPriceChange,
            nonce++,
            priceMetadata,
            priceHistory
        );

        _updatePriceHistory(opinionId, newPrice);
        return newPrice;
    }

    function _estimateNextPrice(uint256 lastPrice) internal pure returns (uint256) {
        return (lastPrice * 130) / 100;
    }

    function _updatePriceHistory(uint256 opinionId, uint256 newPrice) internal {
        uint256 meta = priceMetadata[opinionId];
        uint8 count = uint8(meta);

        priceMetadata[opinionId] = (block.timestamp << 8) | (count < 3 ? count + 1 : 3);

        uint256 history = priceHistory[opinionId];
        history = (history << 80) & (~uint256(0) << 160);
        history |= (newPrice & ((1 << 80) - 1));
        priceHistory[opinionId] = history;
    }

    function _checkAndUpdateTradesInBlock() internal {
        if (userLastBlock[msg.sender] != block.number) {
            userTradesInBlock[msg.sender] = 1;
            userLastBlock[msg.sender] = block.number;
        } else {
            userTradesInBlock[msg.sender]++;
            if (userTradesInBlock[msg.sender] > maxTradesPerBlock) {
                revert MaxTradesPerBlockExceeded(userTradesInBlock[msg.sender], maxTradesPerBlock);
            }
        }
    }

    function _checkTradeAllowed(uint256 opinionId) internal {
        if (userLastTradeBlock[msg.sender][opinionId] == block.number)
            revert OneTradePerBlock();
        userLastTradeBlock[msg.sender][opinionId] = block.number;
    }

    function isValidExtensionKey(string memory key) internal pure returns (bool) {
        bytes memory keyBytes = bytes(key);

        if (keyBytes.length == 0 || keyBytes.length > 32) return false;

        for (uint i = 0; i < keyBytes.length; i++) {
            uint8 char = uint8(keyBytes[i]);
            bool isAlpha = (char >= 65 && char <= 90) || (char >= 97 && char <= 122);
            bool isNumeric = (char >= 48 && char <= 57);
            bool isUnderscore = (char == 95);

            if (!isAlpha && !isNumeric && !isUnderscore) return false;
        }

        return true;
    }

    function _trackExtensionKey(uint256 opinionId, string memory key) internal {
        string[] storage keys = opinionExtensionKeys[opinionId];
        bytes32 keyHash = keccak256(bytes(key));

        for (uint i = 0; i < keys.length; i++) {
            if (keccak256(bytes(keys[i])) == keyHash) {
                return;
            }
        }

        keys.push(key);
    }

    /**
     * @dev Moderates an inappropriate answer by reverting to initial answer (simplified version)
     * @param opinionId The ID of the opinion
     * @param reason The reason for moderation
     */
    function moderateAnswer(
        uint256 opinionId,
        string calldata reason
    ) external onlyRole(MODERATOR_ROLE) {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (!opinion.isActive) revert OpinionNotActive();

        // Can't moderate if creator is still the current owner (no inappropriate answer)
        if (opinion.currentAnswerOwner == opinion.creator) {
            revert("No answer to moderate");
        }

        address previousOwner = opinion.currentAnswerOwner;

        // Get initial answer from first entry in history
        OpinionStructs.AnswerHistory[] storage history = answerHistory[opinionId];
        require(history.length > 0, "No initial answer found");

        string memory initialAnswer = history[0].answer;
        string memory initialDescription = history[0].description;

        // Record moderation in history before reverting
        history.push(OpinionStructs.AnswerHistory({
            answer: "[MODERATED]",
            description: reason,
            owner: previousOwner,
            price: opinion.nextPrice,
            timestamp: uint32(block.timestamp)
        }));

        // Revert to initial answer and creator ownership
        opinion.currentAnswer = initialAnswer;
        opinion.currentAnswerDescription = initialDescription;
        opinion.currentAnswerOwner = opinion.creator;
        // Keep current price (fair for next trader)

        // Emit moderation event
        emit AnswerModerated(
            opinionId,
            previousOwner,
            opinion.creator,
            reason,
            block.timestamp
        );
    }

    // --- MISSING INTERFACE IMPLEMENTATIONS ---

    /**
     * @dev Transfer answer ownership (simplified implementation)
     */
    function transferAnswerOwnership(
        uint256 opinionId,
        address newOwner
    ) external override nonReentrant whenNotPaused {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();
        if (newOwner == address(0)) revert ZeroAddressNotAllowed();

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.currentAnswerOwner != msg.sender) revert NotAnswerOwner();
        if (newOwner == msg.sender) revert("Cannot transfer to yourself");

        address previousOwner = opinion.currentAnswerOwner;
        opinion.currentAnswerOwner = newOwner;

        emit AnswerOwnershipTransferred(opinionId, previousOwner, newOwner, block.timestamp);
        emit OpinionAction(opinionId, 5, "", newOwner, 0);
    }

    /**
     * @dev Update opinion (simplified implementation)  
     */
    function updateOpinion(
        uint256 opinionId,
        string calldata question,
        string calldata ipfsHash,
        string calldata link,
        string[] calldata opinionCategories
    ) external override nonReentrant whenNotPaused {
        if (opinionId >= nextOpinionId) revert OpinionNotFound();

        OpinionStructs.Opinion storage opinion = opinions[opinionId];
        if (opinion.creator != msg.sender) revert("Only creator can update");

        // Basic validation
        if (bytes(question).length > MAX_QUESTION_LENGTH) revert InvalidQuestionLength();
        if (bytes(ipfsHash).length > MAX_IPFS_HASH_LENGTH) revert InvalidIpfsHashLength();
        if (bytes(link).length > MAX_LINK_LENGTH) revert InvalidLinkLength();
        if (opinionCategories.length > MAX_CATEGORIES_PER_OPINION) revert TooManyCategories();

        // Update the opinion
        opinion.question = question;
        opinion.ipfsHash = ipfsHash;
        opinion.link = link;
        opinion.categories = opinionCategories;

        emit OpinionAction(opinionId, 6, question, msg.sender, 0);
    }

    // Events are inherited from IOpinionMarketEvents
}

contracts/core/OpinionCoreV2_Final.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./interfaces/IOpinionCore.sol";
import "./interfaces/IFeeManager.sol";
import "./interfaces/IPoolManager.sol";
import "./interfaces/IMonitoringManager.sol";
import "./interfaces/ISecurityManager.sol";
import "./interfaces/IOpinionMarketEvents.sol";
import "./interfaces/IOpinionMarketErrors.sol";
import "./structs/OpinionStructs.sol";
import "./libraries/ValidationLibrary.sol";
import "./libraries/PriceCalculator.sol";

/**
 * @title OpinionCoreV2_FullFeatures
 * @dev Full-featured version of OpinionCore, compatible with MinimalOpinionCore storage layout.
 * Includes all 50 functions: Moderation, Pools, Trading, Extensions, etc.
 */
contract OpinionCoreV2_Final is 
    Initializable,
    UUPSUpgradeable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    IOpinionCore,
    IOpinionMarketErrors
{
    using SafeERC20 for IERC20;

    // --- EVENTS (Defined locally to avoid interface conflicts) ---
    event OpinionCreatedLocal(
        uint256 indexed opinionId,
        string question,
        string initialAnswer,
        address indexed creator,
        uint256 initialPrice,
        uint256 timestamp
    );

    event OpinionAnsweredLocal(
        uint256 indexed opinionId,
        string answer,
        address indexed previousOwner,
        address indexed newOwner,
        uint256 price,
        uint256 timestamp
    );

    event OpinionStatusChanged(
        uint256 indexed opinionId,
        bool isActive,
        address indexed moderator,
        uint256 timestamp
    );

    event FeesAction(
        uint256 indexed opinionId,
        uint8 actionType,
        address indexed account,
        uint256 amount,
        uint256 platformFee,
        uint256 creatorFee,
        uint256 ownerAmount
    );

    event QuestionSaleAction(
        uint256 indexed opinionId,
        uint8 actionType,
        address indexed seller,
        address indexed buyer,
        uint256 price
    );

    event AnswerModerated(
        uint256 indexed opinionId,
        address indexed moderatedUser,
        address indexed newOwner,
        string reason,
        uint256 timestamp
    );

    event AnswerOwnershipTransferred(
        uint256 indexed opinionId,
        address indexed previousOwner,
        address indexed newOwner,
        uint256 timestamp
    );

    event OpinionStringExtensionSet(uint256 indexed opinionId, string key, string value);

    event ParameterUpdated(uint8 indexed paramId, uint256 value);
    event AdminAction(uint8 indexed actionType, address indexed account, bytes32 data, uint256 amount);
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury, address indexed admin, uint256 timestamp);

    // --- ROLES ---
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");
    bytes32 public constant MARKET_CONTRACT_ROLE = keccak256("MARKET_CONTRACT_ROLE");
    bytes32 public constant POOL_MANAGER_ROLE = keccak256("POOL_MANAGER_ROLE");

    // --- CONSTANTS ---
    uint256 public constant CREATION_FEE = 1_000_000; // 1 USDC (Legacy V1 constant)
    uint256 public constant MIN_ANSWER_PRICE = 1_000_000; // 1 USDC (Legacy V1 constant)

    // V2 Constants
    uint256 public constant MAX_QUESTION_LENGTH = 100; // V1 was 100
    uint256 public constant MAX_ANSWER_LENGTH = 100; // V1 was 100
    uint256 public constant MAX_LINK_LENGTH = 260;
    uint256 public constant MAX_IPFS_HASH_LENGTH = 68;
    uint256 public constant MAX_DESCRIPTION_LENGTH = 120;
    uint256 public constant MAX_CATEGORIES_PER_OPINION = 3;
    uint96 public constant MIN_INITIAL_PRICE = 1_000_000;   // 1 USDC
    uint96 public constant MAX_INITIAL_PRICE = 100_000_000; // 100 USDC
    uint256 public constant TREASURY_CHANGE_DELAY = 48 hours;

    // --- STORAGE LAYOUT (MUST MATCH MINIMAL V1 EXACTLY FIRST) ---

    IERC20 public usdcToken;
    address public feeManagerAddress; // Renamed to avoid conflict with interface, but storage slot is same
    address public treasury;
    uint256 public nextOpinionId;

    // Extended Opinion Struct (V1 fields + V2 fields)
    struct Opinion {
        // --- V1 Fields (DO NOT TOUCH ORDER) ---
        string question;
        string currentAnswer;
        address creator;
        address currentOwner; // Maps to currentAnswerOwner
        uint256 nextPrice;
        uint256 totalVolume;
        bool isActive;

        // --- V2 New Fields (Appended) ---
        address questionOwner;
        uint96 salePrice;
        uint96 lastPrice;
        string currentAnswerDescription;
        string link;
        string[] categories;
        string ipfsHash;
        string description;
    }

    mapping(uint256 => Opinion) public opinions;
    mapping(uint256 => uint256) public answerCount;

    // --- NEW STORAGE (Replacing __gap from V1) ---

    IPoolManager public poolManager;
    IMonitoringManager public monitoringManager;
    ISecurityManager public securityManager;

    address public pendingTreasury;
    uint256 public treasuryChangeTimestamp;

    bool public isPublicCreationEnabled;
    uint256 public maxTradesPerBlock;

    // Mappings
    mapping(uint256 => OpinionStructs.AnswerHistory[]) public answerHistory;
    mapping(address => uint256) private userLastBlock;
    mapping(address => uint256) private userTradesInBlock;
    mapping(address => mapping(uint256 => uint256)) private userLastTradeBlock;

    // Categories
    string[] public categories;

    // Extensions (Simplified to string only for space)
    mapping(uint256 => mapping(string => string)) public opinionStringExtensions;
    mapping(uint256 => string[]) public opinionExtensionKeys;

    // Config
    uint96 public minimumPrice;
    uint96 public questionCreationFee;
    uint96 public initialAnswerPrice;
    uint256 public absoluteMaxPriceChange;

    // Price calculation state
    uint256 private nonce;
    mapping(uint256 => uint256) private priceMetadata;
    mapping(uint256 => uint256) private priceHistory;

    // Remaining gap (48 - 20 used = 28)
    uint256[28] private __gap;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize V2 (Re-initializer)
     */
    function initializeV2(
        address _poolManager,
        address _monitoringManager,
        address _securityManager
    ) public reinitializer(2) {
        __Pausable_init();

        poolManager = IPoolManager(_poolManager);
        if (_monitoringManager != address(0)) monitoringManager = IMonitoringManager(_monitoringManager);
        if (_securityManager != address(0)) securityManager = ISecurityManager(_securityManager);

        _grantRole(MODERATOR_ROLE, msg.sender);

        // Initialize V2 defaults
        isPublicCreationEnabled = true;
        maxTradesPerBlock = 3;
        minimumPrice = 1_000_000;
        questionCreationFee = 5_000_000;
        initialAnswerPrice = 2_000_000;
        absoluteMaxPriceChange = 200;

        // Default categories
        categories = ["Crypto", "Politics", "Science", "Technology", "Sports", "Entertainment", "Other"];
    }

    // --- CORE FUNCTIONS (V2 Implementation) ---

    function createOpinion(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) external override nonReentrant whenNotPaused {
        _createOpinionInternal(question, answer, description, "", "", initialPrice, opinionCategories);
    }

    function createOpinionWithExtras(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories,
        string calldata ipfsHash,
        string calldata link
    ) external override nonReentrant whenNotPaused {
        _createOpinionInternal(question, answer, description, ipfsHash, link, initialPrice, opinionCategories);
    }

    function _createOpinionInternal(
        string calldata question,
        string calldata answer,
        string calldata description,
        string memory ipfsHash,
        string memory link,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) internal {
        if (!isPublicCreationEnabled && !hasRole(ADMIN_ROLE, msg.sender)) revert UnauthorizedCreator();

        // Validation
        ValidationLibrary.validateOpinionCategories(opinionCategories, categories);
        ValidationLibrary.validateOpinionParams(question, answer, MAX_QUESTION_LENGTH, MAX_ANSWER_LENGTH);
        if (initialPrice < MIN_INITIAL_PRICE) revert InvalidInitialPrice();

        // Payment
        usdcToken.safeTransferFrom(msg.sender, treasury, initialPrice);

        uint256 opinionId = nextOpinionId++;

        // Create Opinion (V1 + V2 fields)
        Opinion storage op = opinions[opinionId];
        op.question = question;
        op.currentAnswer = answer;
        op.creator = msg.sender;
        op.currentOwner = msg.sender;
        op.nextPrice = uint96(_calculateNextPrice(opinionId, initialPrice));
        op.totalVolume = 0;
        op.isActive = true;

        // V2 fields
        op.questionOwner = msg.sender;
        op.lastPrice = initialPrice;
        op.currentAnswerDescription = description;
        op.link = link;
        op.categories = opinionCategories;
        op.ipfsHash = ipfsHash;
        op.description = description;

        emit OpinionCreatedLocal(opinionId, question, answer, msg.sender, initialPrice, block.timestamp);
    }

    function submitAnswer(
        uint256 opinionId,
        string calldata answer,
        string calldata description,
        string calldata link
    ) external override nonReentrant whenNotPaused {
        Opinion storage op = opinions[opinionId];
        require(op.isActive, "Not active");
        require(op.currentOwner != msg.sender, "Same owner");

        uint256 price = op.nextPrice;

        // Payment
        usdcToken.safeTransferFrom(msg.sender, address(this), price);

        // Fees
        IFeeManager feeMgr = IFeeManager(feeManagerAddress);
        (uint96 platformFee, uint96 creatorFee, uint96 ownerAmount) = feeMgr.calculateFeeDistribution(uint96(price));

        feeMgr.accumulateFee(op.creator, creatorFee);

        if (op.currentOwner == address(poolManager)) {
            poolManager.distributePoolRewards(opinionId, price, msg.sender);
        } else {
            feeMgr.accumulateFee(op.currentOwner, ownerAmount);
        }

        // Update State
        op.currentAnswer = answer;
        op.currentOwner = msg.sender;
        op.totalVolume += price;
        op.lastPrice = uint96(price);
        op.nextPrice = uint96(_calculateNextPrice(opinionId, price));

        // V2 fields
        op.currentAnswerDescription = description;
        op.link = link;

        // History
        answerHistory[opinionId].push(OpinionStructs.AnswerHistory({
            answer: answer,
            description: description,
            owner: msg.sender,
            price: uint96(price),
            timestamp: uint32(block.timestamp)
        }));

        answerCount[opinionId]++;

        emit OpinionAnsweredLocal(opinionId, answer, op.currentOwner, msg.sender, price, block.timestamp);
    }

    // --- TRADING ---

    function listQuestionForSale(uint256 opinionId, uint256 price) external override nonReentrant whenNotPaused {
        Opinion storage op = opinions[opinionId];
        require(op.questionOwner == msg.sender, "Not owner");
        op.salePrice = uint96(price);
        emit QuestionSaleAction(opinionId, 0, msg.sender, address(0), price);
    }

    function buyQuestion(uint256 opinionId) external override nonReentrant whenNotPaused {
        Opinion storage op = opinions[opinionId];
        uint256 price = op.salePrice;
        require(price > 0, "Not for sale");

        address seller = op.questionOwner;

        usdcToken.safeTransferFrom(msg.sender, address(this), price);

        uint256 fee = (price * 10) / 100;
        uint256 sellerAmount = price - fee;

        IFeeManager(feeManagerAddress).accumulateFee(seller, uint96(sellerAmount));

        op.questionOwner = msg.sender;
        op.salePrice = 0;

        emit QuestionSaleAction(opinionId, 1, seller, msg.sender, price);
    }

    function cancelQuestionSale(uint256 opinionId) external override nonReentrant whenNotPaused {
        Opinion storage op = opinions[opinionId];
        require(op.questionOwner == msg.sender, "Not owner");
        op.salePrice = 0;
        emit QuestionSaleAction(opinionId, 2, msg.sender, address(0), 0);
    }

    function moderateAnswer(uint256 opinionId, string calldata reason) external override onlyRole(MODERATOR_ROLE) {
        Opinion storage op = opinions[opinionId];
        require(op.isActive, "Not active");

        address oldOwner = op.currentOwner;
        op.currentOwner = op.creator; // Revert to creator
        op.currentAnswer = "Content Moderated";
        op.currentAnswerDescription = reason;

        emit AnswerModerated(opinionId, oldOwner, op.creator, reason, block.timestamp);
    }

    function transferAnswerOwnership(uint256 opinionId, address newOwner) external override nonReentrant whenNotPaused {
        Opinion storage op = opinions[opinionId];
        require(op.currentOwner == msg.sender, "Not owner");
        require(newOwner != address(0), "Zero address");

        op.currentOwner = newOwner;
        emit AnswerOwnershipTransferred(opinionId, msg.sender, newOwner, block.timestamp);
    }

    function updateOpinion(
        uint256 opinionId,
        string calldata question,
        string calldata ipfsHash,
        string calldata link,
        string[] calldata opinionCategories
    ) external override nonReentrant whenNotPaused {
        Opinion storage op = opinions[opinionId];
        require(op.creator == msg.sender, "Not creator");

        op.question = question;
        op.ipfsHash = ipfsHash;
        op.link = link;
        op.categories = opinionCategories;

        // Re-emit creation event for update
        emit OpinionCreatedLocal(opinionId, question, op.currentAnswer, msg.sender, 0, block.timestamp);
    }

    // --- MODERATION ---

    function deactivateOpinion(uint256 opinionId) external override onlyRole(MODERATOR_ROLE) {
        opinions[opinionId].isActive = false;
        emit OpinionStatusChanged(opinionId, false, msg.sender, block.timestamp);
    }

    function reactivateOpinion(uint256 opinionId) external override onlyRole(MODERATOR_ROLE) {
        opinions[opinionId].isActive = true;
        emit OpinionStatusChanged(opinionId, true, msg.sender, block.timestamp);
    }

    // --- POOL INTEGRATION ---

    function updateOpinionOnPoolExecution(
        uint256 opinionId,
        string calldata answer,
        address poolAddress,
        uint256 price
    ) external override onlyRole(POOL_MANAGER_ROLE) {
        Opinion storage op = opinions[opinionId];
        require(op.isActive, "Not active");

        address previousOwner = op.currentOwner;

        op.currentAnswer = answer;
        op.currentOwner = poolAddress;
        op.totalVolume += price;
        op.lastPrice = uint96(price);
        op.nextPrice = uint96(_calculateNextPrice(opinionId, price));

        answerHistory[opinionId].push(OpinionStructs.AnswerHistory({
            answer: answer,
            description: "Pool Execution",
            owner: poolAddress,
            price: uint96(price),
            timestamp: uint32(block.timestamp)
        }));

        emit OpinionAnsweredLocal(opinionId, answer, previousOwner, poolAddress, price, block.timestamp);
    }

    // --- VIEW FUNCTIONS ---

    function getOpinionDetails(uint256 opinionId) external view override returns (OpinionStructs.Opinion memory) {
        Opinion storage op = opinions[opinionId];
        return OpinionStructs.Opinion({
            creator: op.creator,
            questionOwner: op.questionOwner,
            lastPrice: op.lastPrice,
            nextPrice: uint96(op.nextPrice),
            salePrice: op.salePrice,
            isActive: op.isActive,
            question: op.question,
            currentAnswer: op.currentAnswer,
            currentAnswerDescription: op.currentAnswerDescription,
            currentAnswerOwner: op.currentOwner,
            link: op.link,
            ipfsHash: op.ipfsHash,
            totalVolume: uint96(op.totalVolume),
            categories: op.categories
        });
    }

    function getAnswerHistory(uint256 opinionId) external view override returns (OpinionStructs.AnswerHistory[] memory) {
        return answerHistory[opinionId];
    }

    function getNextPrice(uint256 opinionId) external view override returns (uint256) {
        return opinions[opinionId].nextPrice;
    }

    function getTradeCount(uint256 opinionId) external view override returns (uint256) {
        return answerCount[opinionId];
    }

    function getCreatorGain(uint256 opinionId) external view override returns (uint256) {
        // Simplified calculation
        return (opinions[opinionId].totalVolume * 1) / 100; 
    }

    function isPoolOwned(uint256 opinionId) external view override returns (bool) {
        return opinions[opinionId].currentOwner == address(poolManager);
    }

    // --- CATEGORIES ---

    function addCategoryToCategories(string calldata newCategory) external onlyRole(ADMIN_ROLE) {
        categories.push(newCategory);
    }

    function getAvailableCategories() external view returns (string[] memory) {
        return categories;
    }

    function getOpinionCategories(uint256 opinionId) external view returns (string[] memory) {
        return opinions[opinionId].categories;
    }

    function getCategoryCount() external view returns (uint256) {
        return categories.length;
    }

    // --- EXTENSIONS (Full Interface Implementation) ---

    function setOpinionStringExtension(uint256 opinionId, string calldata key, string calldata value) external override onlyRole(ADMIN_ROLE) {
        opinionStringExtensions[opinionId][key] = value;
        opinionExtensionKeys[opinionId].push(key);
        emit OpinionStringExtensionSet(opinionId, key, value);
    }

    // Stubs for other types to save space (we only use string extensions in V2)
    function setOpinionNumberExtension(uint256, string calldata, uint256) external override onlyRole(ADMIN_ROLE) { revert("Not supported in V2"); }
    function setOpinionBoolExtension(uint256, string calldata, bool) external override onlyRole(ADMIN_ROLE) { revert("Not supported in V2"); }
    function setOpinionAddressExtension(uint256, string calldata, address) external override onlyRole(ADMIN_ROLE) { revert("Not supported in V2"); }

    function getOpinionStringExtension(uint256 opinionId, string calldata key) external view override returns (string memory) {
        return opinionStringExtensions[opinionId][key];
    }

    function getOpinionNumberExtension(uint256, string calldata) external view override returns (uint256) { return 0; }
    function getOpinionBoolExtension(uint256, string calldata) external view override returns (bool) { return false; }
    function getOpinionAddressExtension(uint256, string calldata) external view override returns (address) { return address(0); }

    function hasOpinionExtension(uint256 opinionId, string calldata key) external view override returns (bool) {
        bytes32 keyHash = keccak256(bytes(key));
        string[] memory keys = opinionExtensionKeys[opinionId];
        for(uint i=0; i<keys.length; i++) {
            if(keccak256(bytes(keys[i])) == keyHash) return true;
        }
        return false;
    }

    function getOpinionExtensionCount(uint256 opinionId) external view override returns (uint256) {
        return opinionExtensionKeys[opinionId].length;
    }

    function getOpinionExtensions(uint256 opinionId) external view override returns (
        string[] memory keys,
        string[] memory stringValues,
        uint256[] memory numberValues,
        bool[] memory boolValues,
        address[] memory addressValues
    ) {
        keys = opinionExtensionKeys[opinionId];
        stringValues = new string[](keys.length);
        numberValues = new uint256[](keys.length);
        boolValues = new bool[](keys.length);
        addressValues = new address[](keys.length);

        for(uint i=0; i<keys.length; i++) {
            stringValues[i] = opinionStringExtensions[opinionId][keys[i]];
        }
        return (keys, stringValues, numberValues, boolValues, addressValues);
    }

    // --- ADMIN ---

    function setFeeManager(address _feeManager) external onlyRole(ADMIN_ROLE) {
        feeManagerAddress = _feeManager;
    }

    function setPoolManager(address _poolManager) external onlyRole(ADMIN_ROLE) {
        poolManager = IPoolManager(_poolManager);
    }

    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyRole(ADMIN_ROLE) {}

    // --- INTERNAL HELPERS ---

    function _calculateNextPrice(uint256 opinionId, uint256 lastPrice) internal returns (uint256) {
        uint256 newPrice = PriceCalculator.calculateNextPrice(
            opinionId,
            lastPrice,
            minimumPrice,
            absoluteMaxPriceChange,
            nonce++,
            priceMetadata,
            priceHistory
        );

        _updatePriceHistory(opinionId, newPrice);
        return newPrice;
    }

    function _updatePriceHistory(uint256 opinionId, uint256 newPrice) internal {
        uint256 meta = priceMetadata[opinionId];
        uint8 count = uint8(meta);

        priceMetadata[opinionId] = (block.timestamp << 8) | (count < 3 ? count + 1 : 3);

        uint256 history = priceHistory[opinionId];
        history = (history << 80) & (~uint256(0) << 160);
        history |= (newPrice & ((1 << 80) - 1));
        priceHistory[opinionId] = history;
    }

    function version() external pure virtual returns (string memory) {
        return "2.0.0";
    }
}

contracts/core/OpinionMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IOpinionCore.sol";
import "./interfaces/IFeeManager.sol";
import "./interfaces/IPoolManager.sol";
import "./interfaces/IMonitoringManager.sol";
import "./interfaces/ISecurityManager.sol";
import "./interfaces/IOpinionMarketEvents.sol";
import "./interfaces/IOpinionMarketErrors.sol";
import "./structs/OpinionStructs.sol";
import "./structs/PoolStructs.sol";

/**
 * @title OpinionMarket
 * @dev Main entry point for the OpinionMarket system, coordinating between specialized contracts
 */
contract OpinionMarket is
    Initializable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    UUPSUpgradeable,
    IOpinionMarketEvents,
    IOpinionMarketErrors
{
    using SafeERC20 for IERC20;

    // --- ROLES ---
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    bytes32 public constant TREASURY_ROLE = keccak256("TREASURY_ROLE");

    // --- STATE VARIABLES ---
    IOpinionCore public opinionCore;
    IFeeManager public feeManager;
    IPoolManager public poolManager;
    IMonitoringManager public monitoringManager;
    ISecurityManager public securityManager;
    IERC20 public usdcToken;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // --- INITIALIZATION ---
    /**
     * @dev Initializes the contract with required dependencies
     * @param _usdcToken Address of the USDC token contract
     * @param _opinionCore Address of the OpinionCore contract
     * @param _feeManager Address of the FeeManager contract
     * @param _poolManager Address of the PoolManager contract
     * @param _monitoringManager Address of the MonitoringManager contract (optional)
     * @param _securityManager Address of the SecurityManager contract (optional)
     * @param _treasury Address of the treasury that receives platform fees
     */
    function initialize(
        address _usdcToken,
        address _opinionCore,
        address _feeManager,
        address _poolManager,
        address _monitoringManager,
        address _securityManager,
        address _treasury
    ) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();
        __Pausable_init();
        __UUPSUpgradeable_init();

        // Validate required addresses
        if (
            _usdcToken == address(0) ||
            _opinionCore == address(0) ||
            _feeManager == address(0) ||
            _poolManager == address(0) ||
            _treasury == address(0)
        ) revert ZeroAddressNotAllowed();

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(MODERATOR_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, msg.sender);
        _grantRole(TREASURY_ROLE, msg.sender);

        // Set contracts
        usdcToken = IERC20(_usdcToken);
        opinionCore = IOpinionCore(_opinionCore);
        feeManager = IFeeManager(_feeManager);
        poolManager = IPoolManager(_poolManager);

        // Set optional modular contracts (can be zero address)
        if (_monitoringManager != address(0)) {
            monitoringManager = IMonitoringManager(_monitoringManager);
        }
        if (_securityManager != address(0)) {
            securityManager = ISecurityManager(_securityManager);
        }
    }

    // --- UPGRADE AUTHORIZATION ---
    /**
     * @dev Authorizes contract upgrades, restricted to admin
     */
    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyRole(ADMIN_ROLE) {}

    // --- OPINION FUNCTIONS ---
    /**
     * @dev Creates a new opinion
     * @param question The opinion question
     * @param answer The initial answer
     * @param description The answer description (optional, max 120 chars)
     * @param initialPrice The initial price chosen by creator (1-100 USDC)
     * @param opinionCategories Categories for the opinion (1-3 required)
     */
    function createOpinion(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories
    ) external nonReentrant whenNotPaused {
        opinionCore.createOpinion(question, answer, description, initialPrice, opinionCategories);
    }

    /**
     * @dev Creates a new opinion with IPFS hash and link
     * @param question The opinion question
     * @param answer The initial answer
     * @param description The answer description (optional, max 120 chars)
     * @param initialPrice The initial price chosen by creator (1-100 USDC)
     * @param opinionCategories Categories for the opinion (1-3 required)
     * @param ipfsHash The IPFS hash for an image
     * @param link The external URL link
     */
    function createOpinionWithExtras(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories,
        string calldata ipfsHash,
        string calldata link
    ) external nonReentrant whenNotPaused {
        opinionCore.createOpinionWithExtras(
            question,
            answer,
            description,
            initialPrice,
            opinionCategories,
            ipfsHash,
            link
        );
    }

    /**
     * @dev Submits a new answer to an opinion
     * @param opinionId The ID of the opinion
     * @param answer The new answer
     * @param description The answer description (optional, max 120 chars)
     */
    function submitAnswer(
        uint256 opinionId,
        string calldata answer,
        string calldata description,
        string calldata link
    ) external nonReentrant whenNotPaused {
        opinionCore.submitAnswer(opinionId, answer, description, link);
    }

    /**
     * @dev Places a question for sale
     * @param opinionId The ID of the opinion
     * @param price The sale price
     */
    function listQuestionForSale(
        uint256 opinionId,
        uint256 price
    ) external nonReentrant whenNotPaused {
        opinionCore.listQuestionForSale(opinionId, price);
    }

    /**
     * @dev Buys a question that is for sale
     * @param opinionId The ID of the opinion
     */
    function buyQuestion(
        uint256 opinionId
    ) external nonReentrant whenNotPaused {
        opinionCore.buyQuestion(opinionId);
    }

    /**
     * @dev Cancels a question sale
     * @param opinionId The ID of the opinion
     */
    function cancelQuestionSale(
        uint256 opinionId
    ) external nonReentrant whenNotPaused {
        opinionCore.cancelQuestionSale(opinionId);
    }

    /**
     * @dev Deactivates an opinion
     * @param opinionId The ID of the opinion to deactivate
     */
    function deactivateOpinion(
        uint256 opinionId
    ) external onlyRole(MODERATOR_ROLE) {
        opinionCore.deactivateOpinion(opinionId);
    }

    /**
     * @dev Reactivates a previously deactivated opinion
     * @param opinionId The ID of the opinion to reactivate
     */
    function reactivateOpinion(
        uint256 opinionId
    ) external onlyRole(MODERATOR_ROLE) {
        opinionCore.reactivateOpinion(opinionId);
    }

    // --- CATEGORIES FUNCTIONS ---
    /**
     * @dev Adds a new category to available categories
     * @param newCategory The new category to add
     */
    function addCategoryToCategories(string calldata newCategory) external onlyRole(ADMIN_ROLE) {
        opinionCore.addCategoryToCategories(newCategory);
    }

    /**
     * @dev Returns all available categories
     * @return Array of available category strings
     */
    function getAvailableCategories() external view returns (string[] memory) {
        return opinionCore.getAvailableCategories();
    }

    /**
     * @dev Returns categories for a specific opinion
     * @param opinionId The opinion ID
     * @return Array of category strings for the opinion
     */
    function getOpinionCategories(uint256 opinionId) external view returns (string[] memory) {
        return opinionCore.getOpinionCategories(opinionId);
    }

    // --- EXTENSION SLOTS FUNCTIONS ---

    /**
     * @dev Sets a string extension for an opinion (admin only)
     * @param opinionId Opinion ID
     * @param key Extension key
     * @param value Extension value
     */
    function setOpinionStringExtension(
        uint256 opinionId, 
        string calldata key, 
        string calldata value
    ) external onlyRole(ADMIN_ROLE) {
        opinionCore.setOpinionStringExtension(opinionId, key, value);
    }

    /**
     * @dev Sets a number extension for an opinion (admin only)
     * @param opinionId Opinion ID
     * @param key Extension key
     * @param value Extension value
     */
    function setOpinionNumberExtension(
        uint256 opinionId, 
        string calldata key, 
        uint256 value
    ) external onlyRole(ADMIN_ROLE) {
        opinionCore.setOpinionNumberExtension(opinionId, key, value);
    }

    /**
     * @dev Sets a bool extension for an opinion (admin only)
     * @param opinionId Opinion ID
     * @param key Extension key
     * @param value Extension value
     */
    function setOpinionBoolExtension(
        uint256 opinionId, 
        string calldata key, 
        bool value
    ) external onlyRole(ADMIN_ROLE) {
        opinionCore.setOpinionBoolExtension(opinionId, key, value);
    }

    /**
     * @dev Sets an address extension for an opinion (admin only)
     * @param opinionId Opinion ID
     * @param key Extension key
     * @param value Extension value
     */
    function setOpinionAddressExtension(
        uint256 opinionId, 
        string calldata key, 
        address value
    ) external onlyRole(ADMIN_ROLE) {
        opinionCore.setOpinionAddressExtension(opinionId, key, value);
    }

    /**
     * @dev Gets all extensions for an opinion
     * @param opinionId Opinion ID
     * @return keys Array of extension keys
     * @return stringValues Array of string values
     * @return numberValues Array of number values
     * @return boolValues Array of bool values
     * @return addressValues Array of address values
     */
    function getOpinionExtensions(uint256 opinionId) external view returns (
        string[] memory keys,
        string[] memory stringValues,
        uint256[] memory numberValues,
        bool[] memory boolValues,
        address[] memory addressValues
    ) {
        return opinionCore.getOpinionExtensions(opinionId);
    }

    /**
     * @dev Gets a specific string extension for an opinion
     * @param opinionId Opinion ID
     * @param key Extension key
     * @return value Extension value
     */
    function getOpinionStringExtension(uint256 opinionId, string calldata key) external view returns (string memory) {
        return opinionCore.getOpinionStringExtension(opinionId, key);
    }

    /**
     * @dev Gets a specific number extension for an opinion
     * @param opinionId Opinion ID
     * @param key Extension key
     * @return value Extension value
     */
    function getOpinionNumberExtension(uint256 opinionId, string calldata key) external view returns (uint256) {
        return opinionCore.getOpinionNumberExtension(opinionId, key);
    }

    /**
     * @dev Gets a specific bool extension for an opinion
     * @param opinionId Opinion ID
     * @param key Extension key
     * @return value Extension value
     */
    function getOpinionBoolExtension(uint256 opinionId, string calldata key) external view returns (bool) {
        return opinionCore.getOpinionBoolExtension(opinionId, key);
    }

    /**
     * @dev Gets a specific address extension for an opinion
     * @param opinionId Opinion ID
     * @param key Extension key
     * @return value Extension value
     */
    function getOpinionAddressExtension(uint256 opinionId, string calldata key) external view returns (address) {
        return opinionCore.getOpinionAddressExtension(opinionId, key);
    }

    /**
     * @dev Checks if an opinion has a specific extension key
     * @param opinionId Opinion ID
     * @param key Extension key
     * @return exists True if the key exists
     */
    function hasOpinionExtension(uint256 opinionId, string calldata key) external view returns (bool) {
        return opinionCore.hasOpinionExtension(opinionId, key);
    }

    /**
     * @dev Gets the number of extensions for an opinion
     * @param opinionId Opinion ID
     * @return count Number of extensions
     */
    function getOpinionExtensionCount(uint256 opinionId) external view returns (uint256) {
        return opinionCore.getOpinionExtensionCount(opinionId);
    }

    /**
     * @dev Returns the total number of available categories
     * @return The count of available categories
     */
    function getCategoryCount() external view returns (uint256) {
        return opinionCore.getCategoryCount();
    }

    // --- POOL FUNCTIONS ---
    /**
     * @dev Creates a new pool
     * @param opinionId The ID of the opinion
     * @param proposedAnswer The proposed answer
     * @param deadline The pool deadline
     * @param initialContribution The initial contribution
     * @param name The pool name
     * @param ipfsHash The IPFS hash for an image
     */
    function createPool(
        uint256 opinionId,
        string calldata proposedAnswer,
        uint256 deadline,
        uint256 initialContribution,
        string calldata name,
        string calldata ipfsHash
    ) external nonReentrant whenNotPaused {
        poolManager.createPool(
            opinionId,
            proposedAnswer,
            deadline,
            initialContribution,
            name,
            ipfsHash
        );
    }

    /**
     * @dev Contributes to a pool
     * @param poolId The ID of the pool
     * @param amount The contribution amount
     */
    function contributeToPool(
        uint256 poolId,
        uint256 amount
    ) external nonReentrant whenNotPaused {
        poolManager.contributeToPool(poolId, amount);
    }

    /**
     * @dev Withdraws funds from an expired pool
     * @param poolId The ID of the pool
     */
    function withdrawFromExpiredPool(
        uint256 poolId
    ) external nonReentrant whenNotPaused {
        poolManager.withdrawFromExpiredPool(poolId);
    }

    /**
     * @dev Extends a pool deadline
     * @param poolId The ID of the pool
     * @param newDeadline The new deadline
     */
    function extendPoolDeadline(
        uint256 poolId,
        uint256 newDeadline
    ) external nonReentrant whenNotPaused {
        poolManager.extendPoolDeadline(poolId, newDeadline);
    }

    /**
     * @dev Allows contributor to withdraw early from pool with 20% penalty (SECURITY FIX)
     * @param poolId Pool ID to withdraw from
     */
    function withdrawFromPoolEarly(uint256 poolId) external nonReentrant whenNotPaused {
        poolManager.withdrawFromPoolEarly(poolId);
    }

    /**
     * @dev Preview early withdrawal amounts for a user (SECURITY FIX)
     * @param poolId Pool ID
     * @param user User address
     * @return userContribution Current user contribution
     * @return penalty Total penalty amount (20% - enhanced)
     * @return userWillReceive Amount user will receive (80%)
     * @return canWithdraw Whether withdrawal is currently possible
     */
    function getEarlyWithdrawalPreview(uint256 poolId, address user) external view returns (
        uint96 userContribution,
        uint96 penalty,
        uint96 userWillReceive,
        bool canWithdraw
    ) {
        return poolManager.getEarlyWithdrawalPreview(poolId, user);
    }

    /**
     * @dev Get detailed penalty breakdown for early withdrawal (SECURITY FIX)
     * @param poolId Pool ID  
     * @param user User address
     * @return userContribution Current user contribution
     * @return totalPenalty Total penalty (20% - enhanced)
     * @return treasuryReceives Treasury receives (100% of penalty)
     * @return userReceives Amount user receives (80%)
     */
    function getEarlyWithdrawalBreakdown(uint256 poolId, address user) external view returns (
        uint96 userContribution,
        uint96 totalPenalty,
        uint96 treasuryReceives,
        uint96 userReceives
    ) {
        return poolManager.getEarlyWithdrawalBreakdown(poolId, user);
    }

    /**
     * @dev Check if early withdrawal is possible for a user
     * @param poolId Pool ID
     * @param user User address
     * @return possible Whether early withdrawal is possible
     * @return reason Reason if not possible (0=possible, 1=invalid_pool, 2=not_active, 3=deadline_passed, 4=no_contribution)
     */
    function canWithdrawEarly(uint256 poolId, address user) external view returns (bool possible, uint8 reason) {
        return poolManager.canWithdrawEarly(poolId, user);
    }

    // --- FEE FUNCTIONS ---
    /**
     * @dev Allows users to claim their accumulated fees
     */
    function claimAccumulatedFees() external nonReentrant whenNotPaused {
        feeManager.claimAccumulatedFees();
    }

    /**
     * @dev Withdraws platform fees, restricted to TREASURY_ROLE
     * @param token Token to withdraw
     * @param recipient Address to send tokens to
     */
    function withdrawPlatformFees(
        address token,
        address recipient
    ) external nonReentrant onlyRole(TREASURY_ROLE) {
        feeManager.withdrawPlatformFees(token, recipient);
    }

    // --- VIEW FUNCTIONS ---
    /**
     * @dev Returns the answer history for an opinion
     * @param opinionId The ID of the opinion
     * @return History array of answers
     */
    function getAnswerHistory(
        uint256 opinionId
    ) external view returns (OpinionStructs.AnswerHistory[] memory) {
        return opinionCore.getAnswerHistory(opinionId);
    }

    /**
     * @dev Returns the next price for submitting an answer
     * @param opinionId The ID of the opinion
     * @return The next price
     */
    function getNextPrice(uint256 opinionId) external view returns (uint256) {
        return opinionCore.getNextPrice(opinionId);
    }

    /**
     * @dev Returns detailed information about an opinion
     * @param opinionId The ID of the opinion
     * @return Opinion details
     */
    function getOpinionDetails(
        uint256 opinionId
    ) external view returns (OpinionStructs.Opinion memory) {
        return opinionCore.getOpinionDetails(opinionId);
    }

    /**
     * @dev Returns detailed information about a pool
     * @param poolId The ID of the pool
     * @return info Pool details
     * @return currentPrice Current price to execute the pool
     * @return remainingAmount Amount needed to reach execution price
     * @return timeRemaining Time until pool expiration
     */
    function getPoolDetails(
        uint256 poolId
    )
        external
        view
        returns (
            PoolStructs.PoolInfo memory info,
            uint256 currentPrice,
            uint256 remainingAmount,
            uint256 timeRemaining
        )
    {
        return poolManager.getPoolDetails(poolId);
    }

    /**
     * @dev Returns all contributor addresses for a pool
     * @param poolId The ID of the pool
     * @return Array of contributor addresses
     */
    function getPoolContributors(
        uint256 poolId
    ) external view returns (address[] memory) {
        return poolManager.getPoolContributors(poolId);
    }

    /**
     * @dev Returns accumulated fees for a user
     * @param user User address
     * @return Accumulated fees
     */
    function getAccumulatedFees(address user) external view returns (uint96) {
        return feeManager.getAccumulatedFees(user);
    }

    /**
     * @dev Returns total accumulated fees
     * @return Total accumulated fees
     */
    function getTotalAccumulatedFees() external view returns (uint96) {
        return feeManager.getTotalAccumulatedFees();
    }

    // --- ADMIN FUNCTIONS ---
    /**
     * @dev Updates the OpinionCore contract address
     * @param _opinionCore New OpinionCore address
     */
    function setOpinionCore(
        address _opinionCore
    ) external onlyRole(ADMIN_ROLE) {
        if (_opinionCore == address(0)) revert ZeroAddressNotAllowed();
        opinionCore = IOpinionCore(_opinionCore);
        emit ContractAddressUpdated(0, _opinionCore);
    }

    /**
     * @dev Updates the FeeManager contract address
     * @param _feeManager New FeeManager address
     */
    function setFeeManager(address _feeManager) external onlyRole(ADMIN_ROLE) {
        if (_feeManager == address(0)) revert ZeroAddressNotAllowed();
        feeManager = IFeeManager(_feeManager);
        emit ContractAddressUpdated(1, _feeManager);
    }

    /**
     * @dev Updates the PoolManager contract address
     * @param _poolManager New PoolManager address
     */
    function setPoolManager(
        address _poolManager
    ) external onlyRole(ADMIN_ROLE) {
        if (_poolManager == address(0)) revert ZeroAddressNotAllowed();
        poolManager = IPoolManager(_poolManager);
        emit ContractAddressUpdated(2, _poolManager);
    }

    /**
     * @dev Updates the MonitoringManager contract address
     * @param _monitoringManager New MonitoringManager address (can be zero to disable)
     */
    function setMonitoringManager(
        address _monitoringManager
    ) external onlyRole(ADMIN_ROLE) {
        monitoringManager = _monitoringManager != address(0) ? IMonitoringManager(_monitoringManager) : IMonitoringManager(address(0));
        emit ContractAddressUpdated(3, _monitoringManager);
    }

    /**
     * @dev Updates the SecurityManager contract address
     * @param _securityManager New SecurityManager address (can be zero to disable)
     */
    function setSecurityManager(
        address _securityManager
    ) external onlyRole(ADMIN_ROLE) {
        securityManager = _securityManager != address(0) ? ISecurityManager(_securityManager) : ISecurityManager(address(0));
        emit ContractAddressUpdated(4, _securityManager);
    }

    /**
     * @dev Toggles public creation of opinions
     */
    function togglePublicCreation() external onlyRole(ADMIN_ROLE) {
        // Call the OpinionCore contract
        (bool success, ) = address(opinionCore).call(
            abi.encodeWithSignature("togglePublicCreation()")
        );
        require(success, "Failed to toggle public creation");
    }

    // --- MODULAR CONFIGURATION FUNCTIONS ---
    /**
     * @dev Sets the monitoring manager in OpinionCore
     * @param _monitoringManager MonitoringManager address
     */
    function setOpinionCoreMonitoringManager(address _monitoringManager) external onlyRole(ADMIN_ROLE) {
        (bool success, ) = address(opinionCore).call(
            abi.encodeWithSignature("setMonitoringManager(address)", _monitoringManager)
        );
        require(success, "Failed to set monitoring manager in OpinionCore");
    }

    /**
     * @dev Sets the security manager in OpinionCore
     * @param _securityManager SecurityManager address
     */
    function setOpinionCoreSecurityManager(address _securityManager) external onlyRole(ADMIN_ROLE) {
        (bool success, ) = address(opinionCore).call(
            abi.encodeWithSignature("setSecurityManager(address)", _securityManager)
        );
        require(success, "Failed to set security manager in OpinionCore");
    }

    /**
     * @dev Enables or disables enhanced monitoring in MonitoringManager
     * @param enabled Whether to enable enhanced monitoring
     */
    function setEnhancedMonitoring(bool enabled) external onlyRole(ADMIN_ROLE) {
        if (address(monitoringManager) != address(0)) {
            (bool success, ) = address(monitoringManager).call(
                abi.encodeWithSignature("setEnhancedMonitoringEnabled(bool)", enabled)
            );
            require(success, "Failed to set enhanced monitoring");
        }
    }

    /**
     * @dev Enables or disables bot detection in SecurityManager
     * @param enabled Whether to enable bot detection
     */
    function setBotDetection(bool enabled) external onlyRole(ADMIN_ROLE) {
        if (address(securityManager) != address(0)) {
            (bool success, ) = address(securityManager).call(
                abi.encodeWithSignature("setBotDetectionEnabled(bool)", enabled)
            );
            require(success, "Failed to set bot detection");
        }
    }

    // --- TREASURY MANAGEMENT ---
    /**
     * @dev Sets a new treasury address with timelock protection in OpinionCore
     * @param newTreasury The new treasury address to set after timelock
     */
    function setTreasuryOpinionCore(address newTreasury) external onlyRole(ADMIN_ROLE) {
        (bool success, ) = address(opinionCore).call(
            abi.encodeWithSignature("setTreasury(address)", newTreasury)
        );
        require(success, "Failed to set treasury in OpinionCore");
    }

    /**
     * @dev Confirms the treasury change after timelock period in OpinionCore
     */
    function confirmTreasuryChangeOpinionCore() external onlyRole(ADMIN_ROLE) {
        (bool success, ) = address(opinionCore).call(
            abi.encodeWithSignature("confirmTreasuryChange()")
        );
        require(success, "Failed to confirm treasury change in OpinionCore");
    }

    /**
     * @dev Sets a new treasury address with timelock protection in FeeManager
     * @param newTreasury The new treasury address to set after timelock
     */
    function setTreasuryFeeManager(address newTreasury) external onlyRole(ADMIN_ROLE) {
        (bool success, ) = address(feeManager).call(
            abi.encodeWithSignature("setTreasury(address)", newTreasury)
        );
        require(success, "Failed to set treasury in FeeManager");
    }

    /**
     * @dev Confirms the treasury change after timelock period in FeeManager
     */
    function confirmTreasuryChangeFeeManager() external onlyRole(ADMIN_ROLE) {
        (bool success, ) = address(feeManager).call(
            abi.encodeWithSignature("confirmTreasuryChange()")
        );
        require(success, "Failed to confirm treasury change in FeeManager");
    }

    /**
     * @dev Pauses all contracts
     */
    function pause() external onlyRole(OPERATOR_ROLE) {
        _pause();

        // Try to pause all component contracts
        (bool success1, ) = address(opinionCore).call(
            abi.encodeWithSignature("pause()")
        );
        (bool success2, ) = address(feeManager).call(
            abi.encodeWithSignature("pause()")
        );
        (bool success3, ) = address(poolManager).call(
            abi.encodeWithSignature("pause()")
        );

        // Try to pause modular contracts if they exist
        if (address(monitoringManager) != address(0)) {
            (bool success4, ) = address(monitoringManager).call(
                abi.encodeWithSignature("pause()")
            );
        }
        if (address(securityManager) != address(0)) {
            (bool success5, ) = address(securityManager).call(
                abi.encodeWithSignature("pause()")
            );
        }

        // Continue even if some fail (they might not all have pause functionality)
    }

    /**
     * @dev Unpauses all contracts
     */
    function unpause() external onlyRole(OPERATOR_ROLE) {
        _unpause();

        // Try to unpause all component contracts
        (bool success1, ) = address(opinionCore).call(
            abi.encodeWithSignature("unpause()")
        );
        (bool success2, ) = address(feeManager).call(
            abi.encodeWithSignature("unpause()")
        );
        (bool success3, ) = address(poolManager).call(
            abi.encodeWithSignature("unpause()")
        );

        // Try to unpause modular contracts if they exist
        if (address(monitoringManager) != address(0)) {
            (bool success4, ) = address(monitoringManager).call(
                abi.encodeWithSignature("unpause()")
            );
        }
        if (address(securityManager) != address(0)) {
            (bool success5, ) = address(securityManager).call(
                abi.encodeWithSignature("unpause()")
            );
        }

        // Continue even if some fail
    }

    /**
     * @dev Emergency withdraw from all contracts
     * @param token Token to withdraw
     */
    function emergencyWithdraw(
        address token
    ) external nonReentrant whenPaused onlyRole(ADMIN_ROLE) {
        if (token == address(0)) revert ZeroAddressNotAllowed();

        // Withdraw from OpinionCore
        (bool success1, ) = address(opinionCore).call(
            abi.encodeWithSignature("emergencyWithdraw(address)", token)
        );

        // Withdraw from FeeManager
        (bool success2, ) = address(feeManager).call(
            abi.encodeWithSignature("emergencyWithdraw(address)", token)
        );

        // Withdraw from PoolManager
        (bool success3, ) = address(poolManager).call(
            abi.encodeWithSignature("emergencyWithdraw(address)", token)
        );

        // Withdraw from modular contracts if they exist
        if (address(monitoringManager) != address(0)) {
            (bool success4, ) = address(monitoringManager).call(
                abi.encodeWithSignature("emergencyWithdraw(address)", token)
            );
        }
        if (address(securityManager) != address(0)) {
            (bool success5, ) = address(securityManager).call(
                abi.encodeWithSignature("emergencyWithdraw(address)", token)
            );
        }

        // Withdraw from this contract
        IERC20 tokenContract = IERC20(token);
        uint256 balance = tokenContract.balanceOf(address(this));
        if (balance > 0) {
            tokenContract.safeTransfer(msg.sender, balance);
        }

        emit AdminAction(0, msg.sender, bytes32(0), balance);
    }
}

contracts/core/PoolManager.sol.content:
// PoolManager.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IPoolManager.sol";
import "./interfaces/IOpinionCore.sol";
import "./interfaces/IFeeManager.sol";
import "./interfaces/IOpinionMarketEvents.sol";
import "./interfaces/IOpinionMarketErrors.sol";
import "./structs/PoolStructs.sol";
import "./libraries/PoolLibrary.sol";
import "./libraries/ValidationLibrary.sol";

/**
 * @title PoolManager
 * @dev Manages pools for collective funding of answer changes
 */
contract PoolManager is
    Initializable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable,
    IPoolManager,
    IOpinionMarketEvents,
    IOpinionMarketErrors
{
    using SafeERC20 for IERC20;

    // --- STATE VARIABLES ---
    // Core references
    IOpinionCore public opinionCore;
    IFeeManager public feeManager;
    IERC20 public usdcToken;
    address public treasury;

    // Pool configuration
    uint96 public poolCreationFee;
    uint96 public poolContributionFee;
    uint32 public minPoolDuration;
    uint32 public maxPoolDuration;

    // Constants
    uint256 public constant MAX_POOL_NAME_LENGTH = 30;
    uint256 public constant MAX_IPFS_HASH_LENGTH = 64;

    // Pool storage
    uint256 public poolCount;
    mapping(uint256 => PoolStructs.PoolInfo) public pools;
    mapping(uint256 => PoolStructs.PoolContribution[])
        private poolContributions;
    mapping(uint256 => mapping(address => uint96))
        public poolContributionAmounts;
    mapping(uint256 => address[]) public poolContributors;
    mapping(uint256 => uint256[]) public opinionPools;
    mapping(address => uint256[]) public userPools;

    // --- ROLES ---
    // --- ROLES ---
    /**
     * @dev Administrative role for the pool management system
     * Accounts with this role can:
     * - Configure pool creation and contribution fees
     * - Set minimum/maximum amounts for pool contributions
     * - Update global pool parameters and thresholds
     * - Configure time-based settings (deadlines, extension periods)
     * - Enable or disable specific pool types
     * - Define pool validation rules
     * - Grant or revoke other roles in this contract
     * - Update contract integrations and dependencies
     */
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    /**
     * @dev Moderation role for pool operations
     * Accounts with this role can:
     * - Extend pool deadlines in exceptional cases
     * - Cancel problematic or invalid pools
     * - Force finalize pools under specific conditions
     * - Moderate pool metadata and descriptions
     * - Handle dispute resolution for pools
     * - Cannot modify system parameters (requires ADMIN_ROLE)
     * - Typically assigned to trusted community moderators or governance participants
     */
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initializes the pool manager
     */
    function initialize(
        address _opinionCore,
        address _feeManager,
        address _usdcToken,
        address _treasury,
        address _admin
    ) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();

        // Set contract references
        ValidationLibrary.validateAddress(_opinionCore);
        ValidationLibrary.validateAddress(_feeManager);
        ValidationLibrary.validateAddress(_usdcToken);
        ValidationLibrary.validateAddress(_treasury);
        ValidationLibrary.validateAddress(_admin);

        opinionCore = IOpinionCore(_opinionCore);
        feeManager = IFeeManager(_feeManager);
        usdcToken = IERC20(_usdcToken);
        treasury = _treasury;

        // Set initial configuration
        poolCreationFee = 5 * 10 ** 6; // 5 USDC
        poolContributionFee = 1 * 10 ** 6; // 1 USDC
        minPoolDuration = 1 days;
        maxPoolDuration = 30 days;

        // Setup access control
        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);
        _grantRole(MODERATOR_ROLE, _admin);
    }

    /**
     * @dev Creates a new pool to collectively fund an answer change
     */
    function createPool(
        uint256 opinionId,
        string calldata proposedAnswer,
        uint256 deadline,
        uint256 initialContribution,
        string calldata name,
        string calldata ipfsHash
    ) external override nonReentrant {
        // Get current answer
        OpinionStructs.Opinion memory opinion = opinionCore.getOpinionDetails(
            opinionId
        );

        // Check NextPrice >= 100 USDC requirement for pool creation
        uint256 nextPrice = opinionCore.getNextPrice(opinionId);
        if (nextPrice < 100_000_000) { // 100 USDC (6 decimals)
            revert PoolNextPriceTooLow(nextPrice, 100_000_000);
        }

        // Validate parameters
        PoolStructs.PoolCreationParams memory params = PoolStructs
            .PoolCreationParams({
                opinionId: opinionId,
                proposedAnswer: proposedAnswer,
                deadline: uint32(deadline),
                initialContribution: uint96(initialContribution),
                name: name,
                ipfsHash: ipfsHash
            });

        PoolLibrary.validatePoolCreationParams(
            params,
            minPoolDuration,
            maxPoolDuration,
            1_000_000, // Minimum 1 USDC
            block.timestamp,
            MAX_POOL_NAME_LENGTH,
            50, // Max answer length from opinion core
            MAX_POOL_NAME_LENGTH,
            MAX_IPFS_HASH_LENGTH,
            opinion.currentAnswer
        );

        // Calculate total required amount
        uint96 totalRequired = poolCreationFee + uint96(initialContribution);

        // Check allowance
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < totalRequired)
            revert InsufficientAllowance(totalRequired, allowance);

        // Create the pool and get pool ID
        uint256 poolId = _createPoolRecord(
            opinionId,
            proposedAnswer,
            deadline,
            initialContribution,
            name,
            ipfsHash
        );

        // Handle funds transfer
        usdcToken.safeTransferFrom(msg.sender, address(this), totalRequired);

        // Send creation fee directly to treasury (full treasury model)
        usdcToken.safeTransfer(treasury, poolCreationFee);

        // Emit creation event
        emit PoolCreated(
            poolId,
            opinionId,
            proposedAnswer,
            msg.sender,
            initialContribution,
            deadline,
            name,
            block.timestamp
        );
    }

    /**
     * @dev Allows users to contribute to an existing pool
     */
    function contributeToPool(
        uint256 poolId,
        uint256 amount
    ) external override nonReentrant {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Validate pool is active
        if (pool.status != PoolStructs.PoolStatus.Active)
            revert PoolNotActive(poolId, uint8(pool.status));

        // Check deadline
        if (block.timestamp > pool.deadline)
            revert PoolDeadlinePassed(poolId, pool.deadline);

        // Use dynamic NextPrice as target (real-time)
        uint256 opinionId = pool.opinionId;
        uint96 targetPrice = uint96(opinionCore.getNextPrice(opinionId)); //  DYNAMIC: Always current NextPrice

        // Check if pool already has enough funds
        if (pool.totalAmount >= targetPrice) revert PoolAlreadyFunded(poolId);

        // Calculate maximum allowed contribution
        uint96 maxAllowed = targetPrice - pool.totalAmount;

        // Adjust contribution if needed
        uint96 actualAmount = amount > maxAllowed ? maxAllowed : uint96(amount);

        // Ensure non-zero contribution
        if (actualAmount == 0) revert PoolContributionTooLow(actualAmount, 1);

        // No contribution fee anymore - just transfer the contribution amount
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < actualAmount)
            revert InsufficientAllowance(actualAmount, allowance);

        // Update pool state
        _updatePoolForContribution(poolId, actualAmount);

        // Transfer funds (no fee)
        usdcToken.safeTransferFrom(msg.sender, address(this), actualAmount);

        // Emit contribution event
        emit PoolContribution(
            poolId,
            opinionId,
            msg.sender,
            actualAmount,
            pool.totalAmount,
            block.timestamp
        );

        // Check if pool is ready to execute
        _checkAndExecutePoolIfReady(poolId);
    }

    /**
     * @dev Complete a pool by contributing the exact remaining amount
     * @param poolId Pool ID to complete
     */
    function completePool(uint256 poolId) external nonReentrant {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Validate pool is active
        if (pool.status != PoolStructs.PoolStatus.Active)
            revert PoolNotActive(poolId, uint8(pool.status));

        // Check deadline
        if (block.timestamp > pool.deadline)
            revert PoolDeadlinePassed(poolId, pool.deadline);

        // Calculate exact remaining amount using current NextPrice
        uint96 targetPrice = uint96(opinionCore.getNextPrice(pool.opinionId));
        if (pool.totalAmount >= targetPrice) revert PoolAlreadyFunded(poolId);

        uint96 remainingAmount = targetPrice - pool.totalAmount;

        //  FIX: If remaining amount is tiny (< 0.01 USDC), allow free completion
        // This handles precision issues from bonding curve calculations
        if (remainingAmount > 0 && remainingAmount < 10000) { // < 0.01 USDC
            // Micro-amount completion - no additional payment needed
            pool.totalAmount = targetPrice; // Set to exact target

            emit PoolContribution(
                poolId,
                pool.opinionId,
                msg.sender,
                remainingAmount, // Show the micro-amount as contributed
                pool.totalAmount,
                block.timestamp
            );

            // Pool should be ready to execute now
            _checkAndExecutePoolIfReady(poolId);
            return;
        }

        // Ensure there's actually a meaningful amount to contribute
        if (remainingAmount == 0) revert PoolAlreadyFunded(poolId);

        // No contribution fee anymore - just transfer the remaining amount
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < remainingAmount)
            revert InsufficientAllowance(remainingAmount, allowance);

        // Update pool state
        _updatePoolForContribution(poolId, remainingAmount);

        // Transfer funds (no fee)
        usdcToken.safeTransferFrom(msg.sender, address(this), remainingAmount);

        // Emit contribution event
        emit PoolContribution(
            poolId,
            pool.opinionId,
            msg.sender,
            remainingAmount,
            pool.totalAmount,
            block.timestamp
        );

        // Pool should be ready to execute now
        _checkAndExecutePoolIfReady(poolId);
    }

    /**
     * @dev Allows contributor to withdraw from an expired pool
     */
    function withdrawFromExpiredPool(
        uint256 poolId
    ) external override nonReentrant {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Check if already expired or needs to be marked
        bool isExpired = pool.status == PoolStructs.PoolStatus.Expired;
        if (!isExpired) {
            // If not already marked, check if it should be
            isExpired = block.timestamp > pool.deadline;

            if (isExpired) {
                // Update status if expired
                pool.status = PoolStructs.PoolStatus.Expired;

                // Emit expiry event
                emit PoolExpired(
                    poolId,
                    pool.opinionId,
                    pool.totalAmount,
                    poolContributors[poolId].length,
                    block.timestamp
                );
            }
        }

        // Only allow withdrawals from expired pools
        if (!isExpired) revert PoolNotExpired(poolId, pool.deadline);

        // Check user contribution
        uint96 userContribution = poolContributionAmounts[poolId][msg.sender];
        if (userContribution == 0)
            revert PoolNoContribution(poolId, msg.sender);

        // Reset contribution before transfer
        poolContributionAmounts[poolId][msg.sender] = 0;

        // Transfer funds back to contributor
        usdcToken.safeTransfer(msg.sender, userContribution);

        // Emit refund event
        emit PoolRefund(poolId, msg.sender, userContribution, block.timestamp);
    }

    /**
     * @dev Extends the deadline of a pool
     */
    function extendPoolDeadline(
        uint256 poolId,
        uint256 newDeadline
    ) external override nonReentrant {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Ensure pool can be extended
        bool canExtend = pool.status == PoolStructs.PoolStatus.Active ||
            (pool.status == PoolStructs.PoolStatus.Expired &&
                block.timestamp <= pool.deadline + 7 days);

        if (!canExtend) revert("Pool cannot be extended");

        // Validate new deadline
        if (newDeadline <= pool.deadline) revert("New deadline must be later");

        if (newDeadline > block.timestamp + maxPoolDuration)
            revert PoolDeadlineTooLong(uint256(newDeadline), maxPoolDuration);

        // Update pool deadline and status
        pool.deadline = uint32(newDeadline);
        pool.status = PoolStructs.PoolStatus.Extended;

        // Emit deadline extension event
        emit PoolExtended(
            poolId,
            pool.opinionId,
            msg.sender,
            newDeadline,
            block.timestamp
        );
    }

    /**
     * @dev Checks if a pool has expired and updates its status
     */
    function checkPoolExpiry(uint256 poolId) external override returns (bool) {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Only check active pools
        if (pool.status != PoolStructs.PoolStatus.Active) {
            return pool.status == PoolStructs.PoolStatus.Expired;
        }

        // Check if deadline has passed
        if (block.timestamp > pool.deadline) {
            pool.status = PoolStructs.PoolStatus.Expired;

            // Emit expiry event
            emit PoolExpired(
                poolId,
                pool.opinionId,
                pool.totalAmount,
                poolContributors[poolId].length,
                block.timestamp
            );

            return true;
        }

        return false;
    }

    /**
     * @dev Distributes rewards when a pool-owned answer is purchased
     */
    function distributePoolRewards(
        uint256 opinionId,
        uint256 purchasePrice,
        address buyer
    ) external override {
        // Only allow calls from the Opinion Core contract
        require(msg.sender == address(opinionCore), "Unauthorized caller");

        // Find the pool that owns this answer
        uint256[] memory poolsForOpinion = opinionPools[opinionId];
        uint256 ownerPoolId;
        bool foundPool = false;

        for (uint256 i = 0; i < poolsForOpinion.length; i++) {
            uint256 poolId = poolsForOpinion[i];
            PoolStructs.PoolInfo storage pool = pools[poolId];

            // Check if this pool owns the answer (is executed and has matching answer)
            if (pool.status == PoolStructs.PoolStatus.Executed) {
                OpinionStructs.Opinion memory opinion = opinionCore
                    .getOpinionDetails(opinionId);
                if (
                    keccak256(bytes(pool.proposedAnswer)) ==
                    keccak256(bytes(opinion.currentAnswer))
                ) {
                    ownerPoolId = poolId;
                    foundPool = true;
                    break;
                }
            }
        }

        if (!foundPool) return;

        // Calculate reward amount (after platform and creator fees)
        (uint96 platformFee, uint96 creatorFee, ) = feeManager
            .calculateFeeDistribution(purchasePrice);
        uint96 rewardAmount = uint96(purchasePrice) - platformFee - creatorFee;

        // Get pool contributors and distribute rewards
        address[] memory contributors = poolContributors[ownerPoolId];
        uint96 totalContributed = pools[ownerPoolId].totalAmount;

        for (uint256 i = 0; i < contributors.length; i++) {
            address contributor = contributors[i];
            uint96 contribution = poolContributionAmounts[ownerPoolId][
                contributor
            ];

            if (contribution > 0) {
                // Calculate contributor's share
                uint256 sharePercent = (uint256(contribution) * 100) /
                    totalContributed;
                uint96 reward = uint96(
                    (uint256(rewardAmount) * sharePercent) / 100
                );

                // Accumulate reward
                feeManager.accumulateFee(contributor, reward);

                // Emit reward event
                emit PoolRewardDistributed(
                    ownerPoolId,
                    opinionId,
                    contributor,
                    contribution,
                    sharePercent,
                    reward,
                    block.timestamp
                );
            }
        }
    }

    /**
     * @dev Executes a pool if ready (reached target price)
     */
    function executePoolIfReady(
        uint256 poolId,
        uint256 opinionId
    ) external override {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        // Only allow calls from within contract or Opinion Core
        require(
            msg.sender == address(this) || msg.sender == address(opinionCore),
            "Unauthorized caller"
        );

        _checkAndExecutePoolIfReady(poolId);
    }

    /**
     * @dev Returns detailed information about a pool
     */
    function getPoolDetails(
        uint256 poolId
    )
        external
        view
        override
        returns (
            PoolStructs.PoolInfo memory info,
            uint256 currentPrice,
            uint256 remainingAmount,
            uint256 timeRemaining
        )
    {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        info = pools[poolId];

        // Use dynamic NextPrice as target (real-time)
        currentPrice = opinionCore.getNextPrice(info.opinionId); //  DYNAMIC: Current NextPrice

        // Calculate remaining amount needed
        if (info.totalAmount >= currentPrice) {
            remainingAmount = 0;
        } else {
            remainingAmount = currentPrice - info.totalAmount;
        }

        // Calculate time remaining
        if (block.timestamp >= info.deadline) {
            timeRemaining = 0;
        } else {
            timeRemaining = info.deadline - block.timestamp;
        }
    }

    /**
     * @dev Returns all contributor addresses for a pool
     */
    function getPoolContributors(
        uint256 poolId
    ) external view override returns (address[] memory) {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);
        return poolContributors[poolId];
    }

    /**
     * @dev Returns all pools for a specific opinion
     */
    function getOpinionPools(
        uint256 opinionId
    ) external view override returns (uint256[] memory) {
        return opinionPools[opinionId];
    }

    /**
     * @dev Returns reward information for a pool
     */
    function getPoolRewardInfo(
        uint256 poolId
    )
        external
        view
        override
        returns (
            address[] memory contributors,
            uint96[] memory amounts,
            uint96 totalAmount
        )
    {
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        contributors = poolContributors[poolId];
        amounts = new uint96[](contributors.length);

        for (uint256 i = 0; i < contributors.length; i++) {
            amounts[i] = poolContributionAmounts[poolId][contributors[i]];
        }

        totalAmount = pools[poolId].totalAmount;
    }

    // --- INTERNAL FUNCTIONS ---

    /**
     * @dev Creates a new pool record
     */
    function _createPoolRecord(
        uint256 opinionId,
        string memory proposedAnswer,
        uint256 deadline,
        uint256 initialContribution,
        string memory name,
        string memory ipfsHash
    ) internal returns (uint256) {
        uint256 poolId = poolCount++;
        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Initialize pool
        pool.id = poolId;
        pool.opinionId = opinionId;
        pool.proposedAnswer = proposedAnswer;
        pool.totalAmount = uint96(initialContribution);
        pool.targetPrice = uint96(opinionCore.getNextPrice(opinionId)); //  INITIAL: Store initial NextPrice (for reference only)
        pool.deadline = uint32(deadline);
        pool.creator = msg.sender;
        pool.status = PoolStructs.PoolStatus.Active;
        pool.name = name;
        pool.ipfsHash = ipfsHash;

        // Track initial contribution
        poolContributions[poolId].push(
            PoolStructs.PoolContribution({
                contributor: msg.sender,
                amount: uint96(initialContribution),
                timestamp: uint32(block.timestamp)
            })
        );

        poolContributionAmounts[poolId][msg.sender] = uint96(
            initialContribution
        );
        poolContributors[poolId].push(msg.sender);

        // Update mappings
        opinionPools[opinionId].push(poolId);
        userPools[msg.sender].push(poolId);

        return poolId;
    }

    /**
     * @dev Updates pool state for a contribution
     */
    function _updatePoolForContribution(
        uint256 poolId,
        uint96 amount
    ) internal {
        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Update pool state
        if (poolContributionAmounts[poolId][msg.sender] == 0) {
            // First contribution from this user
            poolContributors[poolId].push(msg.sender);
            userPools[msg.sender].push(poolId);
        }

        // Record contribution
        poolContributions[poolId].push(
            PoolStructs.PoolContribution({
                contributor: msg.sender,
                amount: amount,
                timestamp: uint32(block.timestamp)
            })
        );

        poolContributionAmounts[poolId][msg.sender] += amount;
        pool.totalAmount += amount;
    }

    /**
     * @dev Checks if pool has reached target price and executes if so
     *  FIX: Added completion tolerance to handle precision issues
     */
    function _checkAndExecutePoolIfReady(uint256 poolId) internal {
        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Only execute active pools
        if (pool.status != PoolStructs.PoolStatus.Active) {
            return;
        }

        uint256 opinionId = pool.opinionId;
        uint96 currentNextPrice = uint96(opinionCore.getNextPrice(opinionId)); //  DYNAMIC: Current NextPrice
        uint96 currentAmount = pool.totalAmount;

        //  DYNAMIC: Execute if we have enough funds for CURRENT NextPrice
        // Allow 0.01% tolerance for precision issues
        uint96 tolerance = currentNextPrice / 10000; // 0.01% tolerance
        if (tolerance < 1) tolerance = 1; // Minimum 1 wei tolerance

        // Execute if we're within tolerance of CURRENT NextPrice
        if (currentAmount >= currentNextPrice - tolerance) {
            _executePool(poolId, opinionId, currentNextPrice);
        }
    }

    /**
     * @dev Executes a pool when it has reached the target price
     */
    function _executePool(
        uint256 poolId,
        uint256 opinionId,
        uint96 targetPrice
    ) internal {
        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Double-check status
        if (pool.status != PoolStructs.PoolStatus.Active)
            revert PoolNotActive(poolId, uint8(pool.status));

        // Double-check funds against CURRENT NextPrice (dynamic)
        uint96 currentNextPrice = uint96(opinionCore.getNextPrice(opinionId));
        if (pool.totalAmount < currentNextPrice)
            revert PoolInsufficientFunds(pool.totalAmount, currentNextPrice);

        // Get current answer owner before execution
        OpinionStructs.Opinion memory opinion = opinionCore.getOpinionDetails(
            opinionId
        );
        address currentOwner = opinion.currentAnswerOwner;

        // Update opinion through core contract using CURRENT NextPrice
        opinionCore.updateOpinionOnPoolExecution(
            opinionId,
            pool.proposedAnswer,
            address(this), // Pool address as owner
            targetPrice // This is now the current NextPrice, not fixed
        );

        // Update pool status
        pool.status = PoolStructs.PoolStatus.Executed;

        // Emit execution event
        emit PoolExecuted(
            poolId,
            opinionId,
            pool.proposedAnswer,
            targetPrice,
            block.timestamp
        );
    }

    // --- ADMIN FUNCTIONS ---

    /**
     * @dev Sets pool creation fee
     */
    function setPoolCreationFee(uint96 newFee) external onlyRole(ADMIN_ROLE) {
        poolCreationFee = newFee;
        emit ParameterUpdated(
            8,
            poolCreationFee,
            newFee,
            msg.sender,
            block.timestamp
        );
    }

    /**
     * @dev Sets pool contribution fee
     */
    function setPoolContributionFee(
        uint96 newFee
    ) external onlyRole(ADMIN_ROLE) {
        poolContributionFee = newFee;
        emit ParameterUpdated(
            9,
            poolContributionFee,
            newFee,
            msg.sender,
            block.timestamp
        );
    }

    /**
     * @dev Sets minimum pool duration
     */
    function setMinPoolDuration(
        uint32 newDuration
    ) external onlyRole(ADMIN_ROLE) {
        minPoolDuration = newDuration;
        emit ParameterUpdated(
            10,
            minPoolDuration,
            newDuration,
            msg.sender,
            block.timestamp
        );
    }

    /**
     * @dev Sets maximum pool duration
     */
    function setMaxPoolDuration(
        uint32 newDuration
    ) external onlyRole(ADMIN_ROLE) {
        maxPoolDuration = newDuration;
        emit ParameterUpdated(
            11,
            maxPoolDuration,
            newDuration,
            msg.sender,
            block.timestamp
        );
    }

    // --- EARLY WITHDRAWAL FEATURE ---

    /**
     * @dev Allows contributor to withdraw early from pool with 10% penalty
     * OBLIGATOIRE: Cette signature exacte
     * @param poolId Pool ID to withdraw from
     */
    function withdrawFromPoolEarly(uint256 poolId) external nonReentrant {
        // === CHECKS === (OBLIGATOIRE: Cet ordre exact de validations)

        // 1. Pool ID validation FIRST
        if (poolId >= poolCount) revert PoolInvalidPoolId(poolId);

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // 2. Pool status validation  
        if (pool.status != PoolStructs.PoolStatus.Active) revert PoolNotActive(poolId, uint8(pool.status));

        // 3. Deadline validation
        if (block.timestamp >= pool.deadline) revert PoolDeadlinePassed(poolId, pool.deadline);

        // 4. User contribution validation
        uint96 userContribution = poolContributionAmounts[poolId][msg.sender];
        if (userContribution == 0) revert PoolNoContribution(poolId, msg.sender);

        // === EFFECTS === (OBLIGATOIRE: State updates BEFORE transfers for reentrancy protection)

        // 5. Calculate penalty and splits (SECURITY FIX: Enhanced penalty, 100% treasury)
        uint96 penalty = userContribution * 20 / 100;           //  20% total penalty (enhanced deterrent)
        uint96 userAmount = userContribution - penalty;         // 80% vers user
        //  SECURITY: 100% penalty vers treasury (ZERO creator incentive)

        // 6. Update state (OBLIGATOIRE: Reset user contribution BEFORE transfers)
        poolContributionAmounts[poolId][msg.sender] = 0;

        // 7. Update pool total amount
        pool.totalAmount -= userContribution;

        // === INTERACTIONS === (SECURITY FIX: Safe transfer pattern)

        // 8. Transfer to user (80%) - Enhanced penalty applied
        usdcToken.safeTransfer(msg.sender, userAmount);

        // 9. Transfer to treasury (100% of penalty) -  SECURITY FIX: NO creator share
        if (penalty > 0) {
            usdcToken.safeTransfer(treasury, penalty);
        }

        //  REMOVED: All creator penalty sharing logic eliminated
        //  SECURITY: Zero incentive for gaming attacks

        // 10. Emit event (Updated with new penalty structure)
        emit PoolEarlyWithdrawal(poolId, msg.sender, userContribution, penalty, userAmount, block.timestamp);
    }

    // --- HELPER VIEW FUNCTIONS (CREATIVE FREEDOM ZONE) ---

    /**
     * @dev Preview early withdrawal amounts for a user
     * @param poolId Pool ID
     * @param user User address
     * @return userContribution Current user contribution
     * @return penalty Total penalty amount (10%)
     * @return userWillReceive Amount user will receive (90%)
     * @return canWithdraw Whether withdrawal is currently possible
     */
    function getEarlyWithdrawalPreview(uint256 poolId, address user) external view returns (
        uint96 userContribution,
        uint96 penalty,
        uint96 userWillReceive,
        bool canWithdraw
    ) {
        // Validate pool exists
        if (poolId >= poolCount) {
            return (0, 0, 0, false);
        }

        PoolStructs.PoolInfo storage pool = pools[poolId];
        userContribution = poolContributionAmounts[poolId][user];

        // Calculate amounts if user has contribution
        if (userContribution > 0) {
            penalty = userContribution * 20 / 100;           //  20% total penalty (security fix)
            userWillReceive = userContribution - penalty;    // 80% to user

            // Check if withdrawal is possible
            canWithdraw = pool.status == PoolStructs.PoolStatus.Active && 
                         block.timestamp < pool.deadline;
        } else {
            penalty = 0;
            userWillReceive = 0;
            canWithdraw = false;
        }
    }

    /**
     * @dev Get detailed penalty breakdown for early withdrawal (SECURITY FIX)
     * @param poolId Pool ID  
     * @param user User address
     * @return userContribution Current user contribution
     * @return totalPenalty Total penalty (20% - enhanced)
     * @return treasuryReceives Treasury receives (100% of penalty)
     * @return userReceives Amount user receives (80%)
     */
    function getEarlyWithdrawalBreakdown(uint256 poolId, address user) external view returns (
        uint96 userContribution,
        uint96 totalPenalty,
        uint96 treasuryReceives,
        uint96 userReceives
    ) {
        if (poolId >= poolCount) {
            return (0, 0, 0, 0);
        }

        userContribution = poolContributionAmounts[poolId][user];

        if (userContribution > 0) {
            totalPenalty = userContribution * 20 / 100;        //  20% total penalty (security fix)
            treasuryReceives = totalPenalty;                   //  100% vers treasury (no creator share)
            userReceives = userContribution - totalPenalty;    // 80% vers user
        }
    }

    /**
     * @dev Check if early withdrawal is possible for a user
     * @param poolId Pool ID
     * @param user User address
     * @return possible Whether early withdrawal is possible
     * @return reason Reason if not possible (0=possible, 1=invalid_pool, 2=not_active, 3=deadline_passed, 4=no_contribution)
     */
    function canWithdrawEarly(uint256 poolId, address user) external view returns (bool possible, uint8 reason) {
        // Check pool exists
        if (poolId >= poolCount) {
            return (false, 1); // invalid_pool
        }

        PoolStructs.PoolInfo storage pool = pools[poolId];

        // Check pool status
        if (pool.status != PoolStructs.PoolStatus.Active) {
            return (false, 2); // not_active
        }

        // Check deadline
        if (block.timestamp >= pool.deadline) {
            return (false, 3); // deadline_passed
        }

        // Check user contribution
        if (poolContributionAmounts[poolId][user] == 0) {
            return (false, 4); // no_contribution
        }

        return (true, 0); // possible
    }

    /**
     * @dev Authorize contract upgrades - only admin can upgrade
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyRole(ADMIN_ROLE) {}
}

contracts/core/ReferralManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";

/**
 * @title ReferralManager
 * @dev Manages referral system for free opinion creation
 * 
 * FEATURES:
 * - Generate unique referral codes
 * - Track referral relationships
 * - Provide free mints for both referrer and referee
 * - Anti-abuse mechanisms (one referral per address pair)
 */
contract ReferralManager is
    Initializable,
    UUPSUpgradeable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable
{
    // 
    // ROLES
    // 

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant OPINION_CONTRACT_ROLE = keccak256("OPINION_CONTRACT_ROLE");

    // 
    // STRUCTS
    // 

    struct ReferralData {
        address referrer;           // Who made the referral
        uint256 timestamp;          // When referral was created
        bool referrerRewardClaimed; // Has referrer used their free mint
        bool refereeRewardClaimed;  // Has referee used their free mint
        uint256 referralCode;       // Unique referral code used
    }

    struct ReferrerStats {
        uint256 totalReferrals;     // Total successful referrals
        uint256 availableFreeMints; // Unclaimed free mints
        uint256 totalFreeMints;     // Total free mints earned
    }

    // 
    // STATE VARIABLES
    // 

    // Referral tracking
    mapping(address => ReferrerStats) public referrerStats;
    mapping(address => ReferralData) public refereeData;
    mapping(uint256 => address) public referralCodeToReferrer;
    mapping(address => uint256) public referrerToCode;

    // Anti-abuse: prevent same address pair from referring multiple times
    mapping(address => mapping(address => bool)) public hasReferred;

    // Configuration
    uint256 public nextReferralCode;
    bool public referralSystemEnabled;
    uint256 public maxFreeMints; // Max accumulated free mints per user

    // 
    // EVENTS
    // 

    event ReferralCreated(
        address indexed referrer,
        address indexed referee,
        uint256 indexed referralCode,
        uint256 timestamp
    );

    event FreeMintUsed(
        address indexed user,
        uint256 indexed opinionId,
        bool isReferrer,
        uint256 timestamp
    );

    event ReferralCodeGenerated(
        address indexed user,
        uint256 indexed referralCode,
        uint256 timestamp
    );

    // 
    // INITIALIZATION
    // 

    function initialize(address admin) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(ADMIN_ROLE, admin);

        nextReferralCode = 100000; // Start with 6-digit codes
        referralSystemEnabled = true;
        maxFreeMints = 4; // Max 4 accumulated free mints (reasonable limit)
    }

    // 
    // REFERRAL CODE MANAGEMENT
    // 

    /**
     * @dev Generate a referral code for a user
     * @param user Address to generate code for
     * @return referralCode The generated referral code
     */
    function generateReferralCode(address user) external returns (uint256 referralCode) {
        require(user != address(0), "Invalid address");
        require(referralSystemEnabled, "Referral system disabled");

        // Check if user already has a code
        if (referrerToCode[user] != 0) {
            return referrerToCode[user];
        }

        referralCode = nextReferralCode++;

        referralCodeToReferrer[referralCode] = user;
        referrerToCode[user] = referralCode;

        emit ReferralCodeGenerated(user, referralCode, block.timestamp);

        return referralCode;
    }

    // 
    // REFERRAL TRACKING
    // 

    /**
     * @dev Process a referral when new user creates first opinion
     * @param referee New user address
     * @param referralCode Referral code used
     */
    function processReferral(
        address referee,
        uint256 referralCode
    ) external nonReentrant returns (bool success) {
        require(referralSystemEnabled, "Referral system disabled");
        require(referee != address(0), "Invalid referee");

        address referrer = referralCodeToReferrer[referralCode];
        require(referrer != address(0), "Invalid referral code");
        require(referrer != referee, "Cannot refer yourself");
        require(!hasReferred[referrer][referee], "Already referred this user");
        require(refereeData[referee].referrer == address(0), "User already referred");

        // Record the referral
        refereeData[referee] = ReferralData({
            referrer: referrer,
            timestamp: block.timestamp,
            referrerRewardClaimed: false,
            refereeRewardClaimed: false,
            referralCode: referralCode
        });

        // Update referrer stats (always track the referral)
        ReferrerStats storage stats = referrerStats[referrer];
        stats.totalReferrals++;

        // Grant free mints to referrer ONLY if they haven't hit the 4-mint cap
        // But the referral is still valid for the referee regardless!
        if (stats.availableFreeMints < maxFreeMints) {
            stats.availableFreeMints++;
            stats.totalFreeMints++;
        }
        // Note: Even if referrer doesn't get free mint, referee still gets theirs!

        // Mark this referral pair as used
        hasReferred[referrer][referee] = true;

        emit ReferralCreated(referrer, referee, referralCode, block.timestamp);

        return true; // Always return true - referral is processed for referee
    }

    // 
    // FREE MINT SYSTEM
    // 

    /**
     * @dev Check if user has free mints available
     * @param user Address to check
     * @return available Number of free mints available
     */
    function getAvailableFreeMints(address user) external view returns (uint256 available) {
        // Check referrer free mints
        available = referrerStats[user].availableFreeMints;

        // Check referee free mint (one-time)
        ReferralData storage data = refereeData[user];
        if (data.referrer != address(0) && !data.refereeRewardClaimed) {
            available += 1;
        }

        return available;
    }

    /**
     * @dev Use a free mint (called by OpinionCore)
     * @param user Address using the free mint
     * @param opinionId Opinion ID being created
     * @return success Whether free mint was successfully used
     */
    function useFreeMint(
        address user,
        uint256 opinionId
    ) external onlyRole(OPINION_CONTRACT_ROLE) returns (bool success) {
        // Try to use referee free mint FIRST (one-time only for new users)
        ReferralData storage data = refereeData[user];
        if (data.referrer != address(0) && !data.refereeRewardClaimed) {
            data.refereeRewardClaimed = true;
            emit FreeMintUsed(user, opinionId, false, block.timestamp);
            return true;
        }

        // Try to use referrer free mint (accumulated from successful referrals)
        ReferrerStats storage stats = referrerStats[user];
        if (stats.availableFreeMints > 0) {
            stats.availableFreeMints--;
            emit FreeMintUsed(user, opinionId, true, block.timestamp);
            return true;
        }

        return false; // No free mints available
    }

    // 
    // VIEW FUNCTIONS
    // 

    /**
     * @dev Get referral statistics for a user
     */
    function getReferralStats(address user) external view returns (
        uint256 totalReferrals,
        uint256 availableFreeMints,
        uint256 totalFreeMints,
        uint256 referralCode,
        bool isReferred,
        address referredBy
    ) {
        ReferrerStats storage stats = referrerStats[user];
        ReferralData storage data = refereeData[user];

        return (
            stats.totalReferrals,
            stats.availableFreeMints,
            stats.totalFreeMints,
            referrerToCode[user],
            data.referrer != address(0),
            data.referrer
        );
    }

    /**
     * @dev Get referrer address from referral code
     */
    function getReferrerFromCode(uint256 referralCode) external view returns (address) {
        return referralCodeToReferrer[referralCode];
    }

    // 
    // ADMIN FUNCTIONS
    // 

    /**
     * @dev Toggle referral system on/off
     */
    function setReferralSystemEnabled(bool enabled) external onlyRole(ADMIN_ROLE) {
        referralSystemEnabled = enabled;
    }

    /**
     * @dev Set maximum free mints per user
     */
    function setMaxFreeMints(uint256 _maxFreeMints) external onlyRole(ADMIN_ROLE) {
        maxFreeMints = _maxFreeMints;
    }

    /**
     * @dev Grant opinion contract role to OpinionCore
     */
    function grantOpinionContractRole(address opinionContract) external onlyRole(ADMIN_ROLE) {
        _grantRole(OPINION_CONTRACT_ROLE, opinionContract);
    }

    /**
     * @dev Emergency: manually add free mints to user (for promotions)
     */
    function emergencyGrantFreeMints(
        address user,
        uint256 amount
    ) external onlyRole(ADMIN_ROLE) {
        ReferrerStats storage stats = referrerStats[user];
        stats.availableFreeMints += amount;
        stats.totalFreeMints += amount;
    }

    // 
    // UPGRADE AUTHORIZATION
    // 

    function _authorizeUpgrade(address newImplementation) internal override onlyRole(ADMIN_ROLE) {}
}

contracts/core/SecurityManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";

import "./interfaces/IOpinionMarketEvents.sol";
import "./libraries/PriceCalculator.sol";
import "./libraries/MevProtection.sol";
import "./libraries/InputValidation.sol";

/**
 * @title SecurityManager
 * @dev Dedicated contract for bot detection, MEV protection, and security features
 * Extracted from OpinionCore to reduce contract size
 */
contract SecurityManager is
    Initializable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable
{
    // --- ROLES ---
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant CORE_CONTRACT_ROLE = keccak256("CORE_CONTRACT_ROLE");
    bytes32 public constant MARKET_CONTRACT_ROLE = keccak256("MARKET_CONTRACT_ROLE");

    // --- STATE VARIABLES ---

    //  Bot detection and anti-bot protection
    mapping(address => PriceCalculator.TraderPattern) private traderPatterns;
    bool public botDetectionEnabled;
    uint256 public botDetectionStartTime;

    //  Enhanced MEV Protection
    mapping(address => MevProtection.MevProfile) private mevProfiles;
    bool public enhancedMevProtectionEnabled;

    //  Input Validation Hardening
    bool public validationHardeningEnabled;
    uint256 public emergencyModeTimestamp;
    mapping(bytes32 => uint256) private validationMetrics;

    // --- EVENTS ---
    event BotDetectionToggled(bool enabled, address admin);
    event EnhancedMevProtectionToggled(bool enabled, address admin);
    event ValidationHardeningToggled(bool enabled, address admin);
    event AdminTraderFlagged(address indexed trader, bool flagAsBot, uint8 suspicionLevel, address admin);
    event AdminTraderReset(address indexed trader, address admin);
    event AdminMevRiskAdjusted(address indexed trader, uint8 oldLevel, uint8 newLevel, string reason, address admin);
    event AdminMevProfileReset(address indexed trader, address admin);
    event EmergencyShutdownTriggered(string reason, uint8 severity, string action);
    event SystemRecovered(string message, address admin);
    event ValidationWarning(string operation, uint256 gasUsed, string warning);
    event DataCorruptionDetected(string dataType, uint8 severity, string action);

    // --- INITIALIZATION ---
    function initialize(
        address _opinionCore,
        address _usdcToken,
        address _treasury
    ) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();
        __Pausable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);

        // Grant role to opinion core to call security functions
        if (_opinionCore != address(0)) {
            _grantRole(CORE_CONTRACT_ROLE, _opinionCore);
        }

        // Initialize security features (disabled by default for gas efficiency)
        botDetectionEnabled = false;
        enhancedMevProtectionEnabled = false;
        validationHardeningEnabled = false;
    }

    // --- MODIFIERS ---
    modifier onlyCoreContract() {
        require(hasRole(CORE_CONTRACT_ROLE, msg.sender), "Only core contract");
        _;
    }

    modifier onlyMarketContract() {
        require(hasRole(MARKET_CONTRACT_ROLE, msg.sender), "Only market contract");
        _;
    }

    // --- BOT DETECTION FUNCTIONS ---

    /**
     * @dev Analyzes trader patterns for bot behavior detection
     * @param trader Trader address to analyze
     * @param tradeSuccess Whether the current trade was profitable
     * @param tradeValue Value of the current trade
     * @return Current bot penalty level for the trader
     */
    function analyzeTraderPattern(
        address trader,
        bool tradeSuccess,
        uint256 tradeValue
    ) external onlyCoreContract returns (PriceCalculator.BotPenaltyLevel) {
        if (!botDetectionEnabled) {
            return PriceCalculator.BotPenaltyLevel.NONE;
        }

        return PriceCalculator.analyzeTraderPattern(
            trader,
            tradeSuccess,
            tradeValue,
            traderPatterns
        );
    }

    /**
     * @dev Applies progressive penalties based on bot detection level
     * @param trader Trader address
     * @param penaltyLevel Current penalty level
     * @param baseReward Original reward/benefit amount
     * @return Adjusted reward after penalties
     */
    function applyBotPenalties(
        address trader,
        PriceCalculator.BotPenaltyLevel penaltyLevel,
        uint256 baseReward
    ) external onlyCoreContract returns (uint256) {
        if (!botDetectionEnabled) {
            return baseReward;
        }

        return PriceCalculator.applyBotPenalties(trader, penaltyLevel, baseReward);
    }

    /**
     * @dev Gets trader bot detection status and statistics
     * @param trader Trader address to query
     * @return penaltyLevel Current penalty level
     * @return flaggedAsBot Whether trader is flagged as bot
     * @return totalTrades Total trades by trader
     * @return successfulTrades Successful trades count
     * @return successRate Success rate percentage
     * @return suspicionLevel Current suspicion level (0-4)
     */
    function getTraderBotInfo(address trader) external view returns (
        PriceCalculator.BotPenaltyLevel penaltyLevel,
        bool flaggedAsBot,
        uint32 totalTrades,
        uint32 successfulTrades,
        uint256 successRate,
        uint8 suspicionLevel
    ) {
        (penaltyLevel, flaggedAsBot) = PriceCalculator.getTraderBotStatus(trader, traderPatterns);
        (totalTrades, successfulTrades, successRate, suspicionLevel) = PriceCalculator.getTraderStats(trader, traderPatterns);
    }

    // --- MEV PROTECTION FUNCTIONS ---

    /**
     * @dev Analyzes MEV risk for a trade
     * @param user User address
     * @param tradeValue Trade value
     * @param opinionId Opinion being traded
     * @return Current MEV risk level
     */
    function analyzeMevRisk(
        address user,
        uint256 tradeValue,
        uint256 opinionId
    ) external onlyCoreContract returns (MevProtection.MevRiskLevel) {
        if (!enhancedMevProtectionEnabled) {
            return MevProtection.MevRiskLevel.NONE;
        }

        return MevProtection.analyzeMevRisk(user, tradeValue, opinionId, mevProfiles);
    }

    /**
     * @dev Updates MEV profile after successful trade
     * @param user User address
     * @param opinionId Opinion traded
     * @param tradeValue Trade value
     */
    function updateMevProfile(
        address user,
        uint256 opinionId,
        uint256 tradeValue
    ) external onlyCoreContract {
        if (!enhancedMevProtectionEnabled) {
            return;
        }

        MevProtection.updateMevProfile(user, opinionId, tradeValue, mevProfiles);
    }

    /**
     * @dev Checks if user should be blocked from trading due to MEV risk
     * @param user User address
     * @return blocked Whether user is currently blocked
     * @return reason Human-readable reason for blocking
     */
    function checkMevTradeBlocking(address user) external view returns (bool blocked, string memory reason) {
        if (!enhancedMevProtectionEnabled) {
            return (false, "Enhanced MEV protection disabled");
        }

        blocked = MevProtection.shouldBlockTrading(user, mevProfiles);

        if (blocked) {
            MevProtection.MevProfile memory profile = MevProtection.getMevProfile(user, mevProfiles);
            if (profile.riskLevel >= uint8(MevProtection.MevRiskLevel.BLOCKED)) {
                reason = "User blocked due to critical MEV risk";
            } else if (MevProtection.isInMevCooldown(user, mevProfiles)) {
                reason = "User in MEV cooldown period";
            } else {
                reason = "MEV trade limit exceeded for current block";
            }
        } else {
            reason = "Trading allowed";
        }
    }

    /**
     * @dev Calculates MEV penalty for a trade
     * @param user User address
     * @param tradeValue Trade value
     * @return penaltyMultiplier Penalty multiplier (100 = no penalty, 150 = 50% penalty)
     */
    function calculateMevPenaltyMultiplier(address user, uint256 tradeValue) external view returns (uint256 penaltyMultiplier) {
        if (!enhancedMevProtectionEnabled) {
            return 100; // No penalty if protection disabled
        }

        return MevProtection.calculateMevPenalty(user, tradeValue, mevProfiles);
    }

    /**
     * @dev Gets user's MEV protection profile and risk assessment
     * @param user User address to query
     * @return profile Complete MEV profile data
     */
    function getUserMevProfile(address user) external view returns (MevProtection.MevProfile memory profile) {
        return MevProtection.getMevProfile(user, mevProfiles);
    }

    // --- VALIDATION HARDENING FUNCTIONS ---

    /**
     * @dev Validates opinion creation inputs with enhanced security
     * @param question The opinion question
     * @param answer The initial answer
     * @param description The answer description
     * @param initialPrice The initial price chosen by creator
     * @param opinionCategories Categories for the opinion
     * @param creator Creator address
     */
    function validateCreateOpinionInputs(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata opinionCategories,
        address creator
    ) external view onlyCoreContract {
        if (!validationHardeningEnabled) return;

        // Validate strings
        InputValidation.validateString(question, 52, false, "question");
        InputValidation.validateString(answer, 52, false, "answer");
        InputValidation.validateString(description, 500, true, "description");

        // Validate price
        InputValidation.validatePrice(initialPrice, 2_000_000, 100_000_000); // 2-100 USDC

        // Validate categories array
        InputValidation.validateArray(opinionCategories.length, 1, 3, "opinionCategories");

        // Validate user
        InputValidation.validateAddress(creator, "creator");

        // Check user limits
        InputValidation.validateUserLimits(creator, 0, 10, "daily_opinion_creation");
    }

    /**
     * @dev Validates opinion state for security checks
     * @param opinionId Opinion identifier
     * @param exists Whether opinion exists
     * @param isActive Whether opinion is active
     * @param lastPrice Last price
     * @param owner Current owner
     */
    function validateOpinionState(
        uint256 opinionId,
        bool exists,
        bool isActive,
        uint256 lastPrice,
        address owner
    ) external view onlyCoreContract {
        if (!validationHardeningEnabled) return;

        InputValidation.validateOpinionState(opinionId, exists, isActive, lastPrice, owner);
    }

    /**
     * @dev Records validation performance metrics
     * @param operation Operation identifier
     * @param gasUsed Gas consumed
     */
    function recordValidationMetrics(string memory operation, uint256 gasUsed) external onlyCoreContract {
        if (!validationHardeningEnabled) return;

        bytes32 totalKey = keccak256("totalValidations");
        bytes32 gasKey = keccak256("totalGasCost");

        validationMetrics[totalKey]++;
        validationMetrics[gasKey] += gasUsed;

        // Emit gas usage warning if excessive
        if (gasUsed > 50000) {
            emit ValidationWarning(operation, gasUsed, "High gas usage detected");
        }
    }

    // --- ADMIN FUNCTIONS ---

    /**
     * @dev Enables or disables bot detection system
     * @param enabled Whether to enable bot detection
     */
    function setBotDetectionEnabled(bool enabled) external onlyRole(ADMIN_ROLE) {
        botDetectionEnabled = enabled;
        if (enabled && botDetectionStartTime == 0) {
            botDetectionStartTime = block.timestamp;
        }
        emit BotDetectionToggled(enabled, msg.sender);
    }

    /**
     * @dev Enables or disables enhanced MEV protection system
     * @param enabled Whether to enable enhanced MEV protection
     */
    function setEnhancedMevProtectionEnabled(bool enabled) external onlyRole(ADMIN_ROLE) {
        enhancedMevProtectionEnabled = enabled;
        emit EnhancedMevProtectionToggled(enabled, msg.sender);
    }

    /**
     * @dev Enables or disables validation hardening system
     * @param enabled Whether to enable validation hardening
     */
    function setValidationHardeningEnabled(bool enabled) external onlyRole(ADMIN_ROLE) {
        validationHardeningEnabled = enabled;
        emit ValidationHardeningToggled(enabled, msg.sender);
    }

    /**
     * @dev Emergency admin function to manually flag/unflag a trader as bot
     * @param trader Trader address
     * @param flagAsBot Whether to flag as bot
     * @param suspicionLevel Manual suspicion level (0-4)
     */
    function adminFlagTrader(
        address trader,
        bool flagAsBot,
        uint8 suspicionLevel
    ) external onlyRole(ADMIN_ROLE) {
        require(suspicionLevel <= 4, "Invalid suspicion level");

        PriceCalculator.TraderPattern storage pattern = traderPatterns[trader];
        pattern.flaggedAsBot = flagAsBot;
        pattern.suspicionLevel = suspicionLevel;

        emit AdminTraderFlagged(trader, flagAsBot, suspicionLevel, msg.sender);
    }

    /**
     * @dev Emergency admin function to reset trader bot detection data
     * @param trader Trader address
     */
    function adminResetTraderData(address trader) external onlyRole(ADMIN_ROLE) {
        delete traderPatterns[trader];
        emit AdminTraderReset(trader, msg.sender);
    }

    /**
     * @dev Admin function to manually adjust user's MEV risk level
     * @param user User address
     * @param newRiskLevel New risk level (0-5)
     * @param reason Reason for manual adjustment
     */
    function adminSetMevRiskLevel(
        address user,
        uint8 newRiskLevel,
        string calldata reason
    ) external onlyRole(ADMIN_ROLE) {
        require(newRiskLevel <= uint8(MevProtection.MevRiskLevel.BLOCKED), "Invalid risk level");

        MevProtection.MevProfile storage profile = mevProfiles[user];
        uint8 oldLevel = profile.riskLevel;
        profile.riskLevel = newRiskLevel;

        emit AdminMevRiskAdjusted(user, oldLevel, newRiskLevel, reason, msg.sender);
    }

    /**
     * @dev Admin function to reset user's MEV protection data
     * @param user User address
     */
    function adminResetMevProfile(address user) external onlyRole(ADMIN_ROLE) {
        delete mevProfiles[user];
        emit AdminMevProfileReset(user, msg.sender);
    }

    /**
     * @dev Emergency function to activate system protection mode
     * @param reason Reason for emergency activation
     */
    function activateEmergencyMode(string calldata reason) external onlyRole(ADMIN_ROLE) {
        emergencyModeTimestamp = block.timestamp;
        emit EmergencyShutdownTriggered(reason, 50, "Admin review required");
    }

    /**
     * @dev Deactivates emergency protection mode
     */
    function deactivateEmergencyMode() external onlyRole(ADMIN_ROLE) {
        emergencyModeTimestamp = 0;
        emit SystemRecovered("Emergency mode deactivated", msg.sender);
    }

    // --- VIEW FUNCTIONS ---

    /**
     * @dev Gets bot detection system status
     * @return enabled Whether bot detection is enabled
     * @return startTime When bot detection was first enabled
     * @return totalFlaggedTraders Count of flagged traders
     */
    function getBotDetectionStatus() external view returns (
        bool enabled,
        uint256 startTime,
        uint256 totalFlaggedTraders
    ) {
        enabled = botDetectionEnabled;
        startTime = botDetectionStartTime;
        // Note: totalFlaggedTraders would require additional tracking mapping
        totalFlaggedTraders = 0; // Placeholder - would need enumeration
    }

    /**
     * @dev Gets MEV protection system statistics
     * @return enabled Whether enhanced MEV protection is enabled
     * @return totalHighRiskUsers Count of users with HIGH+ risk level
     * @return totalBlockedUsers Count of currently blocked users
     */
    function getMevProtectionStats() external view returns (
        bool enabled,
        uint256 totalHighRiskUsers,
        uint256 totalBlockedUsers
    ) {
        enabled = enhancedMevProtectionEnabled;
        // Note: These counts would require additional enumeration tracking for efficiency
        totalHighRiskUsers = 0; // Placeholder - would need enumeration
        totalBlockedUsers = 0; // Placeholder - would need enumeration
    }

    /**
     * @dev Gets validation system metrics
     * @return enabled Whether validation hardening is enabled
     * @return emergencyActive Whether emergency mode is active
     * @return totalValidations Total validation operations performed
     * @return averageGasCost Average gas cost for validation
     */
    function getValidationMetrics() external view returns (
        bool enabled,
        bool emergencyActive,
        uint256 totalValidations,
        uint256 averageGasCost
    ) {
        enabled = validationHardeningEnabled;
        emergencyActive = emergencyModeTimestamp > 0 && (block.timestamp - emergencyModeTimestamp < 3600);
        totalValidations = validationMetrics[keccak256("totalValidations")];

        uint256 totalGas = validationMetrics[keccak256("totalGasCost")];
        averageGasCost = totalValidations > 0 ? totalGas / totalValidations : 0;
    }

    // --- ROLE MANAGEMENT ---

    /**
     * @dev Grants CORE_CONTRACT_ROLE to a contract
     * @param contractAddress Address to grant role to
     */
    function grantCoreContractRole(address contractAddress) external onlyRole(ADMIN_ROLE) {
        require(contractAddress != address(0), "Zero address not allowed");
        _grantRole(CORE_CONTRACT_ROLE, contractAddress);
    }

    /**
     * @dev Grants MARKET_CONTRACT_ROLE to a contract
     * @param contractAddress Address to grant role to
     */
    function grantMarketContractRole(address contractAddress) external onlyRole(ADMIN_ROLE) {
        require(contractAddress != address(0), "Zero address not allowed");
        _grantRole(MARKET_CONTRACT_ROLE, contractAddress);
    }

    /**
     * @dev Revokes CORE_CONTRACT_ROLE from a contract
     * @param contractAddress Address to revoke role from
     */
    function revokeCoreContractRole(address contractAddress) external onlyRole(ADMIN_ROLE) {
        _revokeRole(CORE_CONTRACT_ROLE, contractAddress);
    }

    /**
     * @dev Revokes MARKET_CONTRACT_ROLE from a contract
     * @param contractAddress Address to revoke role from
     */
    function revokeMarketContractRole(address contractAddress) external onlyRole(ADMIN_ROLE) {
        _revokeRole(MARKET_CONTRACT_ROLE, contractAddress);
    }

    // --- EMERGENCY FUNCTIONS ---

    /**
     * @dev Pauses the contract
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpauses the contract
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }
}

