contracts/core/TreasurySecure.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title TreasurySecure
 * @dev Ultra-simple but bulletproof treasury security for solo-dev operations
 * ONE withdrawal function for all amounts (1K to 1M USDC) with 72h timelock
 * Emergency freeze capability + large withdrawal monitoring
 */
contract TreasurySecure is
    Initializable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable
{
    using SafeERC20 for IERC20;

    // === ROLE DEFINITIONS ===

    /**
     * @dev Treasury admin role - can propose and execute withdrawals
     * Should be assigned to the main admin wallet
     */
    bytes32 public constant TREASURY_ADMIN_ROLE = keccak256("TREASURY_ADMIN_ROLE");

    /**
     * @dev Emergency role - can freeze/unfreeze treasury
     * Should be assigned to backup admin wallet for emergency situations
     */
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");

    // === CONFIGURATION CONSTANTS ===

    uint256 public constant WITHDRAWAL_TIMELOCK = 72 hours;    // Universal timelock for ALL withdrawals
    uint256 public constant ALERT_THRESHOLD = 10_000 * 1e6;   // 10K USDC alert threshold (no blocking)
    uint256 public constant FREEZE_DURATION = 24 hours;       // Auto-unfreeze after 24h
    uint256 public constant MAX_WITHDRAWAL = 1_000_000 * 1e6; // 1M USDC maximum single withdrawal

    // === STATE VARIABLES ===

    IERC20 public usdcToken;                                   // USDC token contract
    bool public treasuryFrozen;                                // Emergency freeze state
    uint256 public freezeEndTime;                              // Auto-unfreeze timestamp
    uint256 public proposalCounter;                            // Sequential proposal IDs

    // === WITHDRAWAL PROPOSAL STRUCTURE ===

    struct WithdrawalProposal {
        uint256 amount;                 // Withdrawal amount
        address recipient;              // Recipient address
        uint256 proposedTime;           // When proposal was created
        uint256 executeTime;            // When proposal can be executed (proposedTime + timelock)
        bool executed;                  // Whether proposal has been executed
        bool cancelled;                 // Whether proposal has been cancelled
        string description;             // Optional description for audit trail
    }

    mapping(uint256 => WithdrawalProposal) public proposals;   // Proposal ID  Proposal data

    // === EVENTS ===

    /**
     * @dev Emitted when a withdrawal is proposed
     * @param proposalId Unique proposal identifier
     * @param amount Withdrawal amount
     * @param recipient Recipient address
     * @param executeTime When the proposal can be executed
     * @param description Optional description
     */
    event WithdrawalProposed(
        uint256 indexed proposalId,
        uint256 amount,
        address indexed recipient,
        uint256 executeTime,
        string description
    );

    /**
     * @dev Emitted for large withdrawal proposals (>10K USDC)
     * @param proposalId Proposal identifier
     * @param amount Large withdrawal amount
     * @param alert Alert message for monitoring
     */
    event LargeWithdrawalProposed(
        uint256 indexed proposalId,
        uint256 amount,
        string alert
    );

    /**
     * @dev Emitted when a withdrawal is executed
     * @param proposalId Proposal identifier
     * @param amount Executed amount
     * @param recipient Recipient address
     */
    event WithdrawalExecuted(
        uint256 indexed proposalId,
        uint256 amount,
        address indexed recipient
    );

    /**
     * @dev Emitted when a withdrawal proposal is cancelled
     * @param proposalId Proposal identifier
     * @param reason Cancellation reason
     */
    event WithdrawalCancelled(
        uint256 indexed proposalId,
        string reason
    );

    /**
     * @dev Emitted when treasury is frozen for emergency
     * @param reason Freeze reason
     * @param freezeEndTime When auto-unfreeze occurs
     */
    event TreasuryFrozen(
        string reason,
        uint256 freezeEndTime
    );

    /**
     * @dev Emitted when treasury is unfrozen
     * @param isAutomatic Whether unfreeze was automatic or manual
     */
    event TreasuryUnfrozen(
        bool isAutomatic
    );

    /**
     * @dev Emitted for treasury balance updates
     * @param oldBalance Previous balance
     * @param newBalance New balance
     * @param operation Operation that caused the change
     */
    event TreasuryBalanceChanged(
        uint256 oldBalance,
        uint256 newBalance,
        string operation
    );

    // === ERRORS ===

    error TreasuryFrozenError(string reason, uint256 unfreezeTime);
    error ProposalNotFound(uint256 proposalId);
    error ProposalAlreadyExecuted(uint256 proposalId);
    error ProposalCancelled(uint256 proposalId);
    error TimelockNotExpired(uint256 proposalId, uint256 timeRemaining);
    error InvalidAmount(uint256 amount, uint256 max);
    error InvalidRecipient(address recipient);
    error InsufficientTreasuryBalance(uint256 requested, uint256 available);
    error ProposalAlreadyCancelled(uint256 proposalId);

    // === INITIALIZATION ===

    /**
     * @dev Initializes the treasury security contract
     * @param _usdcToken USDC token contract address
     * @param _treasuryAdmin Primary treasury admin
     * @param _emergencyAdmin Emergency admin (can be same as treasury admin)
     */
    function initialize(
        address _usdcToken,
        address _treasuryAdmin,
        address _emergencyAdmin
    ) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();
        __Pausable_init();

        // Validate inputs
        require(_usdcToken != address(0), "Invalid USDC token address");
        require(_treasuryAdmin != address(0), "Invalid treasury admin address");
        require(_emergencyAdmin != address(0), "Invalid emergency admin address");

        // Set token
        usdcToken = IERC20(_usdcToken);

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, _treasuryAdmin);
        _grantRole(TREASURY_ADMIN_ROLE, _treasuryAdmin);
        _grantRole(EMERGENCY_ROLE, _emergencyAdmin);

        // Initialize state
        treasuryFrozen = false;
        proposalCounter = 1; // Start from 1 for easier tracking
    }

    // === MODIFIERS ===

    /**
     * @dev Ensures treasury is not frozen
     */
    modifier notFrozen() {
        // Check if auto-unfreeze should occur
        if (treasuryFrozen && block.timestamp >= freezeEndTime) {
            _autoUnfreeze();
        }

        if (treasuryFrozen) {
            revert TreasuryFrozenError("Treasury operations frozen", freezeEndTime);
        }
        _;
    }

    // === CORE WITHDRAWAL FUNCTIONS ===

    /**
     * @dev Proposes a withdrawal (universal function for all amounts)
     * @param amount Amount to withdraw (1K to 1M USDC supported)
     * @param recipient Recipient address
     * @param description Optional description for audit trail
     * @return proposalId Unique proposal identifier
     */
    function proposeWithdrawal(
        uint256 amount,
        address recipient,
        string calldata description
    ) external onlyRole(TREASURY_ADMIN_ROLE) notFrozen returns (uint256 proposalId) {
        // Validate inputs
        if (amount == 0 || amount > MAX_WITHDRAWAL) {
            revert InvalidAmount(amount, MAX_WITHDRAWAL);
        }
        if (recipient == address(0)) {
            revert InvalidRecipient(recipient);
        }

        // Check treasury balance
        uint256 balance = usdcToken.balanceOf(address(this));
        if (amount > balance) {
            revert InsufficientTreasuryBalance(amount, balance);
        }

        // Create proposal
        proposalId = proposalCounter++;
        uint256 executeTime = block.timestamp + WITHDRAWAL_TIMELOCK;

        proposals[proposalId] = WithdrawalProposal({
            amount: amount,
            recipient: recipient,
            proposedTime: block.timestamp,
            executeTime: executeTime,
            executed: false,
            cancelled: false,
            description: description
        });

        // Emit events
        emit WithdrawalProposed(proposalId, amount, recipient, executeTime, description);

        // Large withdrawal alert (no blocking, just monitoring)
        if (amount >= ALERT_THRESHOLD) {
            emit LargeWithdrawalProposed(
                proposalId,
                amount,
                "ALERT: Large withdrawal proposed - requires attention"
            );
        }

        return proposalId;
    }

    /**
     * @dev Executes a withdrawal after timelock expires
     * @param proposalId Proposal identifier to execute
     */
    function executeWithdrawal(uint256 proposalId) external onlyRole(TREASURY_ADMIN_ROLE) notFrozen nonReentrant {
        WithdrawalProposal storage proposal = proposals[proposalId];

        // Validate proposal exists
        if (proposal.proposedTime == 0) {
            revert ProposalNotFound(proposalId);
        }

        // Check if already executed
        if (proposal.executed) {
            revert ProposalAlreadyExecuted(proposalId);
        }

        // Check if cancelled
        if (proposal.cancelled) {
            revert ProposalCancelled(proposalId);
        }

        // Check timelock
        if (block.timestamp < proposal.executeTime) {
            uint256 timeRemaining = proposal.executeTime - block.timestamp;
            revert TimelockNotExpired(proposalId, timeRemaining);
        }

        // Check current balance (may have changed since proposal)
        uint256 currentBalance = usdcToken.balanceOf(address(this));
        if (proposal.amount > currentBalance) {
            revert InsufficientTreasuryBalance(proposal.amount, currentBalance);
        }

        // Mark as executed
        proposal.executed = true;

        // Execute transfer
        uint256 oldBalance = currentBalance;
        usdcToken.safeTransfer(proposal.recipient, proposal.amount);
        uint256 newBalance = usdcToken.balanceOf(address(this));

        // Emit events
        emit WithdrawalExecuted(proposalId, proposal.amount, proposal.recipient);
        emit TreasuryBalanceChanged(oldBalance, newBalance, "Withdrawal executed");
    }

    /**
     * @dev Cancels a pending withdrawal proposal
     * @param proposalId Proposal identifier to cancel
     * @param reason Cancellation reason
     */
    function cancelWithdrawal(
        uint256 proposalId,
        string calldata reason
    ) external onlyRole(TREASURY_ADMIN_ROLE) {
        WithdrawalProposal storage proposal = proposals[proposalId];

        // Validate proposal exists
        if (proposal.proposedTime == 0) {
            revert ProposalNotFound(proposalId);
        }

        // Check if already executed
        if (proposal.executed) {
            revert ProposalAlreadyExecuted(proposalId);
        }

        // Check if already cancelled
        if (proposal.cancelled) {
            revert ProposalAlreadyCancelled(proposalId);
        }

        // Cancel proposal
        proposal.cancelled = true;

        emit WithdrawalCancelled(proposalId, reason);
    }

    // === EMERGENCY CONTROLS ===

    /**
     * @dev Freezes all treasury operations immediately
     * @param reason Reason for emergency freeze
     */
    function emergencyFreeze(string calldata reason) external onlyRole(EMERGENCY_ROLE) {
        treasuryFrozen = true;
        freezeEndTime = block.timestamp + FREEZE_DURATION;

        emit TreasuryFrozen(reason, freezeEndTime);
    }

    /**
     * @dev Manually unfreezes treasury (before auto-unfreeze)
     */
    function manualUnfreeze() external onlyRole(EMERGENCY_ROLE) {
        require(treasuryFrozen, "Treasury not frozen");

        treasuryFrozen = false;
        freezeEndTime = 0;

        emit TreasuryUnfrozen(false); // false = manual unfreeze
    }

    /**
     * @dev Internal function for automatic unfreeze
     */
    function _autoUnfreeze() internal {
        treasuryFrozen = false;
        freezeEndTime = 0;

        emit TreasuryUnfrozen(true); // true = automatic unfreeze
    }

    // === VIEW FUNCTIONS ===

    /**
     * @dev Gets detailed proposal information
     * @param proposalId Proposal identifier
     * @return Proposal details
     */
    function getProposal(uint256 proposalId) external view returns (WithdrawalProposal memory) {
        return proposals[proposalId];
    }

    /**
     * @dev Gets proposal status information
     * @param proposalId Proposal identifier
     * @return isPending Whether proposal is pending execution
     * @return isExecutable Whether proposal can be executed now
     * @return timeRemaining Seconds remaining until executable
     */
    function getProposalStatus(uint256 proposalId) external view returns (
        bool isPending,
        bool isExecutable,
        uint256 timeRemaining
    ) {
        WithdrawalProposal storage proposal = proposals[proposalId];

        if (proposal.proposedTime == 0 || proposal.executed || proposal.cancelled) {
            return (false, false, 0);
        }

        isPending = true;

        if (block.timestamp >= proposal.executeTime) {
            isExecutable = true;
            timeRemaining = 0;
        } else {
            isExecutable = false;
            timeRemaining = proposal.executeTime - block.timestamp;
        }
    }

    /**
     * @dev Gets all pending proposals (gas-efficient view)
     * @return pendingIds Array of pending proposal IDs
     */
    function getPendingProposals() external view returns (uint256[] memory pendingIds) {
        // Count pending proposals
        uint256 count = 0;
        for (uint256 i = 1; i < proposalCounter; i++) {
            WithdrawalProposal storage proposal = proposals[i];
            if (proposal.proposedTime > 0 && !proposal.executed && !proposal.cancelled) {
                count++;
            }
        }

        // Build array
        pendingIds = new uint256[](count);
        uint256 index = 0;
        for (uint256 i = 1; i < proposalCounter; i++) {
            WithdrawalProposal storage proposal = proposals[i];
            if (proposal.proposedTime > 0 && !proposal.executed && !proposal.cancelled) {
                pendingIds[index++] = i;
            }
        }
    }

    /**
     * @dev Gets treasury status summary
     * @return balance Current USDC balance
     * @return isFrozen Whether treasury is frozen
     * @return freezeTimeRemaining Seconds until auto-unfreeze (0 if not frozen)
     * @return pendingProposalsCount Number of pending proposals
     * @return nextProposalId Next proposal ID that will be assigned
     */
    function getTreasuryStatus() external view returns (
        uint256 balance,
        bool isFrozen,
        uint256 freezeTimeRemaining,
        uint256 pendingProposalsCount,
        uint256 nextProposalId
    ) {
        balance = usdcToken.balanceOf(address(this));
        isFrozen = treasuryFrozen;

        if (isFrozen && block.timestamp < freezeEndTime) {
            freezeTimeRemaining = freezeEndTime - block.timestamp;
        } else {
            freezeTimeRemaining = 0;
        }

        // Count pending proposals
        for (uint256 i = 1; i < proposalCounter; i++) {
            WithdrawalProposal storage proposal = proposals[i];
            if (proposal.proposedTime > 0 && !proposal.executed && !proposal.cancelled) {
                pendingProposalsCount++;
            }
        }

        nextProposalId = proposalCounter;
    }

    /**
     * @dev Checks if treasury will auto-unfreeze soon
     * @return willAutoUnfreeze Whether auto-unfreeze will occur
     * @return autoUnfreezeTime When auto-unfreeze will occur
     */
    function getAutoUnfreezeInfo() external view returns (bool willAutoUnfreeze, uint256 autoUnfreezeTime) {
        willAutoUnfreeze = treasuryFrozen && freezeEndTime > 0;
        autoUnfreezeTime = freezeEndTime;
    }

    // === ADMIN UTILITIES ===

    /**
     * @dev Emergency function to receive USDC (for manual deposits)
     * Can be called by treasury admin to add funds
     */
    function depositUSDC(uint256 amount) external onlyRole(TREASURY_ADMIN_ROLE) {
        require(amount > 0, "Amount must be greater than 0");

        uint256 oldBalance = usdcToken.balanceOf(address(this));
        usdcToken.safeTransferFrom(msg.sender, address(this), amount);
        uint256 newBalance = usdcToken.balanceOf(address(this));

        emit TreasuryBalanceChanged(oldBalance, newBalance, "Manual deposit");
    }

    /**
     * @dev Gets configuration constants for external reference
     * @return withdrawalTimelock Timelock duration in seconds
     * @return alertThreshold Large withdrawal alert threshold
     * @return freezeDuration Auto-unfreeze duration
     * @return maxWithdrawal Maximum single withdrawal amount
     */
    function getConfiguration() external pure returns (
        uint256 withdrawalTimelock,
        uint256 alertThreshold,
        uint256 freezeDuration,
        uint256 maxWithdrawal
    ) {
        return (WITHDRAWAL_TIMELOCK, ALERT_THRESHOLD, FREEZE_DURATION, MAX_WITHDRAWAL);
    }

    /**
     * @dev Pauses all contract operations (emergency only)
     * Different from freeze - pauses ALL functions including view functions
     */
    function emergencyPause() external onlyRole(EMERGENCY_ROLE) {
        _pause();
    }

    /**
     * @dev Unpauses contract operations
     */
    function emergencyUnpause() external onlyRole(EMERGENCY_ROLE) {
        _unpause();
    }
}

contracts/core/TreasurySecureEnhanced.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title TreasurySecureEnhanced
 * @dev Enhanced treasury security with instant daily limits for solo developers
 * 
 * FEATURES:
 * -  $1,000 USDC: Instant withdrawal (daily limit)
 * - > $1,000 USDC: 72-hour timelock delay
 * - Emergency freeze capability
 * - Full audit trail for all withdrawals
 */
contract TreasurySecureEnhanced is
    Initializable,
    UUPSUpgradeable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable
{
    using SafeERC20 for IERC20;

    // 
    // ROLE DEFINITIONS
    // 

    /**
     * @dev Treasury admin role - can propose/execute withdrawals and manage daily limits
     */
    bytes32 public constant TREASURY_ADMIN_ROLE = keccak256("TREASURY_ADMIN_ROLE");

    /**
     * @dev Emergency role - can freeze/unfreeze treasury
     */
    bytes32 public constant EMERGENCY_ROLE = keccak256("EMERGENCY_ROLE");

    // 
    // CONFIGURATION CONSTANTS
    // 

    uint256 public constant WITHDRAWAL_TIMELOCK = 72 hours;           // Timelock for large withdrawals
    uint256 public constant INSTANT_WITHDRAWAL_LIMIT = 1_000 * 1e6;  // 1K USDC daily instant limit
    uint256 public constant ALERT_THRESHOLD = 10_000 * 1e6;          // 10K USDC alert threshold
    uint256 public constant FREEZE_DURATION = 24 hours;              // Auto-unfreeze after 24h
    uint256 public constant MAX_WITHDRAWAL = 1_000_000 * 1e6;        // 1M USDC maximum single withdrawal
    uint256 public constant DAILY_RESET_PERIOD = 24 hours;           // Daily limit reset period

    // 
    // STATE VARIABLES
    // 

    IERC20 public usdcToken;                                          // USDC token contract
    bool public treasuryFrozen;                                       // Emergency freeze state
    uint256 public freezeEndTime;                                     // Auto-unfreeze timestamp
    uint256 public proposalCounter;                                   // Sequential proposal IDs

    // Daily instant withdrawal tracking
    mapping(address => uint256) public dailyWithdrawn;               // Amount withdrawn today per user
    mapping(address => uint256) public lastWithdrawalReset;          // Last reset timestamp per user
    uint256 public totalInstantWithdrawnToday;                       // Total instant withdrawals today
    uint256 public lastGlobalReset;                                  // Last global reset timestamp

    // 
    // WITHDRAWAL PROPOSAL STRUCTURE (For Large Amounts)
    // 

    struct WithdrawalProposal {
        uint256 amount;                 // Withdrawal amount
        address recipient;              // Recipient address
        uint256 proposedTime;           // When proposal was created
        uint256 executeTime;            // When proposal can be executed
        bool executed;                  // Whether proposal has been executed
        bool cancelled;                 // Whether proposal has been cancelled
        string description;             // Optional description for audit trail
    }

    mapping(uint256 => WithdrawalProposal) public proposals;          // Proposal ID  Proposal data

    // 
    // EVENTS
    // 

    /**
     * @dev Emitted when an instant withdrawal is executed
     */
    event InstantWithdrawalExecuted(
        address indexed recipient,
        uint256 amount,
        string description,
        uint256 remainingDailyLimit
    );

    /**
     * @dev Emitted when daily limit is exceeded and proposal is created
     */
    event DailyLimitExceeded(
        address indexed user,
        uint256 attemptedAmount,
        uint256 instantAmount,
        uint256 proposalAmount,
        uint256 proposalId
    );

    /**
     * @dev Emitted when a large withdrawal is proposed
     */
    event WithdrawalProposed(
        uint256 indexed proposalId,
        uint256 amount,
        address indexed recipient,
        uint256 executeTime,
        string description
    );

    /**
     * @dev Emitted for large withdrawal proposals (>10K USDC)
     */
    event LargeWithdrawalProposed(
        uint256 indexed proposalId,
        uint256 amount,
        string alert
    );

    /**
     * @dev Emitted when a withdrawal is executed
     */
    event WithdrawalExecuted(
        uint256 indexed proposalId,
        uint256 amount,
        address indexed recipient
    );

    /**
     * @dev Emitted when a withdrawal proposal is cancelled
     */
    event WithdrawalCancelled(
        uint256 indexed proposalId,
        string reason
    );

    /**
     * @dev Emitted when treasury is frozen for emergency
     */
    event TreasuryFrozen(
        string reason,
        uint256 freezeEndTime
    );

    /**
     * @dev Emitted when treasury is unfrozen
     */
    event TreasuryUnfrozen(
        bool isAutomatic
    );

    /**
     * @dev Emitted for treasury balance updates
     */
    event TreasuryBalanceChanged(
        uint256 oldBalance,
        uint256 newBalance,
        string operation
    );

    /**
     * @dev Emitted when daily limits are reset
     */
    event DailyLimitsReset(
        address indexed user,
        uint256 previousWithdrawn,
        uint256 resetTime
    );

    // 
    // ERRORS
    // 

    error TreasuryFrozenError(string reason, uint256 unfreezeTime);
    error ProposalNotFound(uint256 proposalId);
    error ProposalAlreadyExecuted(uint256 proposalId);
    error ProposalCancelled(uint256 proposalId);
    error TimelockNotExpired(uint256 proposalId, uint256 timeRemaining);
    error InvalidAmount(uint256 amount, uint256 max);
    error InvalidRecipient(address recipient);
    error InsufficientTreasuryBalance(uint256 requested, uint256 available);
    error ProposalAlreadyCancelled(uint256 proposalId);
    error DailyLimitExceededError(uint256 requested, uint256 available);

    // 
    // INITIALIZATION
    // 

    /**
     * @dev Initializes the enhanced treasury security contract
     */
    function initialize(
        address _usdcToken,
        address _treasuryAdmin,
        address _emergencyAdmin
    ) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();
        __Pausable_init();

        // Validate inputs
        require(_usdcToken != address(0), "Invalid USDC token address");
        require(_treasuryAdmin != address(0), "Invalid treasury admin address");
        require(_emergencyAdmin != address(0), "Invalid emergency admin address");

        // Set token
        usdcToken = IERC20(_usdcToken);

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, _treasuryAdmin);
        _grantRole(TREASURY_ADMIN_ROLE, _treasuryAdmin);
        _grantRole(EMERGENCY_ROLE, _emergencyAdmin);

        // Initialize state
        treasuryFrozen = false;
        proposalCounter = 1;
        lastGlobalReset = block.timestamp;
    }

    // 
    // MODIFIERS
    // 

    /**
     * @dev Ensures treasury is not frozen
     */
    modifier notFrozen() {
        // Check if auto-unfreeze should occur
        if (treasuryFrozen && block.timestamp >= freezeEndTime) {
            _autoUnfreeze();
        }

        if (treasuryFrozen) {
            revert TreasuryFrozenError("Treasury operations frozen", freezeEndTime);
        }
        _;
    }

    /**
     * @dev Updates daily limits if needed
     */
    modifier updateDailyLimits() {
        _updateDailyLimits(msg.sender);
        _;
    }

    // 
    // INSTANT WITHDRAWAL FUNCTIONS ( $1,000 USDC)
    // 

    /**
     * @dev Executes instant withdrawal if within daily limits
     * @param amount Amount to withdraw (max 1K USDC per day)
     * @param recipient Recipient address
     * @param description Description for audit trail
     */
    function instantWithdrawal(
        uint256 amount,
        address recipient,
        string calldata description
    ) external onlyRole(TREASURY_ADMIN_ROLE) notFrozen updateDailyLimits nonReentrant {

        // Validate inputs
        if (amount == 0 || amount > MAX_WITHDRAWAL) {
            revert InvalidAmount(amount, MAX_WITHDRAWAL);
        }
        if (recipient == address(0)) {
            revert InvalidRecipient(recipient);
        }

        // Check treasury balance
        uint256 balance = usdcToken.balanceOf(address(this));
        if (amount > balance) {
            revert InsufficientTreasuryBalance(amount, balance);
        }

        // Check daily limit
        uint256 remainingLimit = getRemainingDailyLimit(msg.sender);
        if (amount > remainingLimit) {
            revert DailyLimitExceededError(amount, remainingLimit);
        }

        // Update daily tracking
        dailyWithdrawn[msg.sender] += amount;
        totalInstantWithdrawnToday += amount;

        // Execute transfer
        uint256 oldBalance = balance;
        usdcToken.safeTransfer(recipient, amount);
        uint256 newBalance = usdcToken.balanceOf(address(this));

        // Calculate new remaining limit
        uint256 newRemainingLimit = getRemainingDailyLimit(msg.sender);

        // Emit events
        emit InstantWithdrawalExecuted(recipient, amount, description, newRemainingLimit);
        emit TreasuryBalanceChanged(oldBalance, newBalance, "Instant withdrawal");
    }

    /**
     * @dev Smart withdrawal - uses instant if possible, creates proposal if needed
     * @param amount Amount to withdraw
     * @param recipient Recipient address  
     * @param description Description for audit trail
     * @return wasInstant Whether withdrawal was executed instantly
     * @return proposalId Proposal ID if created (0 if instant)
     */
    function smartWithdrawal(
        uint256 amount,
        address recipient,
        string calldata description
    ) external onlyRole(TREASURY_ADMIN_ROLE) notFrozen updateDailyLimits nonReentrant 
       returns (bool wasInstant, uint256 proposalId) {

        // Validate inputs
        if (amount == 0 || amount > MAX_WITHDRAWAL) {
            revert InvalidAmount(amount, MAX_WITHDRAWAL);
        }
        if (recipient == address(0)) {
            revert InvalidRecipient(recipient);
        }

        // Check treasury balance
        uint256 balance = usdcToken.balanceOf(address(this));
        if (amount > balance) {
            revert InsufficientTreasuryBalance(amount, balance);
        }

        uint256 remainingLimit = getRemainingDailyLimit(msg.sender);

        if (amount <= remainingLimit) {
            // Can do instant withdrawal
            dailyWithdrawn[msg.sender] += amount;
            totalInstantWithdrawnToday += amount;

            // Execute transfer
            uint256 oldBalance = balance;
            usdcToken.safeTransfer(recipient, amount);
            uint256 newBalance = usdcToken.balanceOf(address(this));

            uint256 newRemainingLimit = getRemainingDailyLimit(msg.sender);
            emit InstantWithdrawalExecuted(recipient, amount, description, newRemainingLimit);
            emit TreasuryBalanceChanged(oldBalance, newBalance, "Smart instant withdrawal");

            return (true, 0);

        } else {
            // Need to create proposal for full amount
            proposalId = _createWithdrawalProposal(amount, recipient, description);

            emit DailyLimitExceeded(
                msg.sender,
                amount,
                0, // No instant amount
                amount, // Full amount in proposal
                proposalId
            );

            return (false, proposalId);
        }
    }

    /**
     * @dev Hybrid withdrawal - instant up to limit, proposal for remainder
     * @param amount Total amount to withdraw
     * @param recipient Recipient address
     * @param description Description for audit trail
     * @return instantAmount Amount withdrawn instantly
     * @return proposalId Proposal ID for remainder (0 if no remainder)
     */
    function hybridWithdrawal(
        uint256 amount,
        address recipient,
        string calldata description
    ) external onlyRole(TREASURY_ADMIN_ROLE) notFrozen updateDailyLimits nonReentrant
       returns (uint256 instantAmount, uint256 proposalId) {

        // Validate inputs
        if (amount == 0 || amount > MAX_WITHDRAWAL) {
            revert InvalidAmount(amount, MAX_WITHDRAWAL);
        }
        if (recipient == address(0)) {
            revert InvalidRecipient(recipient);
        }

        // Check treasury balance
        uint256 balance = usdcToken.balanceOf(address(this));
        if (amount > balance) {
            revert InsufficientTreasuryBalance(amount, balance);
        }

        uint256 remainingLimit = getRemainingDailyLimit(msg.sender);

        if (remainingLimit > 0 && amount > remainingLimit) {
            // Partial instant withdrawal
            instantAmount = remainingLimit;
            uint256 proposalAmount = amount - remainingLimit;

            // Execute instant part
            dailyWithdrawn[msg.sender] += instantAmount;
            totalInstantWithdrawnToday += instantAmount;

            uint256 oldBalance = balance;
            usdcToken.safeTransfer(recipient, instantAmount);
            uint256 newBalance = usdcToken.balanceOf(address(this));

            // Create proposal for remainder
            proposalId = _createWithdrawalProposal(proposalAmount, recipient, description);

            emit InstantWithdrawalExecuted(recipient, instantAmount, description, 0);
            emit TreasuryBalanceChanged(oldBalance, newBalance, "Hybrid instant withdrawal");
            emit DailyLimitExceeded(msg.sender, amount, instantAmount, proposalAmount, proposalId);

            return (instantAmount, proposalId);

        } else if (amount <= remainingLimit) {
            // Full instant withdrawal - implement directly to avoid recursion
            dailyWithdrawn[msg.sender] += amount;
            totalInstantWithdrawnToday += amount;

            // Execute transfer
            uint256 oldBalance = balance;
            usdcToken.safeTransfer(recipient, amount);
            uint256 newBalance = usdcToken.balanceOf(address(this));

            uint256 newRemainingLimit = getRemainingDailyLimit(msg.sender);
            emit InstantWithdrawalExecuted(recipient, amount, description, newRemainingLimit);
            emit TreasuryBalanceChanged(oldBalance, newBalance, "Hybrid full instant withdrawal");

            return (amount, 0);

        } else {
            // Full proposal (no daily limit available)
            proposalId = _createWithdrawalProposal(amount, recipient, description);
            emit DailyLimitExceeded(msg.sender, amount, 0, amount, proposalId);
            return (0, proposalId);
        }
    }

    // 
    // PROPOSAL SYSTEM (Large Amounts > Daily Limit)
    // 

    /**
     * @dev Creates a withdrawal proposal with 72-hour timelock
     * @param amount Amount to withdraw
     * @param recipient Recipient address
     * @param description Description for audit trail
     * @return proposalId Unique proposal identifier
     */
    function _createWithdrawalProposal(
        uint256 amount,
        address recipient,
        string memory description
    ) internal returns (uint256 proposalId) {

        // Create proposal
        proposalId = proposalCounter++;
        uint256 executeTime = block.timestamp + WITHDRAWAL_TIMELOCK;

        proposals[proposalId] = WithdrawalProposal({
            amount: amount,
            recipient: recipient,
            proposedTime: block.timestamp,
            executeTime: executeTime,
            executed: false,
            cancelled: false,
            description: description
        });

        // Emit events
        emit WithdrawalProposed(proposalId, amount, recipient, executeTime, description);

        // Large withdrawal alert
        if (amount >= ALERT_THRESHOLD) {
            emit LargeWithdrawalProposed(
                proposalId,
                amount,
                "ALERT: Large withdrawal proposed - requires attention"
            );
        }

        return proposalId;
    }

    /**
     * @dev Executes a withdrawal proposal after timelock expires
     * @param proposalId Proposal identifier to execute
     */
    function executeWithdrawal(uint256 proposalId) external onlyRole(TREASURY_ADMIN_ROLE) notFrozen nonReentrant {
        WithdrawalProposal storage proposal = proposals[proposalId];

        // Validate proposal exists
        if (proposal.proposedTime == 0) {
            revert ProposalNotFound(proposalId);
        }

        // Check if already executed
        if (proposal.executed) {
            revert ProposalAlreadyExecuted(proposalId);
        }

        // Check if cancelled
        if (proposal.cancelled) {
            revert ProposalCancelled(proposalId);
        }

        // Check timelock
        if (block.timestamp < proposal.executeTime) {
            uint256 timeRemaining = proposal.executeTime - block.timestamp;
            revert TimelockNotExpired(proposalId, timeRemaining);
        }

        // Check current balance
        uint256 currentBalance = usdcToken.balanceOf(address(this));
        if (proposal.amount > currentBalance) {
            revert InsufficientTreasuryBalance(proposal.amount, currentBalance);
        }

        // Mark as executed
        proposal.executed = true;

        // Execute transfer
        uint256 oldBalance = currentBalance;
        usdcToken.safeTransfer(proposal.recipient, proposal.amount);
        uint256 newBalance = usdcToken.balanceOf(address(this));

        // Emit events
        emit WithdrawalExecuted(proposalId, proposal.amount, proposal.recipient);
        emit TreasuryBalanceChanged(oldBalance, newBalance, "Proposal withdrawal executed");
    }

    /**
     * @dev Cancels a pending withdrawal proposal
     * @param proposalId Proposal identifier to cancel
     * @param reason Cancellation reason
     */
    function cancelWithdrawal(
        uint256 proposalId,
        string calldata reason
    ) external onlyRole(TREASURY_ADMIN_ROLE) {
        WithdrawalProposal storage proposal = proposals[proposalId];

        if (proposal.proposedTime == 0) {
            revert ProposalNotFound(proposalId);
        }

        if (proposal.executed) {
            revert ProposalAlreadyExecuted(proposalId);
        }

        if (proposal.cancelled) {
            revert ProposalAlreadyCancelled(proposalId);
        }

        // Cancel proposal
        proposal.cancelled = true;

        emit WithdrawalCancelled(proposalId, reason);
    }

    // 
    // DAILY LIMIT MANAGEMENT
    // 

    /**
     * @dev Updates daily limits if 24 hours have passed
     * @param user User to update limits for
     */
    function _updateDailyLimits(address user) internal {
        // Update user's daily limit
        if (block.timestamp >= lastWithdrawalReset[user] + DAILY_RESET_PERIOD) {
            uint256 previousWithdrawn = dailyWithdrawn[user];
            dailyWithdrawn[user] = 0;
            lastWithdrawalReset[user] = block.timestamp;

            if (previousWithdrawn > 0) {
                emit DailyLimitsReset(user, previousWithdrawn, block.timestamp);
            }
        }

        // Update global daily tracking
        if (block.timestamp >= lastGlobalReset + DAILY_RESET_PERIOD) {
            totalInstantWithdrawnToday = 0;
            lastGlobalReset = block.timestamp;
        }
    }

    /**
     * @dev Gets remaining daily withdrawal limit for user
     * @param user User address
     * @return remaining Remaining daily limit in USDC (6 decimals)
     */
    function getRemainingDailyLimit(address user) public view returns (uint256 remaining) {
        // Check if daily limit should be reset
        if (block.timestamp >= lastWithdrawalReset[user] + DAILY_RESET_PERIOD) {
            return INSTANT_WITHDRAWAL_LIMIT;
        }

        uint256 withdrawn = dailyWithdrawn[user];
        if (withdrawn >= INSTANT_WITHDRAWAL_LIMIT) {
            return 0;
        }

        return INSTANT_WITHDRAWAL_LIMIT - withdrawn;
    }

    // 
    // EMERGENCY CONTROLS
    // 

    /**
     * @dev Freezes all treasury operations immediately
     */
    function emergencyFreeze(string calldata reason) external onlyRole(EMERGENCY_ROLE) {
        treasuryFrozen = true;
        freezeEndTime = block.timestamp + FREEZE_DURATION;

        emit TreasuryFrozen(reason, freezeEndTime);
    }

    /**
     * @dev Manually unfreezes treasury
     */
    function manualUnfreeze() external onlyRole(EMERGENCY_ROLE) {
        require(treasuryFrozen, "Treasury not frozen");

        treasuryFrozen = false;
        freezeEndTime = 0;

        emit TreasuryUnfrozen(false);
    }

    /**
     * @dev Internal function for automatic unfreeze
     */
    function _autoUnfreeze() internal {
        treasuryFrozen = false;
        freezeEndTime = 0;

        emit TreasuryUnfrozen(true);
    }

    // 
    // VIEW FUNCTIONS
    // 

    /**
     * @dev Gets detailed proposal information
     */
    function getProposal(uint256 proposalId) external view returns (WithdrawalProposal memory) {
        return proposals[proposalId];
    }

    /**
     * @dev Gets user's daily withdrawal status
     */
    function getDailyWithdrawalStatus(address user) external view returns (
        uint256 dailyLimit,
        uint256 withdrawn,
        uint256 remaining,
        uint256 resetTime
    ) {
        dailyLimit = INSTANT_WITHDRAWAL_LIMIT;
        withdrawn = dailyWithdrawn[user];
        remaining = getRemainingDailyLimit(user);
        resetTime = lastWithdrawalReset[user] + DAILY_RESET_PERIOD;

        return (dailyLimit, withdrawn, remaining, resetTime);
    }

    /**
     * @dev Gets treasury status summary
     */
    function getTreasuryStatus() external view returns (
        uint256 balance,
        bool isFrozen,
        uint256 freezeTimeRemaining,
        uint256 pendingProposalsCount,
        uint256 nextProposalId,
        uint256 totalInstantToday
    ) {
        balance = usdcToken.balanceOf(address(this));
        isFrozen = treasuryFrozen;

        if (isFrozen && block.timestamp < freezeEndTime) {
            freezeTimeRemaining = freezeEndTime - block.timestamp;
        } else {
            freezeTimeRemaining = 0;
        }

        // Count pending proposals
        for (uint256 i = 1; i < proposalCounter; i++) {
            WithdrawalProposal storage proposal = proposals[i];
            if (proposal.proposedTime > 0 && !proposal.executed && !proposal.cancelled) {
                pendingProposalsCount++;
            }
        }

        nextProposalId = proposalCounter;
        totalInstantToday = totalInstantWithdrawnToday;

        return (balance, isFrozen, freezeTimeRemaining, pendingProposalsCount, nextProposalId, totalInstantToday);
    }

    /**
     * @dev Admin utility to deposit USDC
     */
    function depositUSDC(uint256 amount) external onlyRole(TREASURY_ADMIN_ROLE) {
        require(amount > 0, "Amount must be greater than 0");

        uint256 oldBalance = usdcToken.balanceOf(address(this));
        usdcToken.safeTransferFrom(msg.sender, address(this), amount);
        uint256 newBalance = usdcToken.balanceOf(address(this));

        emit TreasuryBalanceChanged(oldBalance, newBalance, "Manual deposit");
    }

    /**
     * @dev Emergency pause all functions
     */
    function emergencyPause() external onlyRole(EMERGENCY_ROLE) {
        _pause();
    }

    /**
     * @dev Emergency unpause all functions
     */
    function emergencyUnpause() external onlyRole(EMERGENCY_ROLE) {
        _unpause();
    }

    /**
     * @dev Authorize upgrade for UUPS pattern
     * Only DEFAULT_ADMIN_ROLE can authorize upgrades
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyRole(DEFAULT_ADMIN_ROLE) {}
}

contracts/fixed/FixedOpinionMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// Custom errors for gas efficiency
error QuestionEmpty();
error QuestionTooLong(uint256 length);
error AnswerEmpty();
error AnswerTooLong(uint256 length);
error DescriptionTooLong(uint256 length);
error TooManyCategories(uint256 count);
error CategoryEmpty();
error PriceRange(uint256 price, uint256 min, uint256 max);
error InsufficientAllowance(uint256 need, uint256 have);
error InsufficientBalance(uint256 need, uint256 have);
error OpinionNotFound(uint256 id);
error OpinionNotActive(uint256 id);
error SameOwner();
error PoolNotFound(uint256 id);
error PoolNotActive(uint256 id);
error DeadlineExpired(uint256 deadline, uint256 current);
error DeadlineTooSoon(uint256 deadline, uint256 current);
error NoContribution();
error PoolAlreadyExecuted(uint256 id);
error NoFeesToClaim();

contract FixedOpinionMarket is
    Initializable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    UUPSUpgradeable
{
    using SafeERC20 for IERC20;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    IERC20 public usdcToken;
    address public treasury;
    uint256 public nextOpinionId;
    uint256 public nextPoolId;

    // Constants for validation
    uint256 public constant MIN_PRICE = 2e6;      // 2 USDC
    uint256 public constant MAX_PRICE = 100e6;    // 100 USDC
    uint256 public constant MAX_QUESTION = 100;   // chars
    uint256 public constant MAX_ANSWER = 100;     // chars
    uint256 public constant MAX_DESCRIPTION = 120; // chars
    uint256 public constant MAX_CATEGORIES = 3;    // count

    struct Opinion {
        address creator;
        address currentOwner;
        string question;
        string currentAnswer;
        string description;
        uint96 lastPrice;
        uint96 nextPrice;
        bool isActive;
        uint96 salePrice;
        string[] categories;
    }

    struct Pool {
        uint256 opinionId;
        address creator;
        string proposedAnswer;
        uint256 deadline;
        uint96 totalContributed;
        uint96 targetAmount;
        bool executed;
        bool expired;
    }

    mapping(uint256 => Opinion) public opinions;
    mapping(uint256 => Pool) public pools;
    mapping(uint256 => mapping(address => uint96)) public poolContributions;
    mapping(address => uint96) public accumulatedFees;

    event OpinionCreated(uint256 indexed opinionId, address creator, string question, uint96 price);
    event AnswerSubmitted(uint256 indexed opinionId, address submitter, string answer, uint96 price);
    event PoolCreated(uint256 indexed poolId, uint256 opinionId, string proposedAnswer);
    event PoolContribution(uint256 indexed poolId, address contributor, uint96 amount);
    event PoolExecuted(uint256 indexed poolId, uint256 opinionId);
    event FeesDistributed(address indexed recipient, uint96 amount);

    function initialize(address _usdcToken, address _treasury) public initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();
        __Pausable_init();
        __UUPSUpgradeable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);

        usdcToken = IERC20(_usdcToken);
        treasury = _treasury;
        nextOpinionId = 1;
        nextPoolId = 1;
    }

    function _authorizeUpgrade(address) internal override onlyRole(ADMIN_ROLE) {}

    // ============ OPINION FUNCTIONS ============

    function createOpinion(
        string calldata question,
        string calldata answer,
        string calldata description,
        uint96 initialPrice,
        string[] calldata categories
    ) external nonReentrant whenNotPaused {
        // Input validation with custom errors
        if (bytes(question).length == 0) revert QuestionEmpty();
        if (bytes(question).length > MAX_QUESTION) revert QuestionTooLong(bytes(question).length);
        if (bytes(answer).length == 0) revert AnswerEmpty();
        if (bytes(answer).length > MAX_ANSWER) revert AnswerTooLong(bytes(answer).length);
        if (bytes(description).length > MAX_DESCRIPTION) revert DescriptionTooLong(bytes(description).length);
        if (categories.length == 0 || categories.length > MAX_CATEGORIES) revert TooManyCategories(categories.length);

        // Validate categories
        for (uint i = 0; i < categories.length; i++) {
            if (bytes(categories[i]).length == 0) revert CategoryEmpty();
        }

        if (initialPrice < MIN_PRICE || initialPrice > MAX_PRICE) {
            revert PriceRange(initialPrice, MIN_PRICE, MAX_PRICE);
        }

        // Check USDC allowance and balance
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < initialPrice) revert InsufficientAllowance(initialPrice, allowance);

        uint256 balance = usdcToken.balanceOf(msg.sender);
        if (balance < initialPrice) revert InsufficientBalance(initialPrice, balance);

        // Create opinion
        uint256 opinionId = nextOpinionId++;

        opinions[opinionId] = Opinion({
            creator: msg.sender,
            currentOwner: msg.sender,
            question: question,
            currentAnswer: answer,
            description: description,
            lastPrice: initialPrice,
            nextPrice: _calculateNextPrice(initialPrice),
            isActive: true,
            salePrice: 0,
            categories: categories
        });

        // Transfer payment
        usdcToken.safeTransferFrom(msg.sender, treasury, initialPrice);

        emit OpinionCreated(opinionId, msg.sender, question, initialPrice);
    }

    function submitAnswer(
        uint256 opinionId,
        string calldata answer
    ) external nonReentrant whenNotPaused {
        // Validation
        Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound(opinionId);
        if (!opinion.isActive) revert OpinionNotActive(opinionId);
        if (opinion.currentOwner == msg.sender) revert SameOwner();
        if (bytes(answer).length == 0) revert AnswerEmpty();
        if (bytes(answer).length > MAX_ANSWER) revert AnswerTooLong(bytes(answer).length);

        uint96 price = opinion.nextPrice;

        // Check USDC
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < price) revert InsufficientAllowance(price, allowance);

        uint256 balance = usdcToken.balanceOf(msg.sender);
        if (balance < price) revert InsufficientBalance(price, balance);

        // Calculate fees: 2% platform, 3% creator, 95% owner
        uint96 platformFee = (price * 2) / 100;
        uint96 creatorFee = (price * 3) / 100;
        uint96 ownerAmount = price - platformFee - creatorFee;

        // Transfer payment
        usdcToken.safeTransferFrom(msg.sender, address(this), price);

        // Distribute fees
        accumulatedFees[opinion.creator] += creatorFee;
        accumulatedFees[opinion.currentOwner] += ownerAmount;
        usdcToken.safeTransfer(treasury, platformFee);

        // Update opinion
        opinion.currentOwner = msg.sender;
        opinion.currentAnswer = answer;
        opinion.lastPrice = price;
        opinion.nextPrice = _calculateNextPrice(price);

        emit AnswerSubmitted(opinionId, msg.sender, answer, price);
        emit FeesDistributed(opinion.creator, creatorFee);
        emit FeesDistributed(opinion.currentOwner, ownerAmount);
    }

    // ============ POOL FUNCTIONS ============

    function createPool(
        uint256 opinionId,
        string calldata proposedAnswer,
        uint256 deadline,
        uint96 initialContribution
    ) external nonReentrant whenNotPaused {
        // Validation
        Opinion storage opinion = opinions[opinionId];
        if (opinion.creator == address(0)) revert OpinionNotFound(opinionId);
        if (!opinion.isActive) revert OpinionNotActive(opinionId);
        if (deadline <= block.timestamp) revert DeadlineTooSoon(deadline, block.timestamp);
        if (bytes(proposedAnswer).length == 0) revert AnswerEmpty();
        if (bytes(proposedAnswer).length > MAX_ANSWER) revert AnswerTooLong(bytes(proposedAnswer).length);
        if (initialContribution == 0) revert InsufficientBalance(1, 0);

        // Check USDC
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < initialContribution) revert InsufficientAllowance(initialContribution, allowance);

        uint256 balance = usdcToken.balanceOf(msg.sender);
        if (balance < initialContribution) revert InsufficientBalance(initialContribution, balance);

        // Create pool
        uint256 poolId = nextPoolId++;
        uint96 targetAmount = opinion.nextPrice;

        pools[poolId] = Pool({
            opinionId: opinionId,
            creator: msg.sender,
            proposedAnswer: proposedAnswer,
            deadline: deadline,
            totalContributed: initialContribution,
            targetAmount: targetAmount,
            executed: false,
            expired: false
        });

        poolContributions[poolId][msg.sender] = initialContribution;
        usdcToken.safeTransferFrom(msg.sender, address(this), initialContribution);

        // Auto-execute if target reached
        if (initialContribution >= targetAmount) {
            _executePool(poolId);
        }

        emit PoolCreated(poolId, opinionId, proposedAnswer);
        emit PoolContribution(poolId, msg.sender, initialContribution);
    }

    function contributeToPool(uint256 poolId, uint96 amount) external nonReentrant whenNotPaused {
        Pool storage pool = pools[poolId];
        if (pool.creator == address(0)) revert PoolNotFound(poolId);
        if (pool.executed) revert PoolAlreadyExecuted(poolId);
        if (pool.expired) revert PoolNotActive(poolId);
        if (block.timestamp >= pool.deadline) revert DeadlineExpired(pool.deadline, block.timestamp);
        if (amount == 0) revert InsufficientBalance(1, 0);

        // Check USDC
        uint256 allowance = usdcToken.allowance(msg.sender, address(this));
        if (allowance < amount) revert InsufficientAllowance(amount, allowance);

        uint256 balance = usdcToken.balanceOf(msg.sender);
        if (balance < amount) revert InsufficientBalance(amount, balance);

        pool.totalContributed += amount;
        poolContributions[poolId][msg.sender] += amount;

        usdcToken.safeTransferFrom(msg.sender, address(this), amount);

        // Auto-execute if target reached
        if (pool.totalContributed >= pool.targetAmount) {
            _executePool(poolId);
        }

        emit PoolContribution(poolId, msg.sender, amount);
    }

    function _executePool(uint256 poolId) internal {
        Pool storage pool = pools[poolId];
        Opinion storage opinion = opinions[pool.opinionId];

        pool.executed = true;

        // Calculate fees same as submitAnswer
        uint96 price = pool.targetAmount;
        uint96 platformFee = (price * 2) / 100;
        uint96 creatorFee = (price * 3) / 100;
        uint96 ownerAmount = price - platformFee - creatorFee;

        // Distribute fees
        accumulatedFees[opinion.creator] += creatorFee;
        accumulatedFees[opinion.currentOwner] += ownerAmount;
        usdcToken.safeTransfer(treasury, platformFee);

        // Update opinion
        opinion.currentOwner = address(this); // Pool owns it
        opinion.currentAnswer = pool.proposedAnswer;
        opinion.lastPrice = price;
        opinion.nextPrice = _calculateNextPrice(price);

        emit PoolExecuted(poolId, pool.opinionId);
        emit AnswerSubmitted(pool.opinionId, address(this), pool.proposedAnswer, price);
    }

    function withdrawFromExpiredPool(uint256 poolId) external nonReentrant {
        Pool storage pool = pools[poolId];
        if (pool.creator == address(0)) revert PoolNotFound(poolId);
        if (block.timestamp < pool.deadline) revert DeadlineTooSoon(pool.deadline, block.timestamp);
        if (pool.executed) revert PoolAlreadyExecuted(poolId);

        uint96 userContribution = poolContributions[poolId][msg.sender];
        if (userContribution == 0) revert NoContribution();

        poolContributions[poolId][msg.sender] = 0;
        pool.expired = true;

        usdcToken.safeTransfer(msg.sender, userContribution);
    }

    // ============ FEE FUNCTIONS ============

    function claimFees() external nonReentrant {
        uint96 amount = accumulatedFees[msg.sender];
        if (amount == 0) revert NoFeesToClaim();

        accumulatedFees[msg.sender] = 0;
        usdcToken.safeTransfer(msg.sender, amount);

        emit FeesDistributed(msg.sender, amount);
    }

    // ============ INTERNAL FUNCTIONS ============

    function _calculateNextPrice(uint96 currentPrice) internal pure returns (uint96) {
        return (currentPrice * 130) / 100; // 30% increase
    }

    // ============ VIEW FUNCTIONS ============

    function getOpinion(uint256 opinionId) external view returns (Opinion memory) {
        return opinions[opinionId];
    }

    function getPool(uint256 poolId) external view returns (Pool memory) {
        return pools[poolId];
    }

    function getUserContribution(uint256 poolId, address user) external view returns (uint96) {
        return poolContributions[poolId][user];
    }

    function getAccumulatedFees(address user) external view returns (uint96) {
        return accumulatedFees[user];
    }

    // ============ ADMIN FUNCTIONS ============

    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    function setTreasury(address newTreasury) external onlyRole(ADMIN_ROLE) {
        treasury = newTreasury;
    }

    function emergencyWithdraw(address token, uint256 amount) external onlyRole(ADMIN_ROLE) {
        IERC20(token).safeTransfer(treasury, amount);
    }
}

contracts/mocks/MockERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract MockERC20 is ERC20 {
    mapping(address => bool) public blockedAddresses;
    uint8 private _decimals = 6;

    constructor(string memory name, string memory symbol) ERC20(name, symbol) {}

    function mint(address to, uint256 amount) external {
        _mint(to, amount);
    }

    function blockAddress(address account) external {
        blockedAddresses[account] = true;
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function unblockAddress(address account) external {
        blockedAddresses[account] = false;
    }

    function _update(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        require(!blockedAddresses[to], "ERC20: transfer to blocked address");
        super._update(from, to, amount);
    }
}

